import { useState, useEffect, useCallback, useMemo } from 'react';
import type {
  IntelligenceGraph,
  GraphNode,
  InfluenceMap,
  Stakeholder,
  ProactiveInsight,
  IntelligenceFilters,
} from '@/types/intelligence';

/**
 * Hook for managing intelligence graph data
 */
export function useIntelligenceGraph(filters?: IntelligenceFilters) {
  const [graph, setGraph] = useState<IntelligenceGraph | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);
  const [selectedNode, setSelectedNode] = useState<GraphNode | null>(null);

  useEffect(() => {
    const fetchGraph = async () => {
      try {
        setIsLoading(true);
        // Simulated API call
        await new Promise(resolve => setTimeout(resolve, 500));

        // Mock data would be set here
        setGraph({
          nodes: [],
          edges: [],
          clusters: [],
          metadata: {
            totalNodes: 0,
            totalEdges: 0,
            lastUpdated: new Date(),
            coverageScore: 85,
            dataQuality: 92,
          },
        });
      } catch (err) {
        setError(err as Error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchGraph();
  }, [filters]);

  const filteredNodes = useMemo(() => {
    if (!graph || !filters) return graph?.nodes || [];

    return graph.nodes.filter(node => {
      if (filters.nodeTypes && !filters.nodeTypes.includes(node.type)) return false;
      if (filters.intentLevels && !filters.intentLevels.includes(node.intentLevel)) return false;
      if (filters.stages && !filters.stages.includes(node.stage)) return false;
      if (filters.minScore && node.score < filters.minScore) return false;
      return true;
    });
  }, [graph, filters]);

  const selectNode = useCallback((node: GraphNode | null) => {
    setSelectedNode(node);
  }, []);

  const expandNode = useCallback((nodeId: string) => {
    // Expand node to show connected nodes
    console.log('Expanding node:', nodeId);
  }, []);

  const collapseCluster = useCallback((clusterId: string) => {
    // Collapse a cluster of nodes
    console.log('Collapsing cluster:', clusterId);
  }, []);

  return {
    graph,
    filteredNodes,
    isLoading,
    error,
    selectedNode,
    selectNode,
    expandNode,
    collapseCluster,
  };
}

/**
 * Hook for managing influence maps
 */
export function useInfluenceMap(accountId: string) {
  const [map, setMap] = useState<InfluenceMap | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedStakeholder, setSelectedStakeholder] = useState<Stakeholder | null>(null);

  useEffect(() => {
    const fetchMap = async () => {
      setIsLoading(true);
      await new Promise(resolve => setTimeout(resolve, 300));

      // Mock data would be set here
      setMap({
        accountId,
        accountName: 'Sample Account',
        stakeholders: [],
        relationships: [],
        buyingCenter: {
          missingRoles: [],
          blockers: [],
        },
        recommendations: [],
        lastUpdated: new Date(),
      });

      setIsLoading(false);
    };

    if (accountId) {
      fetchMap();
    }
  }, [accountId]);

  const selectStakeholder = useCallback((stakeholder: Stakeholder | null) => {
    setSelectedStakeholder(stakeholder);
  }, []);

  const updateStakeholderRole = useCallback((stakeholderId: string, role: string) => {
    setMap(prev => {
      if (!prev) return null;
      return {
        ...prev,
        stakeholders: prev.stakeholders.map(s =>
          s.id === stakeholderId ? { ...s, role: role as any } : s
        ),
      };
    });
  }, []);

  const addRelationship = useCallback((fromId: string, toId: string, type: string) => {
    // Add new relationship
    console.log('Adding relationship:', fromId, toId, type);
  }, []);

  return {
    map,
    isLoading,
    selectedStakeholder,
    selectStakeholder,
    updateStakeholderRole,
    addRelationship,
  };
}

/**
 * Hook for proactive insights
 */
export function useProactiveInsights() {
  const [insights, setInsights] = useState<ProactiveInsight[]>([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchInsights = async () => {
      setIsLoading(true);
      await new Promise(resolve => setTimeout(resolve, 300));

      // Mock insights
      setInsights([
        {
          id: 'ins-1',
          type: 'opportunity',
          priority: 'high',
          title: 'High-intent account detected',
          description: 'Acme Corp showing buying signals',
          evidence: [],
          suggestedActions: [],
          affectedEntities: ['acc-1'],
          createdAt: new Date(),
          isActioned: false,
        },
      ]);
      setUnreadCount(3);
      setIsLoading(false);
    };

    fetchInsights();
  }, []);

  const markAsActioned = useCallback((insightId: string) => {
    setInsights(prev => prev.map(i => (i.id === insightId ? { ...i, isActioned: true } : i)));
    setUnreadCount(prev => Math.max(0, prev - 1));
  }, []);

  const dismissInsight = useCallback((insightId: string) => {
    setInsights(prev =>
      prev.map(i => (i.id === insightId ? { ...i, dismissedAt: new Date() } : i))
    );
  }, []);

  return {
    insights,
    unreadCount,
    isLoading,
    markAsActioned,
    dismissInsight,
  };
}

/**
 * Hook for intelligence search
 */
export function useIntelligenceSearch() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<GraphNode[]>([]);
  const [isSearching, setIsSearching] = useState(false);

  const search = useCallback(async (searchQuery: string) => {
    setQuery(searchQuery);
    if (!searchQuery.trim()) {
      setResults([]);
      return;
    }

    setIsSearching(true);
    await new Promise(resolve => setTimeout(resolve, 200));

    // Mock search results
    setResults([]);
    setIsSearching(false);
  }, []);

  const clearSearch = useCallback(() => {
    setQuery('');
    setResults([]);
  }, []);

  return {
    query,
    results,
    isSearching,
    search,
    clearSearch,
  };
}

export default {
  useIntelligenceGraph,
  useInfluenceMap,
  useProactiveInsights,
  useIntelligenceSearch,
};

/**
 * Centralized Router Configuration
 * Route definitions with guards, layouts, and metadata
 */
import { Suspense, lazy } from 'react';
import { createBrowserRouter, Outlet, Navigate } from 'react-router-dom';
import { PageLoader } from '../components/Loading';

// Lazy load pages
const LandingPage = lazy(() => import('../pages/LandingPage'));
const Marketing = lazy(() => import('../pages/Marketing'));
const Dashboard = lazy(() => import('../pages/EnhancedDashboardNew'));
const CampaignBuilder = lazy(() => import('../pages/CampaignBuilder'));
const Leads = lazy(() => import('../pages/Leads'));
const LeadDatabase = lazy(() => import('../pages/LeadDatabase'));
const AIAssistant = lazy(() => import('../pages/AIAssistant'));
const Analytics = lazy(() => import('../pages/Analytics'));
const Integrations = lazy(() => import('../pages/Integrations'));
const Settings = lazy(() => import('../pages/Settings'));
const Templates = lazy(() => import('../pages/Templates'));
const Admin = lazy(() => import('../pages/Admin'));
const NotFound = lazy(() => import('../pages/NotFound'));

// Advanced Features
const LeadScoring = lazy(() => import('../pages/LeadScoring'));
const ABTesting = lazy(() => import('../pages/ABTesting'));
const EmailTemplateBuilder = lazy(() => import('../pages/EmailTemplateBuilder'));
const LeadEnrichment = lazy(() => import('../pages/LeadEnrichment'));
const RevenueForecasting = lazy(() => import('../pages/RevenueForecasting'));

// Admin Pages
const AdminAPIKeys = lazy(() => import('../pages/AdminAPIKeys'));
const AdminWebhooks = lazy(() => import('../pages/AdminWebhooks'));
const AdminAuditLog = lazy(() => import('../pages/AdminAuditLog'));

// ============================================
// Route Metadata Types
// ============================================

export interface RouteMetadata {
  title: string;
  description?: string;
  requiresAuth?: boolean;
  requiredRole?: 'user' | 'admin' | 'super_admin';
  icon?: string;
  hideInNav?: boolean;
}

// ============================================
// Layout Components
// ============================================

function PublicLayout() {
  return (
    <Suspense fallback={<PageLoader message="Loading..." />}>
      <Outlet />
    </Suspense>
  );
}

function AppLayout() {
  // This would typically include DashboardLayout wrapper
  return (
    <Suspense fallback={<PageLoader message="Loading..." />}>
      <Outlet />
    </Suspense>
  );
}

function AdminLayout() {
  return (
    <Suspense fallback={<PageLoader message="Loading Admin..." />}>
      <Outlet />
    </Suspense>
  );
}

// ============================================
// Route Definitions
// ============================================

export const routeConfig = {
  // Public routes
  public: [
    { path: '/', element: <LandingPage />, meta: { title: 'Home' } },
    { path: '/marketing', element: <Marketing />, meta: { title: 'Marketing' } },
  ],

  // Protected app routes
  app: [
    { path: 'dashboard', element: <Dashboard />, meta: { title: 'Dashboard', icon: 'home' } },
    {
      path: 'campaigns',
      element: <CampaignBuilder />,
      meta: { title: 'Campaigns', icon: 'megaphone' },
    },
    { path: 'leads', element: <Leads />, meta: { title: 'Leads', icon: 'users' } },
    {
      path: 'lead-database',
      element: <LeadDatabase />,
      meta: { title: 'Lead Database', icon: 'database' },
    },
    {
      path: 'ai-assistant',
      element: <AIAssistant />,
      meta: { title: 'AI Assistant', icon: 'bot' },
    },
    { path: 'analytics', element: <Analytics />, meta: { title: 'Analytics', icon: 'chart' } },
    {
      path: 'integrations',
      element: <Integrations />,
      meta: { title: 'Integrations', icon: 'plug' },
    },
    { path: 'templates', element: <Templates />, meta: { title: 'Templates', icon: 'file-text' } },
    { path: 'settings', element: <Settings />, meta: { title: 'Settings', icon: 'settings' } },
    // Advanced features
    {
      path: 'lead-scoring',
      element: <LeadScoring />,
      meta: { title: 'Lead Scoring', icon: 'target' },
    },
    { path: 'ab-testing', element: <ABTesting />, meta: { title: 'A/B Testing', icon: 'split' } },
    {
      path: 'email-templates',
      element: <EmailTemplateBuilder />,
      meta: { title: 'Email Builder', icon: 'mail' },
    },
    {
      path: 'lead-enrichment',
      element: <LeadEnrichment />,
      meta: { title: 'Enrichment', icon: 'sparkles' },
    },
    {
      path: 'revenue-forecasting',
      element: <RevenueForecasting />,
      meta: { title: 'Revenue Forecast', icon: 'trending-up' },
    },
  ],

  // Admin routes
  admin: [
    { path: '', element: <Admin />, meta: { title: 'Admin Dashboard' } },
    { path: 'api-keys', element: <AdminAPIKeys />, meta: { title: 'API Keys' } },
    { path: 'webhooks', element: <AdminWebhooks />, meta: { title: 'Webhooks' } },
    { path: 'audit-log', element: <AdminAuditLog />, meta: { title: 'Audit Log' } },
  ],
} as const;

// ============================================
// Router Instance
// ============================================

export const router = createBrowserRouter([
  // Public routes
  {
    element: <PublicLayout />,
    children: routeConfig.public.map(({ path, element }) => ({
      path,
      element,
    })),
  },

  // App routes (would normally have auth guard)
  {
    path: '/app',
    element: <AppLayout />,
    children: [
      { index: true, element: <Navigate to="dashboard" replace /> },
      ...routeConfig.app.map(({ path, element }) => ({
        path,
        element,
      })),
    ],
  },

  // Admin routes
  {
    path: '/admin',
    element: <AdminLayout />,
    children: routeConfig.admin.map(({ path, element }) => ({
      path,
      element,
    })),
  },

  // Catch-all
  { path: '*', element: <NotFound /> },
]);

// ============================================
// Navigation Helpers
// ============================================

export const navigationItems = routeConfig.app
  .filter(route => !(route.meta as { hideInNav?: boolean })?.hideInNav)
  .map(route => ({
    path: `/app/${route.path}`,
    title: route.meta.title,
    icon: route.meta.icon,
  }));

export const adminNavigationItems = routeConfig.admin.map(route => ({
  path: route.path ? `/admin/${route.path}` : '/admin',
  title: route.meta.title,
}));

export default router;

/**
 * Hunter Agent
 *
 * Finds accounts & contacts dynamically based on ICP signals.
 * Continuously scans for new opportunities matching target profiles.
 */

import { BaseAgent, AgentContext, ExecutionResult } from './BaseAgent';
import type { Task } from './types';

interface AccountMatch {
  id: string;
  name: string;
  domain: string;
  industry: string;
  size: string;
  signals: string[];
  matchScore: number;
  contacts: ContactMatch[];
}

interface ContactMatch {
  id: string;
  name: string;
  title: string;
  email?: string;
  linkedin?: string;
  seniority: string;
  department: string;
  relevanceScore: number;
}

interface HuntingCriteria {
  industries: string[];
  companySizes: string[];
  technologies: string[];
  signals: string[];
  excludeDomains: string[];
  geographies: string[];
  minMatchScore: number;
}

export class HunterAgent extends BaseAgent {
  private huntingPatterns: Map<string, number> = new Map();
  private discoveredAccounts: Set<string> = new Set();

  constructor(name: string = 'Hunter Prime') {
    super('hunter', name, {
      autonomyLevel: 'autonomous',
      riskTolerance: 0.7,
      creativityBias: 0.6,
    });
  }

  protected initializeCapabilities(): void {
    this.capabilities = [
      {
        id: 'account-discovery',
        name: 'Account Discovery',
        skillLevel: 0.85,
        cost: 5,
        latency: 2000,
        successRate: 0.9,
      },
      {
        id: 'contact-identification',
        name: 'Contact Identification',
        skillLevel: 0.8,
        cost: 3,
        latency: 1500,
        successRate: 0.85,
      },
      {
        id: 'signal-detection',
        name: 'Intent Signal Detection',
        skillLevel: 0.75,
        cost: 4,
        latency: 1000,
        successRate: 0.8,
      },
      {
        id: 'icp-matching',
        name: 'ICP Pattern Matching',
        skillLevel: 0.9,
        cost: 2,
        latency: 500,
        successRate: 0.95,
      },
    ];
  }

  protected async executeTask(task: Task, _context: AgentContext): Promise<ExecutionResult> {
    switch (task.type) {
      case 'find-accounts':
        return this.findAccounts(task);
      case 'enrich-contact':
        return this.enrichContact(task);
      case 'research-signal':
        return this.detectSignals(task);
      default:
        throw new Error(`Hunter cannot handle task type: ${task.type}`);
    }
  }

  /**
   * Find accounts matching ICP criteria
   */
  private async findAccounts(task: Task): Promise<ExecutionResult> {
    const criteria = task.input.data as HuntingCriteria;
    const accounts: AccountMatch[] = [];

    // Simulate account discovery with intelligent matching
    const potentialAccounts = await this.scanSources(criteria);

    for (const account of potentialAccounts) {
      const matchScore = this.calculateMatchScore(account, criteria);

      if (matchScore >= criteria.minMatchScore && !this.discoveredAccounts.has(account.domain)) {
        // Find relevant contacts
        const contacts = await this.identifyContacts(account);

        accounts.push({
          ...account,
          matchScore,
          contacts,
        });

        this.discoveredAccounts.add(account.domain);
        this.updateHuntingPatterns(account, matchScore);
      }
    }

    // Sort by match score
    accounts.sort((a, b) => b.matchScore - a.matchScore);

    const learnings = this.extractLearnings(accounts, criteria);

    return {
      success: accounts.length > 0,
      output: {
        accounts,
        totalFound: accounts.length,
        averageScore: accounts.reduce((a, b) => a + b.matchScore, 0) / accounts.length || 0,
        signalDistribution: this.analyzeSignalDistribution(accounts),
      },
      artifacts: accounts,
      learnings,
      nextSuggestion: accounts.length < 5 ? 'expand-criteria' : undefined,
    };
  }

  /**
   * Enrich contact with additional data
   */
  private async enrichContact(task: Task): Promise<ExecutionResult> {
    const contactId = task.input.data as { contactId: string; accountId: string };

    // Simulate enrichment process
    const enrichedData = await this.performEnrichment(contactId);

    return {
      success: enrichedData.email !== undefined || enrichedData.linkedin !== undefined,
      output: enrichedData,
      artifacts: [enrichedData],
      learnings: enrichedData.email ? ['Email enrichment successful'] : [],
    };
  }

  /**
   * Detect intent signals for an account
   */
  private async detectSignals(task: Task): Promise<ExecutionResult> {
    const accountId = task.input.data as { accountId: string };

    const signals = await this.scanForSignals(accountId);

    return {
      success: signals.length > 0,
      output: {
        signals,
        strongestSignal: signals[0] || null,
        urgency: this.calculateUrgency(signals),
      },
      artifacts: signals,
      learnings: signals.map(s => `Signal detected: ${s.type}`),
    };
  }

  // === Private Helper Methods ===

  private async scanSources(
    criteria: HuntingCriteria
  ): Promise<Omit<AccountMatch, 'matchScore' | 'contacts'>[]> {
    // Simulate scanning multiple data sources
    await this.simulateLatency(1000);

    // Generate synthetic accounts based on criteria
    const accounts: Omit<AccountMatch, 'matchScore' | 'contacts'>[] = [];
    const count = Math.floor(Math.random() * 20) + 5;

    for (let i = 0; i < count; i++) {
      const industry =
        criteria.industries[Math.floor(Math.random() * criteria.industries.length)] || 'Technology';
      const size =
        criteria.companySizes[Math.floor(Math.random() * criteria.companySizes.length)] || '50-200';

      accounts.push({
        id: `acc_${Date.now()}_${i}`,
        name: this.generateCompanyName(),
        domain: this.generateDomain(),
        industry,
        size,
        signals: this.generateSignals(criteria.signals),
      });
    }

    return accounts;
  }

  private calculateMatchScore(
    account: Omit<AccountMatch, 'matchScore' | 'contacts'>,
    criteria: HuntingCriteria
  ): number {
    let score = 0;
    let factors = 0;

    // Industry match
    if (criteria.industries.includes(account.industry)) {
      score += 0.3;
    }
    factors++;

    // Size match
    if (criteria.companySizes.includes(account.size)) {
      score += 0.2;
    }
    factors++;

    // Signal overlap
    const signalOverlap = account.signals.filter(s => criteria.signals.includes(s)).length;
    score += (signalOverlap / Math.max(criteria.signals.length, 1)) * 0.5;
    factors++;

    // Apply learned patterns
    const patternBoost = this.getPatternBoost(account.industry, account.size);
    score += patternBoost * 0.1;

    return Math.min(score, 1);
  }

  private async identifyContacts(
    account: Omit<AccountMatch, 'matchScore' | 'contacts'>
  ): Promise<ContactMatch[]> {
    await this.simulateLatency(500);

    const contacts: ContactMatch[] = [];
    const count = Math.floor(Math.random() * 5) + 1;

    const titles = [
      'VP of Sales',
      'Head of Marketing',
      'Director of Operations',
      'CTO',
      'CEO',
      'VP of Engineering',
      'Director of Business Development',
    ];

    const departments = ['Sales', 'Marketing', 'Engineering', 'Operations', 'Executive'];
    const seniorities = ['VP', 'Director', 'Manager', 'C-Level'];

    for (let i = 0; i < count; i++) {
      contacts.push({
        id: `contact_${Date.now()}_${i}`,
        name: this.generatePersonName(),
        title: titles[Math.floor(Math.random() * titles.length)],
        email: Math.random() > 0.3 ? `contact${i}@${account.domain}` : undefined,
        linkedin: Math.random() > 0.2 ? `linkedin.com/in/contact-${i}` : undefined,
        seniority: seniorities[Math.floor(Math.random() * seniorities.length)],
        department: departments[Math.floor(Math.random() * departments.length)],
        relevanceScore: Math.random() * 0.5 + 0.5,
      });
    }

    return contacts.sort((a, b) => b.relevanceScore - a.relevanceScore);
  }

  private async performEnrichment(contactId: {
    contactId: string;
    accountId: string;
  }): Promise<Record<string, unknown>> {
    await this.simulateLatency(800);

    return {
      contactId: contactId.contactId,
      email: Math.random() > 0.2 ? `enriched@example.com` : undefined,
      linkedin: `linkedin.com/in/enriched-${contactId.contactId}`,
      phone: Math.random() > 0.5 ? '+1-555-0100' : undefined,
      verified: Math.random() > 0.3,
      enrichedAt: new Date(),
    };
  }

  private async scanForSignals(_accountId: {
    accountId: string;
  }): Promise<Array<{ type: string; strength: number; source: string }>> {
    await this.simulateLatency(600);

    const signalTypes = [
      'hiring-surge',
      'funding-round',
      'tech-adoption',
      'leadership-change',
      'expansion-indicator',
      'competitor-mention',
    ];

    const signals: Array<{ type: string; strength: number; source: string }> = [];
    const count = Math.floor(Math.random() * 4);

    for (let i = 0; i < count; i++) {
      signals.push({
        type: signalTypes[Math.floor(Math.random() * signalTypes.length)],
        strength: Math.random() * 0.5 + 0.5,
        source: ['linkedin', 'news', 'job-boards', 'g2'][Math.floor(Math.random() * 4)],
      });
    }

    return signals.sort((a, b) => b.strength - a.strength);
  }

  private updateHuntingPatterns(
    account: Omit<AccountMatch, 'matchScore' | 'contacts'>,
    score: number
  ): void {
    const key = `${account.industry}:${account.size}`;
    const currentWeight = this.huntingPatterns.get(key) || 0.5;
    this.huntingPatterns.set(key, currentWeight * 0.9 + score * 0.1);
  }

  private getPatternBoost(industry: string, size: string): number {
    const key = `${industry}:${size}`;
    return this.huntingPatterns.get(key) || 0;
  }

  private extractLearnings(accounts: AccountMatch[], criteria: HuntingCriteria): string[] {
    const learnings: string[] = [];

    if (accounts.length > 10) {
      learnings.push('High-yield criteria combination identified');
    }

    const avgScore = accounts.reduce((a, b) => a + b.matchScore, 0) / accounts.length;
    if (avgScore > 0.8) {
      learnings.push(`Strong ICP alignment in ${criteria.industries.join(', ')}`);
    }

    const signalCounts = new Map<string, number>();
    accounts.forEach(a =>
      a.signals.forEach(s => signalCounts.set(s, (signalCounts.get(s) || 0) + 1))
    );
    const topSignal = Array.from(signalCounts.entries()).sort((a, b) => b[1] - a[1])[0];
    if (topSignal && topSignal[1] > accounts.length * 0.5) {
      learnings.push(`Signal "${topSignal[0]}" is strong indicator for this ICP`);
    }

    return learnings;
  }

  private analyzeSignalDistribution(accounts: AccountMatch[]): Record<string, number> {
    const distribution: Record<string, number> = {};
    accounts.forEach(a => {
      a.signals.forEach(s => {
        distribution[s] = (distribution[s] || 0) + 1;
      });
    });
    return distribution;
  }

  private calculateUrgency(
    signals: Array<{ type: string; strength: number }>
  ): 'high' | 'medium' | 'low' {
    const urgentSignals = ['funding-round', 'hiring-surge', 'expansion-indicator'];
    const hasUrgent = signals.some(s => urgentSignals.includes(s.type) && s.strength > 0.7);
    if (hasUrgent) return 'high';
    if (signals.length > 2) return 'medium';
    return 'low';
  }

  private generateCompanyName(): string {
    const prefixes = ['Nova', 'Apex', 'Summit', 'Vertex', 'Prime', 'Core', 'Nexus', 'Atlas'];
    const suffixes = ['Tech', 'Labs', 'Systems', 'Solutions', 'AI', 'Cloud', 'Data', 'Ops'];
    return `${prefixes[Math.floor(Math.random() * prefixes.length)]}${suffixes[Math.floor(Math.random() * suffixes.length)]}`;
  }

  private generateDomain(): string {
    return `${this.generateCompanyName().toLowerCase().replace(/\s/g, '')}.com`;
  }

  private generatePersonName(): string {
    const firstNames = ['Alex', 'Jordan', 'Taylor', 'Morgan', 'Casey', 'Riley', 'Quinn', 'Avery'];
    const lastNames = [
      'Smith',
      'Johnson',
      'Williams',
      'Brown',
      'Jones',
      'Garcia',
      'Miller',
      'Davis',
    ];
    return `${firstNames[Math.floor(Math.random() * firstNames.length)]} ${lastNames[Math.floor(Math.random() * lastNames.length)]}`;
  }

  private generateSignals(preferredSignals: string[]): string[] {
    const count = Math.floor(Math.random() * 3) + 1;
    const signals: string[] = [];
    for (let i = 0; i < count; i++) {
      if (preferredSignals.length > 0 && Math.random() > 0.3) {
        signals.push(preferredSignals[Math.floor(Math.random() * preferredSignals.length)]);
      } else {
        signals.push(
          ['hiring-surge', 'funding-round', 'tech-adoption'][Math.floor(Math.random() * 3)]
        );
      }
    }
    return [...new Set(signals)];
  }

  private simulateLatency(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms * 0.1)); // Speed up for development
  }

  protected describeStrategy(_task: Task): string {
    return `Hunter will scan ${this.huntingPatterns.size} learned patterns to find high-match accounts`;
  }
}

export default HunterAgent;

/**
 * Orchestrator - Multi-Agent Coordination System
 *
 * The brain that coordinates all agents, manages task distribution,
 * runs the task marketplace, and ensures collaborative execution.
 */

import type {
  Agent,
  AgentRole,
  Task,
  TaskType,
  TaskBid,
  AgentMessage,
  OrchestrationState,
  OrchestrationMetrics,
  OrchestrationConfig,
  AuctionResult,
  Conversation,
} from './types';

import { HunterAgent } from './HunterAgent';
import { ScoutAgent } from './ScoutAgent';
import { WriterAgent } from './WriterAgent';
import { CloserAgent } from './CloserAgent';
import { RevOpsAgent } from './RevOpsAgent';

interface TaskCreationParams {
  type: TaskType;
  priority?: 'critical' | 'high' | 'medium' | 'low' | 'background';
  input: {
    type: string;
    data: unknown;
    context?: Record<string, unknown>;
    constraints?: string[];
  };
  requirements?: {
    capabilities?: string[];
    minSkillLevel?: number;
    estimatedCredits?: number;
    deadline?: Date;
    dependencies?: string[];
  };
  constraints?: {
    maxLatency?: number;
    maxCost?: number;
    qualityThreshold?: number;
    requiredApprovals?: string[];
    blockedAgents?: string[];
  };
}

interface OrchestrationEvent {
  type:
    | 'task-created'
    | 'task-assigned'
    | 'task-completed'
    | 'task-failed'
    | 'agent-message'
    | 'auction-complete';
  timestamp: Date;
  data: unknown;
}

type EventHandler = (event: OrchestrationEvent) => void;

class Orchestrator {
  private state: OrchestrationState;
  private eventHandlers: Map<string, EventHandler[]> = new Map();
  private taskCounter = 0;

  constructor(config: Partial<OrchestrationConfig> = {}) {
    this.state = {
      agents: new Map(),
      taskQueue: [],
      activeConversations: new Map(),
      marketplace: {
        openTasks: [],
        activeBids: new Map(),
        completedAuctions: [],
        priceHistory: new Map(),
        supplyDemand: new Map(),
      },
      metrics: {
        totalTasks: 0,
        completedTasks: 0,
        failedTasks: 0,
        averageLatency: 0,
        averageCost: 0,
        agentUtilization: new Map(),
        throughput: 0,
        efficiency: 0,
      },
      config: {
        maxConcurrentTasks: 50,
        biddingTimeout: 5000,
        defaultPriority: 'medium',
        autoScaling: true,
        qualityThreshold: 0.7,
        costOptimization: 'balanced',
        ...config,
      },
    };

    this.initializeAgents();
  }

  /**
   * Initialize the default agent fleet
   */
  private initializeAgents(): void {
    // Create one of each agent type
    const agents = [
      new HunterAgent('Hunter Alpha'),
      new HunterAgent('Hunter Beta'),
      new ScoutAgent('Scout Alpha'),
      new ScoutAgent('Scout Beta'),
      new WriterAgent('Writer Prime'),
      new WriterAgent('Writer Secondary'),
      new CloserAgent('Closer Prime'),
      new CloserAgent('Closer Secondary'),
      new RevOpsAgent('RevOps Prime'),
    ];

    agents.forEach(agent => {
      this.state.agents.set(agent.id, agent);
      this.state.metrics.agentUtilization.set(agent.id, 0);
    });
  }

  /**
   * Create and submit a new task
   */
  async createTask(params: TaskCreationParams): Promise<Task> {
    const task: Task = {
      id: `task_${++this.taskCounter}_${Date.now()}`,
      type: params.type,
      priority: params.priority || this.state.config.defaultPriority,
      status: 'pending',
      input: {
        type: params.input.type,
        data: params.input.data,
        context: params.input.context || {},
        constraints: params.input.constraints || [],
      },
      requirements: {
        capabilities: params.requirements?.capabilities || this.inferCapabilities(params.type),
        minSkillLevel: params.requirements?.minSkillLevel || 0.5,
        estimatedCredits: params.requirements?.estimatedCredits || 10,
        deadline: params.requirements?.deadline,
        dependencies: params.requirements?.dependencies || [],
      },
      constraints: {
        maxLatency: params.constraints?.maxLatency || 30000,
        maxCost: params.constraints?.maxCost || 100,
        qualityThreshold:
          params.constraints?.qualityThreshold || this.state.config.qualityThreshold,
        requiredApprovals: params.constraints?.requiredApprovals || [],
        blockedAgents: params.constraints?.blockedAgents || [],
      },
      bids: [],
      childTasks: [],
      timeline: {
        created: new Date(),
      },
      metadata: {},
    };

    this.state.taskQueue.push(task);
    this.state.metrics.totalTasks++;

    this.emit('task-created', { task });

    // Start the bidding process
    await this.runAuction(task);

    return task;
  }

  /**
   * Run an auction for a task
   */
  private async runAuction(task: Task): Promise<void> {
    task.status = 'bidding';
    task.timeline.biddingStarted = new Date();

    // Collect bids from eligible agents
    const eligibleAgents = this.findEligibleAgents(task);

    if (eligibleAgents.length === 0) {
      console.warn(`No eligible agents for task ${task.id}`);
      task.status = 'pending';
      return;
    }

    // Request bids
    for (const agent of eligibleAgents) {
      const bid = agent.generateBid(task);
      if (bid) {
        task.bids.push(bid);

        // Track in marketplace
        const existing = this.state.marketplace.activeBids.get(task.id) || [];
        existing.push(bid);
        this.state.marketplace.activeBids.set(task.id, existing);
      }
    }

    // Wait for bidding period or all bids received
    await this.waitForBids(task);

    // Select winner
    if (task.bids.length > 0) {
      const winner = this.selectWinner(task);
      await this.assignTask(task, winner);
    } else {
      task.status = 'pending';
    }
  }

  /**
   * Find agents eligible to bid on a task
   */
  private findEligibleAgents(task: Task): Agent[] {
    const eligible: Agent[] = [];

    for (const agent of this.state.agents.values()) {
      // Check if agent is blocked
      if (task.constraints.blockedAgents.includes(agent.id)) continue;

      // Check if agent can handle the task
      if (agent.canHandle(task)) {
        eligible.push(agent);
      }
    }

    return eligible;
  }

  /**
   * Wait for bidding to complete
   */
  private async waitForBids(task: Task): Promise<void> {
    const timeout = this.state.config.biddingTimeout;
    const expectedBids = this.findEligibleAgents(task).length;

    const start = Date.now();
    while (Date.now() - start < timeout && task.bids.length < expectedBids) {
      await new Promise(resolve => setTimeout(resolve, 100));
    }
  }

  /**
   * Select the winning bid based on cost optimization strategy
   */
  private selectWinner(task: Task): TaskBid {
    const bids = task.bids;
    const strategy = this.state.config.costOptimization;

    switch (strategy) {
      case 'aggressive':
        // Lowest cost
        return bids.reduce((a, b) => (a.proposedCost < b.proposedCost ? a : b), bids[0]);

      case 'quality-first':
        // Highest confidence
        return bids.reduce((a, b) => (a.confidence > b.confidence ? a : b), bids[0]);

      case 'balanced':
      default:
        // Best value (confidence / cost ratio)
        return bids.reduce((a, b) => {
          const valueA = a.confidence / a.proposedCost;
          const valueB = b.confidence / b.proposedCost;
          return valueA > valueB ? a : b;
        });
    }
  }

  /**
   * Assign task to winning agent
   */
  private async assignTask(task: Task, winningBid: TaskBid): Promise<void> {
    const agent = this.state.agents.get(winningBid.agentId);
    if (!agent) return;

    // Record auction result
    const result: AuctionResult = {
      taskId: task.id,
      winnerId: winningBid.agentId,
      winningBid: winningBid.proposedCost,
      competingBids: task.bids.length,
      timestamp: new Date(),
    };
    this.state.marketplace.completedAuctions.push(result);

    // Update price history
    const history = this.state.marketplace.priceHistory.get(task.type) || [];
    history.push(winningBid.proposedCost);
    this.state.marketplace.priceHistory.set(task.type, history.slice(-100));

    // Clear active bids
    this.state.marketplace.activeBids.delete(task.id);

    // Assign to agent
    agent.acceptTask(task);

    this.emit('task-assigned', { task, agent: agent.id, bid: winningBid });
    this.emit('auction-complete', { result });

    // Update utilization
    this.updateAgentUtilization(agent.id);
  }

  /**
   * Handle task completion
   */
  onTaskComplete(task: Task): void {
    this.state.metrics.completedTasks++;

    // Update average latency
    if (task.timeline.started && task.timeline.completed) {
      const latency = task.timeline.completed.getTime() - task.timeline.started.getTime();
      const n = this.state.metrics.completedTasks;
      this.state.metrics.averageLatency =
        (this.state.metrics.averageLatency * (n - 1) + latency) / n;
    }

    // Update average cost
    const winningBid = task.bids.find(b => b.agentId === task.assignedAgent);
    if (winningBid) {
      const n = this.state.metrics.completedTasks;
      this.state.metrics.averageCost =
        (this.state.metrics.averageCost * (n - 1) + winningBid.proposedCost) / n;
    }

    // Update throughput
    this.updateThroughput();

    // Update agent utilization
    if (task.assignedAgent) {
      this.updateAgentUtilization(task.assignedAgent);
    }

    this.emit('task-completed', { task });

    // Check for child tasks
    this.processChildTasks(task);
  }

  /**
   * Handle task failure
   */
  onTaskFailed(task: Task, error: Error): void {
    this.state.metrics.failedTasks++;

    this.emit('task-failed', { task, error: error.message });

    // Retry logic
    const retryCount = (task.metadata.retryCount as number | undefined) ?? 0;

    if (retryCount < 3) {
      task.metadata.retryCount = retryCount + 1;
      task.status = 'pending';
      task.bids = [];

      // Block the failed agent for this task
      if (task.assignedAgent) {
        task.constraints.blockedAgents.push(task.assignedAgent);
      }

      // Re-run auction
      this.runAuction(task);
    }
  }

  /**
   * Send a message between agents
   */
  sendMessage(message: AgentMessage): void {
    if (message.to === 'broadcast') {
      // Broadcast to all agents
      for (const agent of this.state.agents.values()) {
        if (agent.id !== message.from) {
          agent.receive(message);
        }
      }
    } else {
      // Direct message
      const recipient = this.state.agents.get(message.to);
      if (recipient) {
        recipient.receive(message);
      }
    }

    this.emit('agent-message', { message });

    // Track conversation if applicable
    if (message.conversationId) {
      this.trackConversation(message);
    }
  }

  /**
   * Create a collaboration between agents
   */
  async createCollaboration(
    topic: string,
    participantRoles: AgentRole[],
    context: Record<string, unknown>
  ): Promise<Conversation> {
    // Find agents for each role
    const participants: string[] = [];

    for (const role of participantRoles) {
      const agent = Array.from(this.state.agents.values()).find(
        a => a.role === role && !participants.includes(a.id)
      );

      if (agent) {
        participants.push(agent.id);
      }
    }

    const conversation: Conversation = {
      id: `conv_${Date.now()}`,
      participants,
      topic,
      messages: [],
      status: 'active',
      startedAt: new Date(),
    };

    this.state.activeConversations.set(conversation.id, conversation);

    // Notify participants
    for (const participantId of participants) {
      const agent = this.state.agents.get(participantId);
      if (agent) {
        agent.receive({
          id: `msg_${Date.now()}`,
          from: 'orchestrator',
          to: participantId,
          type: 'task-request',
          payload: { conversation, context },
          priority: 1,
          timestamp: new Date(),
          requiresResponse: true,
          conversationId: conversation.id,
        });
      }
    }

    return conversation;
  }

  /**
   * Get orchestration state summary
   */
  getState(): {
    agents: Array<{ id: string; role: AgentRole; status: string; queueDepth: number }>;
    metrics: OrchestrationMetrics;
    marketplace: {
      openTasks: number;
      activeBids: number;
      averagePrice: Record<string, number>;
    };
    activeConversations: number;
  } {
    const agents = Array.from(this.state.agents.values()).map(a => ({
      id: a.id,
      role: a.role,
      status: a.status,
      queueDepth: a.taskQueue.length,
    }));

    const averagePrice: Record<string, number> = {};
    for (const [type, history] of this.state.marketplace.priceHistory) {
      averagePrice[type] = history.reduce((a, b) => a + b, 0) / history.length;
    }

    return {
      agents,
      metrics: this.state.metrics,
      marketplace: {
        openTasks: this.state.marketplace.openTasks.length,
        activeBids: this.state.marketplace.activeBids.size,
        averagePrice,
      },
      activeConversations: this.state.activeConversations.size,
    };
  }

  /**
   * Get visualization data for the orchestration canvas
   */
  getVisualizationData(): {
    agents: Array<{
      id: string;
      role: AgentRole;
      name: string;
      status: string;
      position: { x: number; y: number };
      connections: Array<{ targetId: string; strength: number }>;
    }>;
    taskFlows: Array<{
      id: string;
      from: string;
      to: string;
      taskType: string;
      progress: number;
    }>;
    metrics: {
      throughput: number;
      efficiency: number;
      agentUtilization: Record<string, number>;
    };
  } {
    const agents = Array.from(this.state.agents.values()).map((a, i) => {
      // Arrange agents in a circle
      const angle = (i / this.state.agents.size) * 2 * Math.PI;
      const radius = 200;

      return {
        id: a.id,
        role: a.role,
        name: a.name,
        status: a.status,
        position: {
          x: Math.cos(angle) * radius + 250,
          y: Math.sin(angle) * radius + 250,
        },
        connections: Array.from(a.memory.collaboratorProfiles.entries()).map(
          ([targetId, profile]) => ({
            targetId,
            strength: profile.trustScore,
          })
        ),
      };
    });

    // Create task flows from active tasks
    const taskFlows = this.state.taskQueue
      .filter(t => t.status === 'in-progress' && t.assignedAgent)
      .map(t => ({
        id: t.id,
        from: 'queue',
        to: t.assignedAgent!,
        taskType: t.type,
        progress: 0.5, // Would be calculated from actual progress
      }));

    return {
      agents,
      taskFlows,
      metrics: {
        throughput: this.state.metrics.throughput,
        efficiency: this.state.metrics.efficiency,
        agentUtilization: Object.fromEntries(this.state.metrics.agentUtilization),
      },
    };
  }

  /**
   * Scale agents up or down based on demand
   */
  async autoScale(): Promise<void> {
    if (!this.state.config.autoScaling) return;

    // Check supply/demand for each task type
    for (const [taskType, { supply, demand }] of this.state.marketplace.supplyDemand) {
      if (demand > supply * 1.5) {
        // Need more agents for this task type
        const role = this.taskTypeToRole(taskType);
        await this.spawnAgent(role);
      } else if (supply > demand * 2 && supply > 2) {
        // Can reduce agents
        const role = this.taskTypeToRole(taskType);
        await this.retireAgent(role);
      }
    }
  }

  /**
   * Spawn a new agent of the given role
   */
  private async spawnAgent(role: AgentRole): Promise<Agent | null> {
    const count = Array.from(this.state.agents.values()).filter(a => a.role === role).length;

    let agent: Agent;
    switch (role) {
      case 'hunter':
        agent = new HunterAgent(`Hunter ${String.fromCharCode(65 + count)}`);
        break;
      case 'scout':
        agent = new ScoutAgent(`Scout ${String.fromCharCode(65 + count)}`);
        break;
      case 'writer':
        agent = new WriterAgent(`Writer ${String.fromCharCode(65 + count)}`);
        break;
      case 'closer':
        agent = new CloserAgent(`Closer ${String.fromCharCode(65 + count)}`);
        break;
      case 'revops':
        agent = new RevOpsAgent(`RevOps ${String.fromCharCode(65 + count)}`);
        break;
      default:
        return null;
    }

    this.state.agents.set(agent.id, agent);
    this.state.metrics.agentUtilization.set(agent.id, 0);

    console.log(`Spawned new agent: ${agent.name}`);
    return agent;
  }

  /**
   * Retire an idle agent of the given role
   */
  private async retireAgent(role: AgentRole): Promise<boolean> {
    const candidate = Array.from(this.state.agents.values()).find(
      a => a.role === role && a.status === 'idle' && a.taskQueue.length === 0
    );

    if (candidate) {
      this.state.agents.delete(candidate.id);
      this.state.metrics.agentUtilization.delete(candidate.id);
      console.log(`Retired agent: ${candidate.name}`);
      return true;
    }

    return false;
  }

  // === Event System ===

  on(event: string, handler: EventHandler): void {
    const handlers = this.eventHandlers.get(event) || [];
    handlers.push(handler);
    this.eventHandlers.set(event, handlers);
  }

  off(event: string, handler: EventHandler): void {
    const handlers = this.eventHandlers.get(event) || [];
    const index = handlers.indexOf(handler);
    if (index > -1) {
      handlers.splice(index, 1);
    }
  }

  private emit(type: OrchestrationEvent['type'], data: unknown): void {
    const event: OrchestrationEvent = { type, timestamp: new Date(), data };
    const handlers = this.eventHandlers.get(type) || [];
    handlers.forEach(handler => handler(event));
  }

  // === Private Helpers ===

  private inferCapabilities(taskType: TaskType): string[] {
    const mapping: Record<TaskType, string[]> = {
      'find-accounts': ['account-discovery', 'icp-matching'],
      'research-signal': ['signal-research', 'signal-detection'],
      'enrich-contact': ['contact-identification'],
      'craft-message': ['message-generation', 'personalization'],
      personalize: ['personalization', 'tone-adaptation'],
      'send-sequence': ['sequence-optimization'],
      'handle-reply': ['reply-handling', 'objection-handling'],
      'schedule-meeting': ['meeting-scheduling'],
      negotiate: ['negotiation'],
      'optimize-sequence': ['sequence-optimization'],
      'analyze-performance': ['pipeline-analysis', 'performance-forecasting'],
      'rebalance-pipeline': ['pipeline-analysis', 'routing-optimization'],
    };
    return mapping[taskType] || [];
  }

  private taskTypeToRole(taskType: TaskType): AgentRole {
    const mapping: Record<TaskType, AgentRole> = {
      'find-accounts': 'hunter',
      'research-signal': 'scout',
      'enrich-contact': 'hunter',
      'craft-message': 'writer',
      personalize: 'writer',
      'send-sequence': 'revops',
      'handle-reply': 'closer',
      'schedule-meeting': 'closer',
      negotiate: 'closer',
      'optimize-sequence': 'revops',
      'analyze-performance': 'revops',
      'rebalance-pipeline': 'revops',
    };
    return mapping[taskType] || 'revops';
  }

  private updateAgentUtilization(agentId: string): void {
    const agent = this.state.agents.get(agentId);
    if (!agent) return;

    const utilization =
      agent.status === 'working'
        ? 1
        : agent.taskQueue.length > 0
          ? 0.5 + agent.taskQueue.length * 0.1
          : 0;

    this.state.metrics.agentUtilization.set(agentId, Math.min(utilization, 1));
  }

  private updateThroughput(): void {
    // Calculate throughput as tasks completed per minute (rolling 5-minute window)
    const now = Date.now();
    const recentAuctions = this.state.marketplace.completedAuctions.filter(
      a => now - a.timestamp.getTime() < 5 * 60 * 1000
    );

    this.state.metrics.throughput = recentAuctions.length / 5;
  }

  private trackConversation(message: AgentMessage): void {
    if (!message.conversationId) return;

    const conversation = this.state.activeConversations.get(message.conversationId);
    if (conversation) {
      conversation.messages.push(message);
    }
  }

  private processChildTasks(parentTask: Task): void {
    // If task output suggests follow-up tasks, create them
    if (parentTask.output?.sideEffects) {
      for (const effect of parentTask.output.sideEffects) {
        if (effect.startsWith('create-task:')) {
          const taskType = effect.replace('create-task:', '') as TaskType;
          this.createTask({
            type: taskType,
            input: {
              type: 'follow-up',
              data: parentTask.output.data,
              context: { parentTaskId: parentTask.id },
            },
          });
        }
      }
    }
  }
}

// Singleton export
export const orchestrator = new Orchestrator();
export { Orchestrator };
export default Orchestrator;

/**
 * Scout Agent
 *
 * Researches signals and intent. Deep-dives into accounts
 * to uncover timing, triggers, and competitive intelligence.
 */

import { BaseAgent, AgentContext, ExecutionResult } from './BaseAgent';
import type { Task } from './types';

interface SignalIntelligence {
  accountId: string;
  signals: DiscoveredSignal[];
  competitorPresence: CompetitorIntel[];
  buyingCommittee: BuyingCommitteeMember[];
  timingIndicators: TimingIndicator[];
  recommendedApproach: string;
  urgencyScore: number;
}

interface DiscoveredSignal {
  type: string;
  source: string;
  confidence: number;
  evidence: string;
  discoveredAt: Date;
  expiresAt?: Date;
}

interface CompetitorIntel {
  competitor: string;
  relationship: 'active-customer' | 'evaluating' | 'churned' | 'unknown';
  contractEnd?: Date;
  satisfactionSignals: string[];
}

interface BuyingCommitteeMember {
  contactId: string;
  role: 'champion' | 'decision-maker' | 'influencer' | 'blocker' | 'user';
  engagement: number;
  sentiment: 'positive' | 'neutral' | 'negative' | 'unknown';
  notes: string[];
}

interface TimingIndicator {
  type: 'budget-cycle' | 'contract-renewal' | 'initiative' | 'pain-event' | 'growth-trigger';
  timing: 'now' | 'soon' | 'future' | 'passed';
  confidence: number;
  details: string;
}

export class ScoutAgent extends BaseAgent {
  private researchCache: Map<string, SignalIntelligence> = new Map();
  private signalPatterns: Map<string, number> = new Map();

  constructor(name: string = 'Scout Alpha') {
    super('scout', name, {
      autonomyLevel: 'semi-autonomous',
      riskTolerance: 0.4,
      creativityBias: 0.8,
    });
  }

  protected initializeCapabilities(): void {
    this.capabilities = [
      {
        id: 'signal-research',
        name: 'Deep Signal Research',
        skillLevel: 0.9,
        cost: 8,
        latency: 3000,
        successRate: 0.85,
      },
      {
        id: 'competitor-analysis',
        name: 'Competitive Intelligence',
        skillLevel: 0.8,
        cost: 6,
        latency: 2500,
        successRate: 0.8,
      },
      {
        id: 'committee-mapping',
        name: 'Buying Committee Mapping',
        skillLevel: 0.85,
        cost: 7,
        latency: 2000,
        successRate: 0.82,
      },
      {
        id: 'timing-analysis',
        name: 'Timing Window Analysis',
        skillLevel: 0.75,
        cost: 5,
        latency: 1500,
        successRate: 0.78,
      },
    ];
  }

  protected async executeTask(task: Task, _context: AgentContext): Promise<ExecutionResult> {
    switch (task.type) {
      case 'research-signal':
        return this.deepResearch(task);
      default:
        throw new Error(`Scout cannot handle task type: ${task.type}`);
    }
  }

  /**
   * Perform deep research on an account
   */
  private async deepResearch(task: Task): Promise<ExecutionResult> {
    const input = task.input.data as { accountId: string; accountName?: string; domain?: string };

    // Check cache first
    if (this.researchCache.has(input.accountId)) {
      const cached = this.researchCache.get(input.accountId)!;
      const age = Date.now() - cached.signals[0]?.discoveredAt.getTime() || 0;
      if (age < 24 * 60 * 60 * 1000) {
        // 24 hour cache
        return {
          success: true,
          output: cached,
          artifacts: [cached],
          learnings: ['Retrieved from cache'],
        };
      }
    }

    // Parallel research operations
    const [signals, competitors, committee, timing] = await Promise.all([
      this.discoverSignals(input.accountId),
      this.analyzeCompetitors(input.accountId),
      this.mapBuyingCommittee(input.accountId),
      this.analyzeTimingWindows(input.accountId),
    ]);

    const urgencyScore = this.calculateUrgencyScore(signals, timing);
    const recommendedApproach = this.synthesizeApproach(signals, competitors, committee, timing);

    const intelligence: SignalIntelligence = {
      accountId: input.accountId,
      signals,
      competitorPresence: competitors,
      buyingCommittee: committee,
      timingIndicators: timing,
      recommendedApproach,
      urgencyScore,
    };

    // Cache the results
    this.researchCache.set(input.accountId, intelligence);

    // Extract learnings
    const learnings = this.extractResearchLearnings(intelligence);

    return {
      success: signals.length > 0 || committee.length > 0,
      output: intelligence,
      artifacts: [intelligence],
      learnings,
      nextSuggestion: urgencyScore > 0.7 ? 'prioritize-outreach' : undefined,
    };
  }

  // === Research Methods ===

  private async discoverSignals(_accountId: string): Promise<DiscoveredSignal[]> {
    await this.simulateLatency(800);

    const signalTypes = [
      { type: 'hiring-surge', sources: ['linkedin', 'glassdoor'] },
      { type: 'funding-round', sources: ['crunchbase', 'news'] },
      { type: 'tech-adoption', sources: ['builtwith', 'g2'] },
      { type: 'leadership-change', sources: ['linkedin', 'news'] },
      { type: 'expansion-indicator', sources: ['news', 'job-boards'] },
      { type: 'pain-signal', sources: ['g2-reviews', 'social'] },
    ];

    const signals: DiscoveredSignal[] = [];
    const count = Math.floor(Math.random() * 5) + 1;

    for (let i = 0; i < count; i++) {
      const signalDef = signalTypes[Math.floor(Math.random() * signalTypes.length)];
      signals.push({
        type: signalDef.type,
        source: signalDef.sources[Math.floor(Math.random() * signalDef.sources.length)],
        confidence: Math.random() * 0.4 + 0.6,
        evidence: this.generateEvidence(signalDef.type),
        discoveredAt: new Date(),
        expiresAt: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000),
      });

      // Update signal patterns
      const patternWeight = this.signalPatterns.get(signalDef.type) || 0.5;
      this.signalPatterns.set(signalDef.type, patternWeight * 0.9 + 0.1);
    }

    return signals.sort((a, b) => b.confidence - a.confidence);
  }

  private async analyzeCompetitors(_accountId: string): Promise<CompetitorIntel[]> {
    await this.simulateLatency(600);

    const competitors = ['Competitor A', 'Competitor B', 'Competitor C', 'Legacy System'];
    const intel: CompetitorIntel[] = [];
    const count = Math.floor(Math.random() * 3);

    const relationships: CompetitorIntel['relationship'][] = [
      'active-customer',
      'evaluating',
      'churned',
      'unknown',
    ];

    for (let i = 0; i < count; i++) {
      const relationship = relationships[Math.floor(Math.random() * relationships.length)];
      intel.push({
        competitor: competitors[Math.floor(Math.random() * competitors.length)],
        relationship,
        contractEnd:
          relationship === 'active-customer'
            ? new Date(Date.now() + Math.random() * 365 * 24 * 60 * 60 * 1000)
            : undefined,
        satisfactionSignals: this.generateSatisfactionSignals(relationship),
      });
    }

    return intel;
  }

  private async mapBuyingCommittee(accountId: string): Promise<BuyingCommitteeMember[]> {
    await this.simulateLatency(700);

    const roles: BuyingCommitteeMember['role'][] = [
      'champion',
      'decision-maker',
      'influencer',
      'blocker',
      'user',
    ];
    const sentiments: BuyingCommitteeMember['sentiment'][] = [
      'positive',
      'neutral',
      'negative',
      'unknown',
    ];

    const committee: BuyingCommitteeMember[] = [];
    const count = Math.floor(Math.random() * 5) + 2;

    for (let i = 0; i < count; i++) {
      committee.push({
        contactId: `contact_${accountId}_${i}`,
        role: roles[Math.floor(Math.random() * roles.length)],
        engagement: Math.random(),
        sentiment: sentiments[Math.floor(Math.random() * sentiments.length)],
        notes: [this.generateCommitteeNote()],
      });
    }

    // Ensure at least one decision-maker
    if (!committee.some(c => c.role === 'decision-maker')) {
      committee[0].role = 'decision-maker';
    }

    return committee.sort((a, b) => {
      const roleOrder = { 'decision-maker': 0, champion: 1, influencer: 2, user: 3, blocker: 4 };
      return (roleOrder[a.role] || 5) - (roleOrder[b.role] || 5);
    });
  }

  private async analyzeTimingWindows(_accountId: string): Promise<TimingIndicator[]> {
    await this.simulateLatency(500);

    const types: TimingIndicator['type'][] = [
      'budget-cycle',
      'contract-renewal',
      'initiative',
      'pain-event',
      'growth-trigger',
    ];
    const timings: TimingIndicator['timing'][] = ['now', 'soon', 'future', 'passed'];

    const indicators: TimingIndicator[] = [];
    const count = Math.floor(Math.random() * 3) + 1;

    for (let i = 0; i < count; i++) {
      const type = types[Math.floor(Math.random() * types.length)];
      indicators.push({
        type,
        timing: timings[Math.floor(Math.random() * timings.length)],
        confidence: Math.random() * 0.4 + 0.5,
        details: this.generateTimingDetails(type),
      });
    }

    return indicators.sort((a, b) => {
      const timingOrder = { now: 0, soon: 1, future: 2, passed: 3 };
      return (timingOrder[a.timing] || 4) - (timingOrder[b.timing] || 4);
    });
  }

  // === Synthesis Methods ===

  private calculateUrgencyScore(signals: DiscoveredSignal[], timing: TimingIndicator[]): number {
    let score = 0;
    let factors = 0;

    // Signal strength
    const avgSignalConfidence =
      signals.reduce((a, b) => a + b.confidence, 0) / Math.max(signals.length, 1);
    score += avgSignalConfidence * 0.4;
    factors++;

    // Timing urgency
    const urgentTiming = timing.filter(t => t.timing === 'now' || t.timing === 'soon');
    if (urgentTiming.length > 0) {
      score += (urgentTiming.length / timing.length) * 0.4;
    }
    factors++;

    // High-value signals boost
    const highValueSignals = ['funding-round', 'expansion-indicator', 'pain-signal'];
    const hasHighValue = signals.some(s => highValueSignals.includes(s.type));
    if (hasHighValue) {
      score += 0.2;
    }
    factors++;

    return Math.min(score, 1);
  }

  private synthesizeApproach(
    signals: DiscoveredSignal[],
    competitors: CompetitorIntel[],
    committee: BuyingCommitteeMember[],
    timing: TimingIndicator[]
  ): string {
    const approaches: string[] = [];

    // Signal-based approach
    if (signals.some(s => s.type === 'pain-signal')) {
      approaches.push('Lead with pain acknowledgment and solution');
    }
    if (signals.some(s => s.type === 'funding-round')) {
      approaches.push('Position as growth enabler');
    }
    if (signals.some(s => s.type === 'hiring-surge')) {
      approaches.push('Emphasize scalability and team efficiency');
    }

    // Competitor-based approach
    const churned = competitors.find(c => c.relationship === 'churned');
    if (churned) {
      approaches.push(`Reference ${churned.competitor} limitations`);
    }
    const active = competitors.find(c => c.relationship === 'active-customer');
    if (active && active.contractEnd) {
      const daysToRenewal = (active.contractEnd.getTime() - Date.now()) / (1000 * 60 * 60 * 24);
      if (daysToRenewal < 90) {
        approaches.push('Target upcoming renewal window');
      }
    }

    // Committee-based approach
    const champion = committee.find(c => c.role === 'champion' && c.sentiment === 'positive');
    if (champion) {
      approaches.push('Leverage internal champion');
    }
    const blocker = committee.find(c => c.role === 'blocker');
    if (blocker) {
      approaches.push('Prepare blocker mitigation strategy');
    }

    // Timing-based approach
    if (timing.some(t => t.timing === 'now')) {
      approaches.push('Execute immediately - window is open');
    }

    return approaches.length > 0
      ? approaches.join('. ')
      : 'Standard multi-touch sequence with personalization';
  }

  private extractResearchLearnings(intel: SignalIntelligence): string[] {
    const learnings: string[] = [];

    if (intel.urgencyScore > 0.7) {
      learnings.push('High-urgency account identified - prioritize for immediate action');
    }

    if (intel.buyingCommittee.some(c => c.role === 'champion')) {
      learnings.push('Champion identified - multi-threading opportunity');
    }

    if (intel.competitorPresence.some(c => c.relationship === 'churned')) {
      learnings.push('Competitor churn detected - displacement opportunity');
    }

    const strongSignals = intel.signals.filter(s => s.confidence > 0.8);
    if (strongSignals.length > 2) {
      learnings.push(`Strong signal cluster: ${strongSignals.map(s => s.type).join(', ')}`);
    }

    return learnings;
  }

  // === Helper Methods ===

  private generateEvidence(signalType: string): string {
    const evidenceMap: Record<string, string[]> = {
      'hiring-surge': ['Posted 15 new roles in engineering', '3x hiring velocity vs last quarter'],
      'funding-round': ['Announced Series B of $25M', 'Added 3 new board members'],
      'tech-adoption': ['Migrating to cloud infrastructure', 'Adopted new CRM system'],
      'leadership-change': ['New VP of Sales appointed', 'CTO transition announced'],
      'expansion-indicator': ['Opening new office in EU', 'Launched in 5 new markets'],
      'pain-signal': ['G2 review mentions scaling issues', 'LinkedIn post about tool frustration'],
    };
    const options = evidenceMap[signalType] || ['General activity detected'];
    return options[Math.floor(Math.random() * options.length)];
  }

  private generateSatisfactionSignals(relationship: CompetitorIntel['relationship']): string[] {
    if (relationship === 'churned') {
      return ['Left negative review', 'Mentioned limitations on LinkedIn'];
    }
    if (relationship === 'active-customer') {
      return Math.random() > 0.5
        ? ['Appears satisfied', 'Renewed last year']
        : ['Mixed reviews', 'Exploring alternatives'];
    }
    return [];
  }

  private generateCommitteeNote(): string {
    const notes = [
      'Engaged with content on LinkedIn',
      'Attended competitor webinar',
      'Active in industry Slack community',
      'Published thought leadership on pain points',
      'Connected with our customers',
      'Mentioned evaluation in social post',
    ];
    return notes[Math.floor(Math.random() * notes.length)];
  }

  private generateTimingDetails(type: TimingIndicator['type']): string {
    const details: Record<string, string[]> = {
      'budget-cycle': ['Q1 budget planning', 'Fiscal year starts in March'],
      'contract-renewal': ['Current vendor contract ends Q2', 'Annual renewal coming up'],
      initiative: ['Digital transformation project', 'GTM optimization initiative'],
      'pain-event': ['Recent outage with current tool', 'Missed targets last quarter'],
      'growth-trigger': ['Just raised funding', 'Expanding sales team'],
    };
    const options = details[type] || ['General timing signal'];
    return options[Math.floor(Math.random() * options.length)];
  }

  private simulateLatency(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms * 0.1));
  }

  protected describeStrategy(_task: Task): string {
    return `Scout will perform deep research across ${this.capabilities.length} intelligence dimensions`;
  }
}

export default ScoutAgent;

