);

export const NotificationBell = ({ className }) => {
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  const {
    activities,
    groupedActivities,
    unreadCount,
    isLoading,
    filter,
    setFilter,
    markAsRead,
    markAllAsRead,
    refresh,
  } = useActivityFeed();

  const handleNavigate = activity => {
    setIsOpen(false);
    switch (activity.entityType) {
      case 'campaign':
        navigate(`/campaigns/${activity.entityId}`);
        break;
      case 'lead':
        navigate(`/leads?id=${activity.entityId}`);
        break;
      case 'integration':
        navigate('/integrations');
        break;
      default:
        navigate('/activity');
    }
  };

  return (
    <div className={cn('relative', className)}>
      {/* Bell Button */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="relative p-2 hover:bg-gray-100 dark:hover:bg-white/10 rounded-xl transition-colors"
        aria-label="Notifications"
      >
        <Bell className="h-5 w-5 text-gray-600 dark:text-gray-300" />
        {unreadCount > 0 && (
          <span className="absolute top-0.5 right-0.5 w-4 h-4 bg-red-500 text-white text-[10px] font-bold rounded-full flex items-center justify-center">
            {unreadCount > 9 ? '9+' : unreadCount}
          </span>
        )}
      </button>

      {/* Dropdown Panel */}
      <AnimatePresence>
        {isOpen && (
          <>
            {/* Backdrop */}
            <div
              role="button"
              tabIndex={0}
              className="fixed inset-0 z-40"
              onClick={() => setIsOpen(false)}
              onKeyDown={e => {
                if (e.key === 'Enter' || e.key === ' ' || e.key === 'Escape') {
                  e.preventDefault();
                  setIsOpen(false);
                }
              }}
              aria-label="Close notifications"
            />

            {/* Panel */}
            <motion.div
              initial={{ opacity: 0, y: 10, scale: 0.95 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              exit={{ opacity: 0, y: 10, scale: 0.95 }}
              transition={{ duration: 0.15 }}
              className="absolute right-0 top-full mt-2 w-[400px] max-h-[70vh] bg-white dark:bg-gray-900 rounded-2xl shadow-2xl border border-gray-200 dark:border-white/10 z-50 overflow-hidden flex flex-col"
            >
              {/* Header */}
              <div className="p-4 border-b border-gray-100 dark:border-white/10">
                <div className="flex items-center justify-between mb-3">
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white">Activity</h3>
                  <div className="flex items-center gap-2">
                    <button
                      onClick={refresh}
                      disabled={isLoading}
                      className="p-1.5 hover:bg-gray-100 dark:hover:bg-white/10 rounded-lg transition-colors"
                      title="Refresh"
                    >
                      <RefreshCw
                        className={cn('h-4 w-4 text-gray-500', isLoading && 'animate-spin')}
                      />
                    </button>
                    <button
                      onClick={markAllAsRead}
                      disabled={unreadCount === 0}
                      className="p-1.5 hover:bg-gray-100 dark:hover:bg-white/10 rounded-lg transition-colors disabled:opacity-50"
                      title="Mark all as read"
                    >
                      <CheckCheck className="h-4 w-4 text-gray-500" />
                    </button>
                    <button
                      onClick={() => setIsOpen(false)}
                      className="p-1.5 hover:bg-gray-100 dark:hover:bg-white/10 rounded-lg transition-colors"
                    >
                      <X className="h-4 w-4 text-gray-500" />
                    </button>
                  </div>
                </div>

                {/* Filters */}
                <div className="flex gap-2 overflow-x-auto pb-1 -mb-1">
                  <FilterButton
                    active={filter === 'all'}
                    onClick={() => setFilter('all')}
                    count={0}
                  >
                    All
                  </FilterButton>
                  <FilterButton
                    active={filter === 'unread'}
                    onClick={() => setFilter('unread')}
                    count={unreadCount}
                  >
                    Unread
                  </FilterButton>
                  <FilterButton
                    active={filter === 'campaign'}
                    onClick={() => setFilter('campaign')}
                    count={0}
                  >
                    Campaigns
                  </FilterButton>
                  <FilterButton
                    active={filter === 'lead'}
                    onClick={() => setFilter('lead')}
                    count={0}
                  >
                    Leads
                  </FilterButton>
                  <FilterButton active={filter === 'ai'} onClick={() => setFilter('ai')} count={0}>
                    AI
                  </FilterButton>
                </div>
              </div>

              {/* Activity List */}
              <div className="flex-1 overflow-y-auto">
                {activities.length === 0 ? (
                  <div className="py-12 text-center">
                    <Bell className="h-12 w-12 text-gray-300 dark:text-gray-700 mx-auto mb-3" />
                    <p className="text-gray-500 dark:text-gray-400">No activity yet</p>
                  </div>
                ) : (
                  <AnimatePresence mode="popLayout">
                    {/* Today */}
                    {groupedActivities.today.length > 0 && (
                      <div>
                        <div className="px-4 py-2 text-xs font-semibold text-gray-400 uppercase tracking-wider bg-gray-50 dark:bg-white/5">
                          Today
                        </div>
                        {groupedActivities.today.map(activity => (
                          <ActivityItem
                            key={activity.id}
                            activity={activity}
                            onMarkAsRead={markAsRead}
                            onNavigate={handleNavigate}
                          />
                        ))}
                      </div>
                    )}

                    {/* Yesterday */}
                    {groupedActivities.yesterday.length > 0 && (
                      <div>
                        <div className="px-4 py-2 text-xs font-semibold text-gray-400 uppercase tracking-wider bg-gray-50 dark:bg-white/5">
                          Yesterday
                        </div>
                        {groupedActivities.yesterday.map(activity => (
                          <ActivityItem
                            key={activity.id}
                            activity={activity}
                            onMarkAsRead={markAsRead}
                            onNavigate={handleNavigate}
                          />
                        ))}
                      </div>
                    )}

                    {/* Older */}
                    {groupedActivities.older.length > 0 && (
                      <div>
                        <div className="px-4 py-2 text-xs font-semibold text-gray-400 uppercase tracking-wider bg-gray-50 dark:bg-white/5">
                          Earlier
                        </div>
                        {groupedActivities.older.map(activity => (
                          <ActivityItem
                            key={activity.id}
                            activity={activity}
                            onMarkAsRead={markAsRead}
                            onNavigate={handleNavigate}
                          />
                        ))}
                      </div>
                    )}
                  </AnimatePresence>
                )}
              </div>

              {/* Footer */}
              <div className="p-3 border-t border-gray-100 dark:border-white/10 bg-gray-50 dark:bg-white/5">
                <button
                  onClick={() => {
                    setIsOpen(false);
                    navigate('/activity');
                  }}
                  className="w-full text-center text-sm text-accent-600 dark:text-accent-400 font-medium hover:underline"
                >
                  View all activity 
                </button>
              </div>
            </motion.div>
          </>
        )}
      </AnimatePresence>
    </div>
  );
};

