                        <div className="h-1.5 bg-white/10 rounded-full mt-2 overflow-hidden">
                          <motion.div
                            className="h-full rounded-full"
                            style={{ backgroundColor: channel.color }}
                            initial={
                              prefersReducedMotion ? { width: `${channel.value}%` } : { width: 0 }
