// Main Component
export const PlaybookAttributionMatrix = ({ playbooks = [], onDrillDown }) => {
  const [selectedMetric, setSelectedMetric] = useState('response_rate');
  const [selectedCell, setSelectedCell] = useState(null);
  const [viewMode, setViewMode] = useState('standard'); // 'standard' | 'comparison'

  const segments = ['startup', 'midmarket', 'enterprise'];
  const channels = ['email', 'linkedin', 'phone'];

  // Generate matrix data
  const matrixData = useMemo(() => {
    const data = {};

    segments.forEach(segment => {
      data[segment] = {};
      channels.forEach(channel => {
        // Generate realistic data with some variation
        const baseRates = {
          startup: { email: 22, linkedin: 28, phone: 15 },
          midmarket: { email: 26, linkedin: 32, phone: 18 },
          enterprise: { email: 24, linkedin: 38, phone: 14 },
        };

        const baseMeetingRates = {
          startup: { email: 3.2, linkedin: 4.1, phone: 2.8 },
          midmarket: { email: 4.5, linkedin: 5.8, phone: 3.5 },
          enterprise: { email: 5.1, linkedin: 7.2, phone: 4.2 },
        };

        const variance = (Math.random() - 0.5) * 10;

        data[segment][channel] = {
          leads_targeted: Math.floor(Math.random() * 500 + 200),
          emails_sent: Math.floor(Math.random() * 400 + 150),
          responses: Math.floor(Math.random() * 100 + 30),
          meetings: Math.floor(Math.random() * 30 + 5),
          response_rate: baseRates[segment][channel] + variance,
          meeting_rate: baseMeetingRates[segment][channel] + variance / 5,
          roi: Math.floor(Math.random() * 50000 + 10000),
          trend: Math.floor(Math.random() * 40 - 15),
        };
      });
    });

    return data;
  }, [playbooks]);

  // Calculate benchmarks for the selected metric
  const benchmark = useMemo(() => {
    const allValues = segments.flatMap(s =>
      channels.map(c => matrixData[s]?.[c]?.[selectedMetric] || 0)
    );
    return allValues.reduce((a, b) => a + b, 0) / allValues.length;
  }, [matrixData, selectedMetric]);

  // Handle cell click
  const handleCellClick = (segment, channel) => {
    const data = matrixData[segment]?.[channel];
    if (data) {
      setSelectedCell({
        segment,
        channel,
        data: {
          value: data[selectedMetric],
          benchmark,
          trend: data.trend,
        },
      });
    }
  };

  const metricInfo = metricConfig[selectedMetric];

  return (
    <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg border border-gray-200 dark:border-gray-700 overflow-hidden">
      {/* Header */}
      <div className="p-5 border-b border-gray-200 dark:border-gray-700">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-gradient-to-br from-indigo-500 to-purple-500 rounded-lg flex items-center justify-center">
              <Grid3X3 className="text-white" size={20} />
            </div>
            <div>
              <h3 className="font-semibold text-gray-900 dark:text-white">Attribution Matrix</h3>
              <p className="text-sm text-gray-500 dark:text-gray-400">
                See what works where across segments & channels
              </p>
            </div>
          </div>

          <div className="flex items-center gap-3">
            {/* Metric Selector */}
            <select
              value={selectedMetric}
              onChange={e => setSelectedMetric(e.target.value)}
              className="px-3 py-1.5 text-sm bg-gray-100 dark:bg-gray-700 border-0 rounded-lg text-gray-700 dark:text-gray-300"
            >
              {Object.entries(metricConfig).map(([key, { label }]) => (
                <option key={key} value={key}>
                  {label}
                </option>
              ))}
            </select>

            {/* View Mode Toggle */}
            <div className="flex bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
              {['standard', 'comparison'].map(mode => (
                <button
                  key={mode}
                  onClick={() => setViewMode(mode)}
                  className={`px-3 py-1 text-sm rounded-md transition-colors capitalize ${
                    viewMode === mode
                      ? 'bg-white dark:bg-gray-600 text-gray-900 dark:text-white shadow'
                      : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white'
                  }`}
                >
                  {mode}
                </button>
              ))}
            </div>

            {/* Export */}
            <button className="p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors">
              <Download size={18} className="text-gray-600 dark:text-gray-400" />
            </button>
          </div>
        </div>
      </div>

      {/* Matrix Content */}
      <div className="p-5 overflow-x-auto">
        <div className="inline-block min-w-full">
          {/* Column Headers */}
          <div className="flex items-end mb-3">
            <div className="min-w-32" /> {/* Spacer for row headers */}
            {channels.map(channel => (
              <ColumnHeader
                key={channel}
                label={channel}
                icon={
                  channel === 'email' ? Mail : channel === 'linkedin' ? MessageSquare : Calendar
                }
              />
            ))}
            <div className="min-w-24 text-center">
              <span className="text-xs font-medium text-gray-500 dark:text-gray-400">Avg</span>
            </div>
          </div>

          {/* Matrix Rows */}
          <div className="space-y-2">
            {segments.map(segment => {
              const rowData = matrixData[segment];
              const rowAvg =
                channels.reduce((sum, ch) => sum + (rowData?.[ch]?.[selectedMetric] || 0), 0) /
                channels.length;

              return (
                <div key={segment} className="flex items-center gap-2">
                  <RowHeader
                    label={segment}
                    sublabel={
                      segment === 'startup'
                        ? '1-50 emp'
                        : segment === 'midmarket'
                          ? '51-500 emp'
                          : '500+ emp'
                    }
                    icon={Users}
                  />

                  {channels.map(channel => (
                    <MatrixCell
                      key={channel}
                      value={rowData?.[channel]?.[selectedMetric] || 0}
                      benchmark={benchmark}
                      format={metricInfo.format}
                      trend={rowData?.[channel]?.trend}
                      isSelected={
                        selectedCell?.segment === segment && selectedCell?.channel === channel
                      }
                      onClick={() => handleCellClick(segment, channel)}
                    />
                  ))}

                  {/* Row Average */}
                  <div className="min-w-24 h-16 flex items-center justify-center bg-gray-100 dark:bg-gray-700 rounded-lg">
                    <span className="text-sm font-medium text-gray-700 dark:text-gray-300">
                      {formatValue(rowAvg, metricInfo.format)}
                    </span>
                  </div>
                </div>
              );
            })}

            {/* Column Averages */}
            <div className="flex items-center gap-2 pt-2 border-t border-gray-200 dark:border-gray-700">
              <div className="min-w-32 text-right pr-4">
                <span className="text-xs font-medium text-gray-500 dark:text-gray-400">
                  Column Avg
                </span>
              </div>
              {channels.map(channel => {
                const colAvg =
                  segments.reduce(
                    (sum, seg) => sum + (matrixData[seg]?.[channel]?.[selectedMetric] || 0),
                    0
                  ) / segments.length;

                return (
                  <div
                    key={channel}
                    className="w-24 h-12 flex items-center justify-center bg-gray-100 dark:bg-gray-700 rounded-lg"
                  >
                    <span className="text-sm font-medium text-gray-700 dark:text-gray-300">
                      {formatValue(colAvg, metricInfo.format)}
                    </span>
                  </div>
                );
              })}
              <div className="min-w-24 h-12 flex items-center justify-center bg-purple-100 dark:bg-purple-900/30 rounded-lg">
                <span className="text-sm font-bold text-purple-700 dark:text-purple-300">
                  {formatValue(benchmark, metricInfo.format)}
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* Legend */}
        <div className="mt-6 pt-4 border-t border-gray-200 dark:border-gray-700">
          <MatrixLegend />
        </div>
      </div>

      {/* Selected Cell Detail */}
      {selectedCell && (
        <div className="px-5 pb-5">
          <AttributionDetail
            segment={selectedCell.segment}
            channel={selectedCell.channel}
            metric={selectedMetric}
            data={selectedCell.data}
            onClose={() => setSelectedCell(null)}
          />
        </div>
      )}
    </div>
  );
};

PlaybookAttributionMatrix.propTypes = {
  playbooks: PropTypes.array,
  onDrillDown: PropTypes.func,
};

export default PlaybookAttributionMatrix;

/**
 * PlaybookHealthHeatmap.jsx
 *
 * Macro performance awareness through a visual heatmap showing playbook health
 * across multiple dimensions: segments, channels, time periods, and goals.
 *
 * Color intensity represents performance relative to targets:
 * - Green: Exceeding targets
 * - Yellow: Meeting targets
 * - Orange: Below targets
 * - Red: Critical attention needed
 */

import React, { useState, useMemo } from 'react';
import PropTypes from 'prop-types';
import {
  Activity,
  TrendingUp,
  TrendingDown,
  AlertTriangle,
  ChevronDown,
  Filter,
  Maximize2,
  Info,
  Zap,
} from 'lucide-react';
import { AnimatedCounter } from '../../ui/AnimatedComponents';

// Health score to color mapping
const getHealthColor = (score, isDark = false) => {
  if (score >= 80) return isDark ? 'bg-green-600' : 'bg-green-500';
  if (score >= 60) return isDark ? 'bg-emerald-600' : 'bg-emerald-400';
  if (score >= 40) return isDark ? 'bg-yellow-600' : 'bg-yellow-400';
  if (score >= 20) return isDark ? 'bg-orange-600' : 'bg-orange-500';
  return isDark ? 'bg-red-700' : 'bg-red-500';
};

const getHealthTextColor = score => {
  if (score >= 60) return 'text-white';
  if (score >= 40) return 'text-gray-900';
  return 'text-white';
};

const getHealthLabel = score => {
  if (score >= 80) return 'Excellent';
  if (score >= 60) return 'Good';
  if (score >= 40) return 'Fair';
  if (score >= 20) return 'Poor';
  return 'Critical';
};

// Heatmap Cell Component
const HeatmapCell = ({ value, label, sublabel, onClick, isSelected, size = 'md' }) => {
  const sizeClasses = {
    sm: 'w-16 h-16',
    md: 'w-20 h-20',
    lg: 'w-24 h-24',
  };

  return (
    <button
      onClick={onClick}
      className={`
        ${sizeClasses[size]} 
        ${getHealthColor(value)} 
        ${getHealthTextColor(value)}
        ${isSelected ? 'ring-2 ring-white ring-offset-2 ring-offset-gray-900 scale-105' : ''}
        rounded-lg flex flex-col items-center justify-center
        transition-all duration-200 hover:scale-105 hover:shadow-lg
        relative group
      `}
    >
      <span className="text-lg font-bold">{value}%</span>
      {label && <span className="text-xs opacity-90 truncate max-w-full px-1">{label}</span>}

      {/* Tooltip */}
      <div className="absolute bottom-full left-1/2 -translate-x-1/2 mb-2 px-3 py-2 bg-gray-900 text-white text-xs rounded-lg opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none whitespace-nowrap z-10">
        <div className="font-semibold">{label}</div>
        {sublabel && <div className="text-gray-400">{sublabel}</div>}
        <div className="mt-1">{getHealthLabel(value)} Health</div>
        <div className="absolute top-full left-1/2 -translate-x-1/2 -mt-1 border-4 border-transparent border-t-gray-900" />
      </div>
    </button>
  );
};

HeatmapCell.propTypes = {
  value: PropTypes.number.isRequired,
  label: PropTypes.string,
  sublabel: PropTypes.string,
  onClick: PropTypes.func,
  isSelected: PropTypes.bool,
  size: PropTypes.oneOf(['sm', 'md', 'lg']),
};

// Segment Row in Heatmap
const SegmentRow = ({ segment, channels, onCellClick, selectedCell }) => {
  const avgHealth = Math.round(
    Object.values(channels).reduce((sum, ch) => sum + ch.health, 0) / Object.keys(channels).length
  );

  return (
    <div className="flex items-center gap-2">
      {/* Segment Label */}
      <div className="w-28 flex-shrink-0">
        <div className="text-sm font-medium text-gray-900 dark:text-white capitalize">
          {segment}
        </div>
        <div className="text-xs text-gray-500 dark:text-gray-400">Avg: {avgHealth}%</div>
      </div>

      {/* Channel Cells */}
      <div className="flex gap-2">
        {Object.entries(channels).map(([channel, data]) => (
          <HeatmapCell
            key={channel}
            value={data.health}
            label={channel}
            sublabel={`${data.leads} leads  ${data.meetings} meetings`}
            onClick={() => onCellClick({ segment, channel, ...data })}
            isSelected={selectedCell?.segment === segment && selectedCell?.channel === channel}
          />
        ))}
      </div>
    </div>
  );
};

SegmentRow.propTypes = {
  segment: PropTypes.string.isRequired,
  channels: PropTypes.object.isRequired,
  onCellClick: PropTypes.func.isRequired,
  selectedCell: PropTypes.shape({
    segment: PropTypes.string,
    channel: PropTypes.string,
  }),
};

// Time Period Heatmap
const TimeHeatmap = ({ data, onCellClick, selectedCell }) => {
  const periods = ['Week 1', 'Week 2', 'Week 3', 'Week 4'];
  const metrics = ['Response Rate', 'Meeting Rate', 'Pipeline Value', 'Conversion'];

  return (
    <div className="space-y-2">
      {/* Header */}
      <div className="flex items-center gap-2 ml-28">
        {periods.map(period => (
          <div
            key={period}
            className="w-20 text-center text-xs font-medium text-gray-600 dark:text-gray-400"
          >
            {period}
          </div>
        ))}
      </div>

      {/* Rows */}
      {metrics.map(metric => (
        <div key={metric} className="flex items-center gap-2">
          <div className="w-28 flex-shrink-0 text-sm text-gray-700 dark:text-gray-300">
            {metric}
          </div>
          <div className="flex gap-2">
            {periods.map((period, idx) => {
              const value = data[metric]?.[idx] || Math.floor(Math.random() * 60 + 40);
              return (
                <HeatmapCell
                  key={period}
                  value={value}
                  label={period}
                  sublabel={metric}
                  onClick={() => onCellClick({ metric, period, value })}
                  isSelected={selectedCell?.metric === metric && selectedCell?.period === period}
                />
              );
            })}
          </div>
        </div>
      ))}
    </div>
  );
};

TimeHeatmap.propTypes = {
  data: PropTypes.object.isRequired,
  onCellClick: PropTypes.func.isRequired,
  selectedCell: PropTypes.shape({
    metric: PropTypes.string,
    period: PropTypes.string,
  }),
};

// Health Legend
const HealthLegend = () => (
