  Sparkles,
  ChevronRight,
  ThumbsUp,
  ThumbsDown,
  RefreshCw,
  Brain,
} from 'lucide-react';

// Insight types with configurations
const insightTypes = {
  optimization: {
    icon: Zap,
    color: 'from-purple-500 to-pink-500',
    textColor: 'text-purple-600 dark:text-purple-400',
    bgColor: 'bg-purple-50 dark:bg-purple-900/20',
