import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Card, CardHeader, CardTitle, CardContent } from '../ui/Card';
import { Button } from '../ui/Button';
import { Input, Textarea } from '../ui/Input';
import { Badge } from '../ui/Badge';
import { FlaskConical, Plus, X, BarChart3 } from 'lucide-react';

export const ABTestingModule = ({ onSave }) => {
  const [variants, setVariants] = useState([
    { id: 'A', name: 'Variant A (Control)', subject: '', content: '', allocation: 50 },
    { id: 'B', name: 'Variant B', subject: '', content: '', allocation: 50 },
  ]);

  const [testDuration, setTestDuration] = useState('7');
  const [winMetric, setWinMetric] = useState('open_rate');

  const addVariant = () => {
    const nextLetter = String.fromCodePoint(65 + variants.length);
    const newAllocation = Math.floor(100 / (variants.length + 1));

    setVariants([
      ...variants.map(v => ({ ...v, allocation: newAllocation })),
      {
        id: nextLetter,
        name: `Variant ${nextLetter}`,
        subject: '',
        content: '',
        allocation: newAllocation,
      },
    ]);
  };

  const removeVariant = id => {
    if (variants.length <= 2) return;
    const filtered = variants.filter(v => v.id !== id);
    const newAllocation = Math.floor(100 / filtered.length);
    setVariants(filtered.map(v => ({ ...v, allocation: newAllocation })));
  };

  const updateVariant = (id, field, value) => {
    setVariants(variants.map(v => (v.id === id ? { ...v, [field]: value } : v)));
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <FlaskConical className="text-purple-500" size={20} />
            <CardTitle>A/B Testing</CardTitle>
          </div>
          <Badge variant="accent">Split Test</Badge>
        </div>
        <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">
          Test different variations to find what performs best
        </p>
      </CardHeader>
      <CardContent>
        <div className="space-y-6">
          {/* Test Configuration */}
          <div className="grid grid-cols-2 gap-4 p-4 bg-gray-50 dark:bg-white/5 rounded-lg">
            <div>
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                Test Duration
              </label>
              <select
                value={testDuration}
                onChange={e => setTestDuration(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
              >
                <option value="3">3 days</option>
                <option value="7">7 days</option>
                <option value="14">14 days</option>
                <option value="30">30 days</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                Winning Metric
              </label>
              <select
                value={winMetric}
                onChange={e => setWinMetric(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
              >
                <option value="open_rate">Open Rate</option>
                <option value="click_rate">Click Rate</option>
                <option value="reply_rate">Reply Rate</option>
                <option value="meeting_rate">Meeting Booked</option>
              </select>
            </div>
          </div>

          {/* Variants */}
          <div className="space-y-4">
            {variants.map((variant, index) => (
              <div
                key={variant.id}
                className="p-4 border border-gray-200 dark:border-white/10 rounded-lg"
              >
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center gap-2">
                    <span className="font-bold text-lg text-primary-600">{variant.id}</span>
                    <input
                      type="text"
                      value={variant.name}
                      onChange={e => updateVariant(variant.id, 'name', e.target.value)}
                      className="font-medium text-gray-900 dark:text-white bg-transparent border-none focus:outline-none"
                    />
                  </div>
                  <div className="flex items-center gap-2">
                    <span className="text-sm text-gray-600 dark:text-gray-400">
                      {variant.allocation}% traffic
                    </span>
                    {variants.length > 2 && (
                      <button
                        onClick={() => removeVariant(variant.id)}
                        className="p-1 hover:bg-red-100 dark:hover:bg-red-900/20 rounded"
                      >
                        <X size={16} className="text-red-500" />
                      </button>
                    )}
                  </div>
                </div>

                <div className="space-y-3">
                  <Input
                    label="Subject Line"
                    placeholder={`Subject line for variant ${variant.id}`}
                    value={variant.subject}
                    onChange={e => updateVariant(variant.id, 'subject', e.target.value)}
                  />
                  <Textarea
                    label="Email Content"
                    placeholder={`Email content for variant ${variant.id}`}
                    value={variant.content}
                    onChange={e => updateVariant(variant.id, 'content', e.target.value)}
                    rows={4}
                  />
                </div>
              </div>
            ))}
          </div>

          {/* Actions */}
          <div className="flex items-center justify-between pt-4 border-t border-gray-200 dark:border-white/10">
            <Button variant="outline" size="sm" onClick={addVariant} className="gap-2">
              <Plus size={16} />
              Add Variant
            </Button>

            <div className="flex items-center gap-2">
              <Button variant="ghost" size="sm">
                Cancel
              </Button>
              <Button onClick={() => onSave?.(variants)} className="gap-2">
                <BarChart3 size={16} />
                Start A/B Test
              </Button>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

ABTestingModule.propTypes = {
  onSave: PropTypes.func,
};

import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { Card, CardHeader, CardTitle, CardContent } from '../ui/Card';
import { Badge } from '../ui/Badge';
import { Clock, Sparkles, Calendar } from 'lucide-react';
import { dataService } from '../../lib/dataService';

export const SendTimeOptimizer = ({ leads = [], industry = null }) => {
  const [selectedDay, setSelectedDay] = useState(null);
  const [optimalTimes, setOptimalTimes] = useState([]);
  const [timezoneDistribution, setTimezoneDistribution] = useState([]);
  const [recommendation, setRecommendation] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchOptimalTimes();
  }, [leads.length, industry]);

  const fetchOptimalTimes = async () => {
    setLoading(true);

    try {
      // Call Python backend for send time optimization
      const response = await dataService.post('/campaigns/optimize-send-time', {
        lead_count: leads.length,
        lead_timezones: null, // Would extract from leads in production
        industry: industry,
        campaign_id: null,
      });

      setOptimalTimes(response.optimal_slots);
      setTimezoneDistribution(response.timezone_distribution);
      setRecommendation(response.personalized_recommendation);
    } catch (error) {
      console.error('Send time optimization failed:', error);
      // Fallback to default data
      setOptimalTimes([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center gap-2">
          <Clock className="text-primary-500" size={20} />
          <CardTitle>Send Time Optimization</CardTitle>
        </div>
        <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">
          AI predicts optimal send times based on your audience
        </p>
      </CardHeader>
      <CardContent>
        <div className="space-y-6">
          {/* Optimal Times List */}
          {loading ? (
            <div className="text-center py-8 text-gray-500">
              <Clock className="animate-spin mx-auto mb-2" size={24} />
              <p className="text-sm">Analyzing optimal send times...</p>
            </div>
          ) : (
            <div className="space-y-3">
              {optimalTimes.map((slot, index) => (
                <button
                  key={slot.day}
                  type="button"
                  className={`p-4 rounded-lg border transition-all cursor-pointer w-full text-left ${
                    slot.recommended
                      ? 'border-green-300 dark:border-green-600 bg-green-50 dark:bg-green-900/20'
                      : selectedDay === slot.day
                        ? 'border-primary-300 dark:border-primary-600 bg-primary-50 dark:bg-primary-900/20'
                        : 'border-gray-200 dark:border-white/10 hover:border-gray-300 dark:hover:border-white/20'
                  }`}
                  onClick={() => setSelectedDay(slot.day)}
                  onKeyDown={e => {
                    if (e.key === 'Enter' || e.key === ' ') {
                      e.preventDefault();
                      setSelectedDay(slot.day);
                    }
                  }}
                >
                  <div className="flex items-center justify-between mb-2">
                    <div className="flex items-center gap-3">
                      <div>
                        <div className="flex items-center gap-2">
                          <span className="font-semibold text-gray-900 dark:text-white">
                            {slot.day}
                          </span>
                          {slot.recommended && (
                            <Badge variant="success" className="text-xs gap-1">
                              <Sparkles size={10} />
                              Recommended
                            </Badge>
                          )}
                        </div>
                        <span className="text-sm text-gray-600 dark:text-gray-400">
                          {slot.time}
                        </span>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className="text-2xl font-bold text-gray-900 dark:text-white">
                        {slot.reply_rate}%
                      </div>
                      <div className="text-xs text-gray-600 dark:text-gray-400">reply rate</div>
                    </div>
                  </div>

                  <div className="mb-2">
                    <span className="text-sm text-gray-600 dark:text-gray-400">
                      {slot.reasoning}
                    </span>
                  </div>

                  <div className="flex items-center justify-between text-sm">
                    <div className="flex items-center gap-2">
                      <span className="text-xs text-gray-600 dark:text-gray-400">
                        Expected: {slot.expected_replies} replies from {slot.expected_opens} opens
                      </span>
                    </div>
                    <div className="flex items-center gap-2">
                      <Badge variant="secondary" className="text-xs">
                        {slot.confidence}% confidence
                      </Badge>
                      <Badge variant="secondary" className="text-xs">
                        {slot.volume} volume
                      </Badge>
                    </div>
                  </div>
                </button>
              ))}
            </div>
          )}

          {/* Timezone Distribution */}
          {!loading && timezoneDistribution.length > 0 && (
            <div className="pt-6 border-t border-gray-200 dark:border-white/10">
              <h4 className="font-semibold text-gray-900 dark:text-white mb-3 flex items-center gap-2">
                <Calendar size={16} />
                Timezone Distribution
              </h4>
              <div className="space-y-2">
                {timezoneDistribution.map(tz => (
                  <div key={tz.zone} className="flex items-center gap-3">
                    <span className="text-sm font-medium text-gray-700 dark:text-gray-300 w-12">
                      {tz.zone}
                    </span>
                    <div className="flex-1">
                      <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                        <div
                          className="bg-primary-500 h-2 rounded-full"
                          style={{ width: `${tz.percentage}%` }}
                        ></div>
                      </div>
                    </div>
                    <span className="text-sm text-gray-600 dark:text-gray-400 w-20 text-right">
                      {tz.lead_count} leads
                    </span>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* AI Insight */}
          {!loading && recommendation && (
            <div className="p-4 bg-gradient-to-r from-purple-50 to-blue-50 dark:from-purple-900/20 dark:to-blue-900/20 rounded-lg">
              <div className="flex items-start gap-3">
                <Sparkles className="text-purple-600 dark:text-purple-400 mt-0.5" size={18} />
                <div>
                  <h5 className="font-semibold text-gray-900 dark:text-white mb-1">
                    AI Recommendation
                  </h5>
                  <p className="text-sm text-gray-700 dark:text-gray-300">{recommendation}</p>
                </div>
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

SendTimeOptimizer.propTypes = {
  leads: PropTypes.array,
  industry: PropTypes.string,
};

import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { Card, CardHeader, CardTitle, CardContent } from '../ui/Card';
import { Badge } from '../ui/Badge';
import { AlertCircle, CheckCircle2, AlertTriangle, Shield } from 'lucide-react';
import { dataService } from '../../lib/dataService';

export const SpamChecker = ({ subject, content }) => {
  const [score, setScore] = useState(0);
  const [riskLevel, setRiskLevel] = useState('');
  const [issues, setIssues] = useState([]);
  const [loading, setLoading] = useState(false);
  const [safeToSend, setSafeToSend] = useState(true);
  const [deliverability, setDeliverability] = useState(100);

  useEffect(() => {
    if (subject || content) {
      checkSpam();
    }
  }, [subject, content]);

  const checkSpam = async () => {
    setLoading(true);

    try {
      // Call Python backend for spam analysis
      const response = await dataService.post('/campaigns/check-spam', {
        subject: subject || '',
        content: content || '',
      });

      setScore(response.score);
      setRiskLevel(response.risk_level);
      setIssues(response.issues);
      setSafeToSend(response.safe_to_send);
      setDeliverability(response.estimated_deliverability);
    } catch (error) {
      console.error('Spam check failed:', error);
      setIssues([
        {
          type: 'error',
          message: 'Failed to analyze content',
          impact: 'High',
        },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const getScoreColor = () => {
    if (score === 0) return 'text-gray-400';
    if (riskLevel === 'Low') return 'text-green-600';
    if (riskLevel === 'Medium') return 'text-yellow-600';
    if (riskLevel === 'High') return 'text-orange-600';
    return 'text-red-600';
  };

  const getScoreLabel = () => {
    if (score === 0) return 'Not analyzed';
    return riskLevel;
  };

  const getIcon = type => {
    switch (type) {
      case 'error':
        return <AlertCircle className="text-red-500" size={16} />;
      case 'warning':
        return <AlertTriangle className="text-yellow-500" size={16} />;
      default:
        return <CheckCircle2 className="text-blue-500" size={16} />;
    }
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center gap-2">
          <Shield className="text-primary-500" size={20} />
          <CardTitle>Spam Score Checker</CardTitle>
        </div>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {/* Score Display */}
          <div className="text-center p-6 bg-gray-50 dark:bg-white/5 rounded-lg">
            <div className={`text-5xl font-bold mb-2 ${getScoreColor()}`}>
              {loading ? '...' : score}
            </div>
            <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">Spam Score</p>
            <Badge variant={score < 30 ? 'success' : score < 60 ? 'warning' : 'danger'}>
              {getScoreLabel()}
            </Badge>
          </div>

          {/* Issues List */}
          {issues.length > 0 && (
            <div className="space-y-3">
              <h4 className="font-semibold text-gray-900 dark:text-white">Issues Found</h4>
              {issues.map((issue, index) => (
                <div
                  key={index}
                  className="flex items-start gap-3 p-3 rounded-lg bg-gray-50 dark:bg-white/5"
                >
                  {getIcon(issue.type)}
                  <div className="flex-1">
                    <p className="text-sm font-medium text-gray-900 dark:text-white">
                      {issue.message}
                    </p>
                    <p className="text-xs text-gray-600 dark:text-gray-400 mt-1">
                      Impact: {issue.impact}
                    </p>
                    {issue.recommendation && (
                      <p className="text-xs text-blue-600 dark:text-blue-400 mt-1">
                         {issue.recommendation}
                      </p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}

          {issues.length === 0 && !loading && score === 0 && (
            <div className="text-center py-6 text-gray-600 dark:text-gray-400">
              <p className="text-sm">Add subject and content to check spam score</p>
            </div>
          )}

          {issues.length === 0 && score > 0 && (
            <div className="flex items-center justify-center gap-2 text-green-600 dark:text-green-400 py-4">
              <CheckCircle2 size={20} />
              <p className="text-sm font-medium">Excellent! No spam triggers detected.</p>
            </div>
          )}

          {/* Deliverability Score */}
          {score > 0 && (
            <div className="pt-3 border-t border-gray-200 dark:border-white/10">
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600 dark:text-gray-400">
                  Estimated Deliverability:
                </span>
                <span
                  className={`text-sm font-semibold ${deliverability >= 80 ? 'text-green-600' : deliverability >= 60 ? 'text-yellow-600' : 'text-red-600'}`}
                >
                  {deliverability}%
                </span>
              </div>
            </div>
          )}

          {/* Tips */}
          <div className="pt-4 border-t border-gray-200 dark:border-white/10 mt-2">
            <p className="text-xs text-gray-600 dark:text-gray-400">
              <strong>Tip:</strong>{' '}
              {safeToSend ? 'Content looks safe to send!' : 'Review and fix issues before sending'}
            </p>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

SpamChecker.propTypes = {
  subject: PropTypes.string,
  content: PropTypes.string,
};

