import React, { useState, useEffect, useCallback } from 'react';
import { Dialog, DialogContent } from '../ui/Dialog';
import { useNavigate } from 'react-router-dom';
import {
  Search,
  Command,
  Mail,
  Users,
  BarChart3,
  Settings,
