    if (score >= 80) return 'text-green-400';
    if (score >= 50) return 'text-amber-400';
    return 'text-red-400';
  };

  const getBarColor = score => {
    if (score >= 80) return 'bg-green-500';
    if (score >= 50) return 'bg-amber-500';
    return 'bg-red-500';
  };

  return (
    <div className="space-y-6">
      {/* Overall Score */}
      <div className="text-center">
        <div className={`text-5xl font-bold ${getScoreColor(overallScore)}`}>{overallScore}%</div>
        <p className="text-slate-400 mt-1">Enterprise Readiness Score</p>
      </div>

      {/* Category Breakdown */}
      <div className="space-y-4">
        {categories.map(cat => (
          <div key={cat.id} className="space-y-2">
            <div className="flex items-center justify-between">
              <span className="text-sm font-medium text-white">{cat.name}</span>
              <span className={`text-sm font-semibold ${getScoreColor(cat.score)}`}>
                {cat.score}%
              </span>
            </div>
            <div className="h-2 bg-slate-700 rounded-full overflow-hidden">
              <motion.div
                initial={{ width: 0 }}
                animate={{ width: `${cat.score}%` }}
                transition={{ duration: 0.8, ease: 'easeOut' }}
                className={`h-full rounded-full ${getBarColor(cat.score)}`}
              />
            </div>
            <div className="flex flex-wrap gap-2">
              {cat.items.map(item => (
                <a
                  key={item.key}
                  href={item.link}
                  className={`text-xs px-2 py-1 rounded-md transition-colors ${
                    item.done
                      ? 'bg-green-500/10 text-green-400'
