  return (
    <div className="relative group" onMouseEnter={onHover} onMouseLeave={() => {}}>
      {/* Glow effect */}
      <div
        className={`absolute -inset-1 rounded-xl bg-${metric.color}-500/20 blur-lg opacity-0 group-hover:opacity-100 transition-opacity`}
