              <a
                href="#"
                className="flex items-center gap-1 text-sm font-medium text-cyan-400 hover:text-cyan-300 transition-colors"
              >
                View Security Whitepaper
                <ArrowUpRight size={14} />
              </a>
            </div>
          </div>
        </RevealText>
      </div>
    </section>
  );
};

