        </div>

        {/* Flow Visualization */}
        <div className="grid lg:grid-cols-3 gap-6 mb-8">
          {/* Left: Step Navigation */}
          <div className="lg:col-span-1 space-y-3">
            {steps.map((step, idx) => {
              const Icon = step.icon;
              const isActive = activeStep === idx;
              const isPast = activeStep > idx;

              return (
                <button
                  key={step.id}
                  onClick={() => {
                    setActiveStep(idx);
