import React, { useState, useEffect } from 'react';
import { Badge } from '../ui/Badge';
import { Card, CardContent } from '../ui/Card';
import { Check, X, TrendingUp, TrendingDown } from 'lucide-react';
import { useScrollAnimation, useCountUp } from '../../hooks/useScrollAnimation';

/**
 * BeforeAfterCompare Component
 * Side-by-side comparison with animated metrics
 * Mirrors artisan.co's "Before/After" pattern
 */
const BeforeAfterCompare = ({ content }) => {
  const { ref, isVisible } = useScrollAnimation({ threshold: 0.2, triggerOnce: true });
  const [showComparison, setShowComparison] = useState(false);

  useEffect(() => {
    if (isVisible) {
      setTimeout(() => setShowComparison(true), 200);
    }
  }, [isVisible]);

  return (
    <section
      ref={ref}
      className="relative py-24 md:py-32 bg-white dark:bg-gray-900 overflow-hidden"
    >
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-5 dark:opacity-10">
        <div
          className="absolute inset-0"
          style={{
            backgroundImage: `radial-gradient(circle at 2px 2px, currentColor 1px, transparent 0)`,
            backgroundSize: '32px 32px',
          }}
        />
      </div>

      <div className="relative z-10 max-w-7xl mx-auto px-6">
        {/* Header */}
        <div className="text-center mb-16 space-y-4">
          <h2 className="text-4xl md:text-5xl lg:text-6xl font-bold text-primary dark:text-white">
            {content.title}
          </h2>
          <p className="text-xl md:text-2xl text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            {content.subtitle}
          </p>
        </div>

        {/* Comparison Grid */}
        <div className="grid lg:grid-cols-2 gap-8 lg:gap-12">
          {/* Before Column */}
          <ComparisonCard
            data={content.before}
            variant="before"
            isVisible={showComparison}
            delay={0}
          />

          {/* After Column */}
          <ComparisonCard
            data={content.after}
            variant="after"
            isVisible={showComparison}
            delay={0.2}
          />
        </div>

        {/* VS Divider (Desktop) */}
        <div className="hidden lg:flex absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 w-16 h-16 items-center justify-center">
          <div className="w-16 h-16 rounded-full bg-gradient-to-br from-primary to-accent flex items-center justify-center text-white font-bold text-xl shadow-2xl">
            VS
          </div>
        </div>
      </div>
    </section>
  );
};

/**
 * ComparisonCard Component
 */
const ComparisonCard = ({ data, variant, isVisible, delay }) => {
  const isBefore = variant === 'before';

  return (
    <div
      className={`transform transition-all duration-700 ease-out ${
        isVisible ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'
      }`}
      style={{ transitionDelay: `${delay}s` }}
    >
      <Card
        className={`relative overflow-hidden border-2 ${
          isBefore
            ? 'border-red-200 dark:border-red-800 bg-red-50/50 dark:bg-red-900/10'
            : 'border-green-200 dark:border-green-800 bg-green-50/50 dark:bg-green-900/10'
        }`}
      >
        <CardContent className="p-8 space-y-6">
          {/* Header */}
          <div className="space-y-3">
            <Badge
              className={`text-sm font-semibold ${
                isBefore
                  ? 'bg-red-100 text-red-700 dark:bg-red-900/30 dark:text-red-300'
                  : 'bg-green-100 text-green-700 dark:bg-green-900/30 dark:text-green-300'
              }`}
            >
              {data.label}
            </Badge>
            <h3
              className={`text-2xl md:text-3xl font-bold ${
                isBefore ? 'text-red-900 dark:text-red-200' : 'text-green-900 dark:text-green-200'
              }`}
            >
              {isBefore ? data.pain : data.benefit}
            </h3>
          </div>

          {/* Points List */}
          <ul className="space-y-4">
            {data.points.map((point, index) => (
              <li
                key={index}
                className={`flex items-start gap-3 transition-all duration-500 ${
                  isVisible ? 'translate-x-0 opacity-100' : '-translate-x-4 opacity-0'
                }`}
                style={{ transitionDelay: `${delay + 0.1 * (index + 1)}s` }}
              >
                <div
                  className={`mt-1 flex-shrink-0 w-5 h-5 rounded-full flex items-center justify-center ${
                    isBefore ? 'bg-red-100 dark:bg-red-900/30' : 'bg-green-100 dark:bg-green-900/30'
                  }`}
                >
                  {isBefore ? (
                    <X className="w-3 h-3 text-red-600 dark:text-red-400" />
                  ) : (
                    <Check className="w-3 h-3 text-green-600 dark:text-green-400" />
                  )}
                </div>
                <span className="text-gray-700 dark:text-gray-300 leading-relaxed">{point}</span>
              </li>
            ))}
          </ul>

          {/* Metrics */}
          <div className="pt-6 border-t border-gray-200 dark:border-gray-700">
            <div className="grid grid-cols-1 gap-4">
              {data.metrics.map((metric, index) => (
                <MetricDisplay
                  key={index}
                  metric={metric}
                  isNegative={metric.negative}
                  isPositive={metric.positive}
                  isVisible={isVisible}
                  delay={delay + 0.5 + index * 0.1}
                />
              ))}
            </div>
          </div>
        </CardContent>

        {/* Decorative Corner */}
        <div
          className={`absolute top-0 right-0 w-32 h-32 ${
            isBefore ? 'bg-red-500/10' : 'bg-green-500/10'
          } blur-3xl`}
        />
      </Card>
    </div>
  );
};

/**
 * MetricDisplay Component with Animation
 */
const MetricDisplay = ({ metric, isNegative, isPositive, isVisible, delay }) => {
  const { count: animatedValue, animate } = useCountUp(0, 1000, {
    start: 0,
    decimals: 0,
    prefix: '',
    suffix: '',
  });

  useEffect(() => {
    if (isVisible) {
      setTimeout(() => animate(), delay * 1000);
    }
  }, [isVisible, animate, delay]);

  return (
    <div
      className={`flex items-center justify-between p-4 rounded-lg transition-all duration-500 ${
        isNegative
          ? 'bg-red-100/50 dark:bg-red-900/20'
          : isPositive
            ? 'bg-green-100/50 dark:bg-green-900/20'
            : 'bg-gray-100 dark:bg-gray-800'
      } ${isVisible ? 'scale-100 opacity-100' : 'scale-95 opacity-0'}`}
      style={{ transitionDelay: `${delay}s` }}
    >
      <div className="flex items-center gap-3">
        {isNegative && <TrendingDown className="w-5 h-5 text-red-600 dark:text-red-400" />}
        {isPositive && <TrendingUp className="w-5 h-5 text-green-600 dark:text-green-400" />}
        <span className="text-sm font-medium text-gray-700 dark:text-gray-300">{metric.label}</span>
      </div>
      <span
        className={`text-lg font-bold ${
          isNegative
            ? 'text-red-700 dark:text-red-300'
            : isPositive
              ? 'text-green-700 dark:text-green-300'
              : 'text-gray-900 dark:text-gray-100'
        }`}
      >
        {metric.value}
      </span>
    </div>
  );
};

export default BeforeAfterCompare;

import React, { useState } from 'react';
import { Card, CardContent } from '../ui/Card';
import { Badge } from '../ui/Badge';
import {
  MessageSquare,
  Sparkles,
  Database,
  LineChart,
  ShieldCheck,
  Plug,
  ArrowRight,
  Check,
} from 'lucide-react';
import { useScrollAnimation } from '../../hooks/useScrollAnimation';

/**
 * Icon mapping for dynamic icon rendering
 */
const iconMap = {
  MessageSquare,
  Sparkles,
  Database,
  LineChart,
  ShieldCheck,
  Plug,
};

/**
 * FeatureGrid Component
 * Expandable feature tiles with hover interactions
 * Inspired by artisan.co's feature cards
 */
const FeatureGrid = ({ content }) => {
  const { ref, isVisible } = useScrollAnimation({ threshold: 0.1, triggerOnce: true });
  const [expandedFeature, setExpandedFeature] = useState(null);

  return (
    <section
      ref={ref}
      className="relative py-24 md:py-32 bg-gray-50 dark:bg-gray-900 overflow-hidden"
    >
      {/* Background Gradient */}
      <div className="absolute inset-0 bg-gradient-to-b from-transparent via-accent/5 to-transparent pointer-events-none" />

      <div className="relative z-10 max-w-7xl mx-auto px-6">
        {/* Header */}
        <div className="text-center mb-16 space-y-4">
          <h2 className="text-4xl md:text-5xl lg:text-6xl font-bold text-primary dark:text-white">
            {content.title}
          </h2>
          <p className="text-xl md:text-2xl text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            {content.subtitle}
          </p>
        </div>

        {/* Feature Grid */}
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {content.grid.map((feature, index) => (
            <FeatureTile
              key={feature.id}
              feature={feature}
              index={index}
              isExpanded={expandedFeature === feature.id}
              onToggle={() =>
                setExpandedFeature(expandedFeature === feature.id ? null : feature.id)
              }
              isVisible={isVisible}
            />
          ))}
        </div>

        {/* Expanded Feature Detail Modal */}
        {expandedFeature && (
          <ExpandedFeatureModal
            feature={content.grid.find(f => f.id === expandedFeature)}
            onClose={() => setExpandedFeature(null)}
          />
        )}
      </div>
    </section>
  );
};

/**
 * FeatureTile Component
 */
const FeatureTile = ({ feature, index, isExpanded, onToggle, isVisible }) => {
  const [isHovered, setIsHovered] = useState(false);
  const Icon = iconMap[feature.icon] || Sparkles;

  return (
    <Card
      className={`group relative overflow-hidden cursor-pointer transition-all duration-500 border-2 ${
        isHovered
          ? 'border-accent shadow-2xl shadow-accent/20 scale-105 -translate-y-2'
          : 'border-gray-200 dark:border-gray-700 hover:border-accent/50'
      } ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-8 opacity-0'}`}
      style={{ transitionDelay: `${index * 0.1}s` }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={onToggle}
    >
      <CardContent className="p-8 space-y-4">
        {/* Icon */}
        <div
          className={`w-14 h-14 rounded-xl flex items-center justify-center transition-all duration-300 ${
            isHovered
              ? 'bg-accent text-white shadow-lg scale-110'
              : 'bg-accent-100 dark:bg-accent-900/30 text-accent-600 dark:text-accent-400'
          }`}
        >
          <Icon className="w-7 h-7" />
        </div>

        {/* Title */}
        <h3 className="text-xl font-bold text-primary dark:text-white group-hover:text-accent transition-colors">
          {feature.title}
        </h3>

        {/* Preview Text */}
        <p className="text-gray-600 dark:text-gray-300 leading-relaxed">{feature.preview}</p>

        {/* Hover State: Show "Learn More" */}
        <div
          className={`flex items-center gap-2 text-accent font-medium transition-all duration-300 ${
            isHovered ? 'opacity-100 translate-x-0' : 'opacity-0 -translate-x-2'
          }`}
        >
          <span className="text-sm">Learn more</span>
          <ArrowRight className="w-4 h-4" />
        </div>

        {/* Benefit Badge */}
        {isHovered && feature.expanded && (
          <div className="absolute bottom-4 right-4">
            <Badge className="bg-green-100 text-green-700 dark:bg-green-900/30 dark:text-green-300 text-xs">
              {feature.expanded.benefit}
            </Badge>
          </div>
        )}
      </CardContent>

      {/* Animated Background Gradient */}
      <div
        className={`absolute inset-0 bg-gradient-to-br from-accent/0 via-accent/5 to-accent/10 transition-opacity duration-500 pointer-events-none ${
          isHovered ? 'opacity-100' : 'opacity-0'
        }`}
      />

      {/* Corner Accent */}
      <div
        className={`absolute -top-10 -right-10 w-32 h-32 bg-accent/10 rounded-full blur-2xl transition-opacity duration-500 ${
          isHovered ? 'opacity-100' : 'opacity-0'
        }`}
      />
    </Card>
  );
};

/**
 * ExpandedFeatureModal Component
 */
const ExpandedFeatureModal = ({ feature, onClose }) => {
  const Icon = iconMap[feature.icon] || Sparkles;

  return (
    <>
      {/* Backdrop */}
      <div
        className="fixed inset-0 bg-black/60 backdrop-blur-sm z-50 animate-fade-in"
        onClick={onClose}
      />

      {/* Modal */}
      <div className="fixed inset-4 md:inset-auto md:top-1/2 md:left-1/2 md:-translate-x-1/2 md:-translate-y-1/2 md:max-w-3xl md:w-full z-50 animate-scale-in">
        <Card className="bg-white dark:bg-gray-900 shadow-2xl border-2 border-accent overflow-hidden max-h-[90vh] overflow-y-auto">
          <CardContent className="p-8 md:p-12 space-y-8">
            {/* Header */}
            <div className="flex items-start gap-6">
              <div className="w-16 h-16 rounded-xl bg-accent text-white flex items-center justify-center flex-shrink-0">
                <Icon className="w-8 h-8" />
              </div>
              <div className="flex-1">
                <h3 className="text-3xl font-bold text-primary dark:text-white mb-3">
                  {feature.title}
                </h3>
                <p className="text-lg text-gray-600 dark:text-gray-300">
                  {feature.expanded.description}
                </p>
              </div>
              <button
                onClick={onClose}
                className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-200 transition-colors"
              >
                <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              </button>
            </div>

            {/* Capabilities */}
            <div>
              <h4 className="text-lg font-semibold text-primary dark:text-white mb-4">
                Capabilities
              </h4>
              <ul className="grid md:grid-cols-2 gap-3">
                {feature.expanded.capabilities.map((capability, index) => (
                  <li key={index} className="flex items-start gap-3">
                    <div className="w-5 h-5 rounded-full bg-accent-100 dark:bg-accent-900/30 flex items-center justify-center flex-shrink-0 mt-0.5">
                      <Check className="w-3 h-3 text-accent-600 dark:text-accent-400" />
                    </div>
                    <span className="text-gray-700 dark:text-gray-300">{capability}</span>
                  </li>
                ))}
              </ul>
            </div>

            {/* Benefit */}
            <div className="bg-green-50 dark:bg-green-900/20 rounded-xl p-6 border-2 border-green-200 dark:border-green-800">
              <div className="flex items-start gap-4">
                <div className="w-10 h-10 rounded-lg bg-green-500 text-white flex items-center justify-center flex-shrink-0">
                  <Check className="w-6 h-6" />
                </div>
                <div>
                  <h4 className="font-semibold text-green-900 dark:text-green-100 mb-2">
                    Key Benefit
                  </h4>
                  <p className="text-green-700 dark:text-green-300">{feature.expanded.benefit}</p>
                </div>
              </div>
            </div>

            {/* CTA */}
            <div className="flex justify-end">
              <button
                onClick={onClose}
                className="px-6 py-3 bg-accent text-white rounded-lg font-medium hover:bg-accent-600 transition-colors"
              >
                Got it
              </button>
            </div>
          </CardContent>
        </Card>
      </div>
    </>
  );
};

export default FeatureGrid;

import React, { useEffect, useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import { Button } from '../ui/Button';
import { Badge } from '../ui/Badge';
import { ArrowRight, Sparkles, TrendingUp, Users } from 'lucide-react';
import { useScrollProgress } from '../../hooks/useScrollAnimation';

/**
 * HeroScene Component
 * Implements artisan.co-style hero with scroll-driven compression
 * Left: Headline + CTA | Right: Product frame that shrinks on scroll
 */
const HeroScene = ({ content }) => {
  const { ref, progress } = useScrollProgress({ start: 0, end: 0.3 });
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    setIsScrolled(progress > 0.15);
  }, [progress]);

  // Calculate dynamic styles based on scroll progress
  const heroScale = Math.max(0.85, 1 - progress * 0.5);
  const heroOpacity = Math.max(0.7, 1 - progress * 1.5);
  const productFrameScale = Math.max(0.7, 1 - progress * 1);
  const productFrameY = progress * 100;

  return (
    <section
      ref={ref}
      className="relative min-h-screen flex items-center justify-center overflow-hidden bg-gradient-to-br from-primary-50 via-white to-accent-50 dark:from-primary-900 dark:via-gray-900 dark:to-accent-900"
    >
      {/* Background Effects */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div
          className="absolute top-1/4 -left-48 w-96 h-96 bg-accent-300/20 dark:bg-accent-500/10 rounded-full blur-3xl"
          style={{
            transform: `translate3d(${progress * -100}px, ${progress * 50}px, 0)`,
            willChange: 'transform',
          }}
        />
        <div
          className="absolute bottom-1/4 -right-48 w-96 h-96 bg-primary-300/20 dark:bg-primary-500/10 rounded-full blur-3xl"
          style={{
            transform: `translate3d(${progress * 100}px, ${progress * -50}px, 0)`,
            willChange: 'transform',
          }}
        />
      </div>

      {/* Content Container */}
      <div className="relative z-10 w-full max-w-7xl mx-auto px-6 py-20 md:py-32">
        <div className="grid lg:grid-cols-2 gap-12 lg:gap-20 items-center">
          {/* Left Column: Copy + CTA */}
          <div
            className="space-y-8"
            style={{
              opacity: heroOpacity,
              transform: `scale(${heroScale})`,
              transformOrigin: 'left center',
              transition: 'opacity 0.1s ease-out, transform 0.1s ease-out',
              willChange: 'transform, opacity',
            }}
          >
            {/* Badge */}
            <div className="inline-flex">
              <Badge
                variant="secondary"
                className="inline-flex items-center gap-2 px-4 py-2 text-sm font-medium bg-accent-100 dark:bg-accent-900/30 text-accent-700 dark:text-accent-300 border border-accent-300 dark:border-accent-700"
              >
                <Sparkles className="w-4 h-4" />
                {content.badge}
              </Badge>
            </div>

            {/* Headline */}
            <h1 className="text-5xl md:text-6xl lg:text-7xl font-bold tracking-tight text-primary dark:text-white leading-[1.1]">
              {content.headline}
            </h1>

            {/* Subhead */}
            <p className="text-xl md:text-2xl text-gray-600 dark:text-gray-300 leading-relaxed max-w-2xl">
              {content.subhead}
            </p>

            {/* CTAs */}
            <div className="flex flex-col sm:flex-row gap-4 pt-4">
              <Button
                size="lg"
                className="text-lg px-8 py-6 bg-accent hover:bg-accent-600 text-white shadow-lg hover:shadow-xl transition-all duration-200 group"
                asChild
              >
                <Link to="/onboarding">
                  {content.cta.primary}
                  <ArrowRight className="ml-2 w-5 h-5 group-hover:translate-x-1 transition-transform" />
                </Link>
              </Button>
              <Button
                size="lg"
                variant="outline"
                className="text-lg px-8 py-6 border-2 border-primary hover:bg-primary hover:text-white dark:border-accent dark:hover:bg-accent transition-all duration-200"
                asChild
              >
                <Link to="/dashboard">{content.cta.secondary}</Link>
              </Button>
            </div>

            {/* Metrics */}
            <div className="grid grid-cols-3 gap-6 pt-8 border-t border-gray-200 dark:border-gray-700">
              {content.metrics.map((metric, index) => (
                <div key={index} className="text-center sm:text-left">
                  <div className="text-3xl md:text-4xl font-bold text-accent dark:text-accent-400">
                    {metric.value}
                  </div>
                  <div className="text-sm text-gray-600 dark:text-gray-400 mt-1">
                    {metric.label}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Right Column: Product Frame */}
          <div
            className="relative"
            style={{
              transform: `scale(${productFrameScale}) translate3d(0, ${productFrameY}px, 0)`,
              transformOrigin: 'center center',
              transition: 'transform 0.1s ease-out',
              willChange: 'transform',
            }}
          >
            <ProductFrame isScrolled={isScrolled} />
          </div>
        </div>
      </div>

      {/* Scroll Indicator */}
      {!isScrolled && (
        <div className="absolute bottom-8 left-1/2 -translate-x-1/2 flex flex-col items-center gap-2 text-gray-400 dark:text-gray-500 animate-bounce">
          <span className="text-sm font-medium">Scroll to explore</span>
          <div className="w-6 h-10 border-2 border-current rounded-full flex items-start justify-center p-2">
            <div className="w-1 h-3 bg-current rounded-full" />
          </div>
        </div>
      )}
    </section>
  );
};

/**
 * ProductFrame Component
 * Shows live product UI state
 */
const ProductFrame = ({ isScrolled }) => {
  const [activeView, setActiveView] = useState('dashboard');

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveView(prev => {
        const views = ['dashboard', 'campaigns', 'analytics'];
        const currentIndex = views.indexOf(prev);
        return views[(currentIndex + 1) % views.length];
      });
    }, 4000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="relative group">
      {/* Frame Container */}
      <div className="relative bg-white dark:bg-gray-900 rounded-2xl shadow-2xl overflow-hidden border border-gray-200 dark:border-gray-700 transition-all duration-300 group-hover:shadow-3xl">
        {/* Browser Chrome */}
        <div className="flex items-center gap-2 px-4 py-3 bg-gray-100 dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700">
          <div className="flex gap-2">
            <div className="w-3 h-3 rounded-full bg-red-500" />
            <div className="w-3 h-3 rounded-full bg-yellow-500" />
            <div className="w-3 h-3 rounded-full bg-green-500" />
          </div>
          <div className="flex-1 mx-4 bg-white dark:bg-gray-900 rounded px-3 py-1 text-xs text-gray-500 dark:text-gray-400">
            app.artisan.co/{activeView}
          </div>
        </div>

        {/* Product Screenshot/UI */}
        <div className="aspect-[16/10] bg-gradient-to-br from-gray-50 to-gray-100 dark:from-gray-800 dark:to-gray-900 p-6">
          {activeView === 'dashboard' && <DashboardPreview />}
          {activeView === 'campaigns' && <CampaignsPreview />}
          {activeView === 'analytics' && <AnalyticsPreview />}
        </div>

        {/* Glow Effect */}
        <div className="absolute inset-0 bg-gradient-to-tr from-accent-500/0 via-accent-500/5 to-accent-500/10 opacity-0 group-hover:opacity-100 transition-opacity duration-500 pointer-events-none" />
      </div>

      {/* Floating Elements */}
      <FloatingMetric
        icon={TrendingUp}
        label="Response Rate"
        value="+127%"
        className="absolute -top-6 -right-6"
      />
      <FloatingMetric
        icon={Users}
        label="Active Campaigns"
        value="12"
        className="absolute -bottom-6 -left-6"
      />
    </div>
  );
};

/**
 * Mini Preview Components
 */
const DashboardPreview = () => (
  <div className="space-y-4">
    <div className="flex items-center justify-between">
      <div className="h-8 w-48 bg-primary dark:bg-primary-600 rounded" />
      <div className="h-8 w-24 bg-accent dark:bg-accent-600 rounded" />
    </div>
    <div className="grid grid-cols-3 gap-4">
      {[1, 2, 3].map(i => (
        <div key={i} className="bg-white dark:bg-gray-800 rounded-lg p-4 shadow-sm space-y-2">
          <div className="h-4 w-20 bg-gray-300 dark:bg-gray-600 rounded" />
          <div className="h-8 w-16 bg-accent-200 dark:bg-accent-700 rounded" />
        </div>
      ))}
    </div>
    <div className="bg-white dark:bg-gray-800 rounded-lg p-4 shadow-sm h-32" />
  </div>
);

const CampaignsPreview = () => (
  <div className="space-y-3">
    {[1, 2, 3, 4].map(i => (
      <div
        key={i}
        className="bg-white dark:bg-gray-800 rounded-lg p-4 shadow-sm flex items-center gap-4"
      >
        <div className="w-12 h-12 bg-accent-200 dark:bg-accent-700 rounded-lg" />
        <div className="flex-1 space-y-2">
          <div className="h-4 w-3/4 bg-gray-300 dark:bg-gray-600 rounded" />
          <div className="h-3 w-1/2 bg-gray-200 dark:bg-gray-700 rounded" />
        </div>
        <div className="h-8 w-8 bg-primary dark:bg-primary-600 rounded" />
      </div>
    ))}
  </div>
);

const AnalyticsPreview = () => (
  <div className="space-y-4">
    <div className="bg-white dark:bg-gray-800 rounded-lg p-4 shadow-sm h-40 flex items-end gap-2">
      {[40, 65, 45, 80, 60, 90, 70].map((height, i) => (
        <div
          key={i}
          className="flex-1 bg-accent-300 dark:bg-accent-600 rounded-t"
          style={{ height: `${height}%` }}
        />
      ))}
    </div>
    <div className="grid grid-cols-2 gap-4">
      {[1, 2].map(i => (
        <div key={i} className="bg-white dark:bg-gray-800 rounded-lg p-4 shadow-sm space-y-2">
          <div className="h-3 w-16 bg-gray-300 dark:bg-gray-600 rounded" />
          <div className="h-6 w-20 bg-accent-200 dark:bg-accent-700 rounded" />
        </div>
      ))}
    </div>
  </div>
);

/**
 * FloatingMetric Component
 */
const FloatingMetric = ({ icon: Icon, label, value, className = '' }) => (
  <div
    className={`bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4 border border-gray-200 dark:border-gray-700 animate-float ${className}`}
  >
    <div className="flex items-center gap-3">
      <div className="w-10 h-10 bg-accent-100 dark:bg-accent-900/30 rounded-lg flex items-center justify-center">
        <Icon className="w-5 h-5 text-accent-600 dark:text-accent-400" />
      </div>
      <div>
        <div className="text-xs text-gray-500 dark:text-gray-400">{label}</div>
        <div className="text-lg font-bold text-primary dark:text-white">{value}</div>
      </div>
    </div>
  </div>
);

export default HeroScene;

