/**
 * DataTable Component
 * Compound component pattern for flexible, accessible data tables
 */
import React, { createContext, useContext, useState, useCallback } from 'react';
import { cn } from '../../lib/utils';
import { ChevronUp, ChevronDown, Check, Minus } from 'lucide-react';

// ============================================
// Types
// ============================================

type SortDirection = 'asc' | 'desc' | null;

interface DataTableContextValue<T extends { id: string }> {
  data: T[];
  selectedRows: Set<string>;
  toggleRow: (id: string) => void;
  toggleAll: () => void;
  isAllSelected: boolean;
  isPartiallySelected: boolean;
  sortColumn: string | null;
  sortDirection: SortDirection;
  setSort: (column: string) => void;
  getRowById: (id: string) => T | undefined;
}

const DataTableContext = createContext<DataTableContextValue<any> | null>(null);

function useDataTable<T extends { id: string }>() {
  const context = useContext(DataTableContext) as DataTableContextValue<T> | null;
  if (!context) {
    throw new Error('DataTable components must be used within DataTable');
  }
  return context;
}

// ============================================
// Root Component
// ============================================

interface DataTableProps<T extends { id: string }> {
  data: T[];
  children: React.ReactNode;
  className?: string;
  onSelectionChange?: (selectedIds: string[]) => void;
  defaultSortColumn?: string | null;
  defaultSortDirection?: SortDirection;
}

export function DataTable<T extends { id: string }>({
  data,
  children,
  className,
  onSelectionChange,
  defaultSortColumn = null,
  defaultSortDirection = null,
}: DataTableProps<T>) {
  const [selectedRows, setSelectedRows] = useState<Set<string>>(new Set());
  const [sortColumn, setSortColumn] = useState<string | null>(defaultSortColumn ?? null);
  const [sortDirection, setSortDirection] = useState<SortDirection>(defaultSortDirection ?? null);

  const toggleRow = useCallback(
    (id: string) => {
      setSelectedRows(prev => {
        const next = new Set(prev);
        if (next.has(id)) {
          next.delete(id);
        } else {
          next.add(id);
        }
        onSelectionChange?.(Array.from(next));
        return next;
      });
    },
    [onSelectionChange]
  );

  const toggleAll = useCallback(() => {
    setSelectedRows(prev => {
      const next = prev.size === data.length ? new Set<string>() : new Set(data.map(d => d.id));
      onSelectionChange?.(Array.from(next));
      return next;
    });
  }, [data, onSelectionChange]);

  const setSort = useCallback((column: string) => {
    setSortColumn(prevColumn => {
      if (prevColumn === column) {
        setSortDirection(prevDir => {
          if (prevDir === 'asc') return 'desc';
          if (prevDir === 'desc') return null;
          return 'asc';
        });
        return column;
      }
      setSortDirection('asc');
      return column;
    });
  }, []);

  const getRowById = useCallback((id: string) => data.find(d => d.id === id), [data]);

  const isAllSelected = data.length > 0 && selectedRows.size === data.length;
  const isPartiallySelected = selectedRows.size > 0 && selectedRows.size < data.length;

  const value: DataTableContextValue<T> = {
    data,
    selectedRows,
    toggleRow,
    toggleAll,
    isAllSelected,
    isPartiallySelected,
    sortColumn,
    sortDirection,
    setSort,
    getRowById,
  };

  return (
    <DataTableContext.Provider value={value}>
      <div className={cn('w-full overflow-auto', className)}>
        <table className="w-full caption-bottom text-sm">{children}</table>
      </div>
    </DataTableContext.Provider>
  );
}

// ============================================
// Header Component
// ============================================

interface DataTableHeaderProps {
  children: React.ReactNode;
  className?: string;
}

DataTable.Header = function DataTableHeader({ children, className }: DataTableHeaderProps) {
  return (
    <thead className={cn('[&_tr]:border-b', className)}>
      <tr className="border-b transition-colors hover:bg-muted/50">{children}</tr>
    </thead>
  );
};

// ============================================
// Header Cell Component
// ============================================

interface DataTableHeaderCellProps {
  children: React.ReactNode;
  className?: string;
  sortKey?: string;
  align?: 'left' | 'center' | 'right';
}

DataTable.HeaderCell = function DataTableHeaderCell({
  children,
  className,
  sortKey,
  align = 'left',
}: DataTableHeaderCellProps) {
  const { sortColumn, sortDirection, setSort } = useDataTable();

  const isSorted = sortKey && sortColumn === sortKey;
  const canSort = !!sortKey;

  const alignClass = {
    left: 'text-left',
    center: 'text-center',
    right: 'text-right',
  }[align];

  return (
    <th
      className={cn(
        'h-12 px-4 align-middle font-medium text-muted-foreground',
        alignClass,
        canSort && 'cursor-pointer select-none hover:text-foreground',
        className
      )}
      onClick={() => canSort && setSort(sortKey)}
    >
      <div className={cn('flex items-center gap-1', align === 'right' && 'justify-end')}>
        {children}
        {canSort && (
          <span className="ml-1">
            {isSorted ? (
              sortDirection === 'asc' ? (
                <ChevronUp className="h-4 w-4" />
              ) : sortDirection === 'desc' ? (
                <ChevronDown className="h-4 w-4" />
              ) : (
                <span className="h-4 w-4" />
              )
            ) : (
              <span className="h-4 w-4 opacity-0 group-hover:opacity-50">
                <ChevronUp className="h-4 w-4" />
              </span>
            )}
          </span>
        )}
      </div>
    </th>
  );
};

// ============================================
// Select All Checkbox
// ============================================

DataTable.SelectAll = function DataTableSelectAll({ className }: { className?: string }) {
  const { isAllSelected, isPartiallySelected, toggleAll } = useDataTable();

  return (
    <th className={cn('h-12 w-12 px-4 align-middle', className)}>
      <button
        type="button"
        role="checkbox"
        aria-checked={isPartiallySelected ? 'mixed' : isAllSelected}
        onClick={toggleAll}
        className={cn(
          'flex h-4 w-4 items-center justify-center rounded border',
          'focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2',
          isAllSelected || isPartiallySelected
            ? 'border-primary bg-primary text-primary-foreground'
            : 'border-input'
        )}
      >
        {isAllSelected && <Check className="h-3 w-3" />}
        {isPartiallySelected && <Minus className="h-3 w-3" />}
      </button>
    </th>
  );
};

// ============================================
// Body Component
// ============================================

interface DataTableBodyProps<T extends { id: string }> {
  children: (item: T, index: number) => React.ReactNode;
  className?: string;
  emptyState?: React.ReactNode;
}

DataTable.Body = function DataTableBody<T extends { id: string }>({
  children,
  className,
  emptyState,
}: DataTableBodyProps<T>) {
  const { data } = useDataTable<T>();

  if (data.length === 0 && emptyState) {
    return (
      <tbody className={className}>
        <tr>
          <td colSpan={100} className="h-24 text-center">
            {emptyState}
          </td>
        </tr>
      </tbody>
    );
  }

  return (
    <tbody className={cn('[&_tr:last-child]:border-0', className)}>{data.map(children)}</tbody>
  );
};

// ============================================
// Row Component
// ============================================

interface DataTableRowProps {
  id: string;
  children: React.ReactNode;
  className?: string;
  onClick?: () => void;
}

DataTable.Row = function DataTableRow({ id, children, className, onClick }: DataTableRowProps) {
  const { selectedRows } = useDataTable();
  const isSelected = selectedRows.has(id);

  return (
    <tr
      className={cn(
        'border-b transition-colors hover:bg-muted/50',
        isSelected && 'bg-muted',
        onClick && 'cursor-pointer',
        className
      )}
      onClick={onClick}
      data-state={isSelected ? 'selected' : undefined}
    >
      {children}
    </tr>
  );
};

// ============================================
// Cell Component
// ============================================

interface DataTableCellProps {
  children: React.ReactNode;
  className?: string;
  align?: 'left' | 'center' | 'right';
}

DataTable.Cell = function DataTableCell({
  children,
  className,
  align = 'left',
}: DataTableCellProps) {
  const alignClass = {
    left: 'text-left',
    center: 'text-center',
    right: 'text-right',
  }[align];

  return <td className={cn('p-4 align-middle', alignClass, className)}>{children}</td>;
};

// ============================================
// Row Checkbox
// ============================================

DataTable.RowCheckbox = function DataTableRowCheckbox({
  id,
  className,
}: {
  id: string;
  className?: string;
}) {
  const { selectedRows, toggleRow } = useDataTable();
  const isSelected = selectedRows.has(id);

  return (
    <td className={cn('w-12 px-4 align-middle', className)}>
      <button
        type="button"
        role="checkbox"
        aria-checked={isSelected}
        onClick={e => {
          e.stopPropagation();
          toggleRow(id);
        }}
        className={cn(
          'flex h-4 w-4 items-center justify-center rounded border',
          'focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2',
          isSelected ? 'border-primary bg-primary text-primary-foreground' : 'border-input'
        )}
      >
        {isSelected && <Check className="h-3 w-3" />}
      </button>
    </td>
  );
};

// ============================================
// Pagination Component
// ============================================

interface DataTablePaginationProps {
  page: number;
  pageSize: number;
  total: number;
  onPageChange: (page: number) => void;
  onPageSizeChange?: (pageSize: number) => void;
  pageSizeOptions?: number[];
  className?: string;
}

DataTable.Pagination = function DataTablePagination({
  page,
  pageSize,
  total,
  onPageChange,
  onPageSizeChange,
  pageSizeOptions = [10, 20, 50, 100],
  className,
}: DataTablePaginationProps) {
  const { selectedRows } = useDataTable();
  const totalPages = Math.ceil(total / pageSize);
  const start = (page - 1) * pageSize + 1;
  const end = Math.min(page * pageSize, total);

  return (
    <div className={cn('flex items-center justify-between px-2 py-4', className)}>
      <div className="flex-1 text-sm text-muted-foreground">
        {selectedRows.size > 0 ? (
          <span>
            {selectedRows.size} of {total} row(s) selected
          </span>
        ) : (
          <span>
            Showing {start} to {end} of {total} results
          </span>
        )}
      </div>

      <div className="flex items-center gap-6">
        {onPageSizeChange && (
          <div className="flex items-center gap-2">
            <span className="text-sm text-muted-foreground">Rows per page</span>
            <select
              value={pageSize}
              onChange={e => onPageSizeChange(Number(e.target.value))}
              className="h-8 rounded-md border border-input bg-background px-2 text-sm"
            >
              {pageSizeOptions.map(size => (
                <option key={size} value={size}>
                  {size}
                </option>
              ))}
            </select>
          </div>
        )}

        <div className="flex items-center gap-1">
          <button
            onClick={() => onPageChange(1)}
            disabled={page === 1}
            className="h-8 w-8 rounded border border-input disabled:opacity-50"
          >

          </button>
          <button
            onClick={() => onPageChange(page - 1)}
            disabled={page === 1}
            className="h-8 w-8 rounded border border-input disabled:opacity-50"
          >

          </button>
          <span className="px-3 text-sm">
            Page {page} of {totalPages}
          </span>
          <button
            onClick={() => onPageChange(page + 1)}
            disabled={page === totalPages}
            className="h-8 w-8 rounded border border-input disabled:opacity-50"
          >

          </button>
          <button
            onClick={() => onPageChange(totalPages)}
            disabled={page === totalPages}
            className="h-8 w-8 rounded border border-input disabled:opacity-50"
          >

          </button>
        </div>
      </div>
    </div>
  );
};

export default DataTable;

import React, { useState, useMemo } from 'react';
import { cn } from '../../lib/utils';
import {
  ChevronDown,
  ChevronUp,
  ChevronLeft,
  ChevronRight,
  Search,
  Filter,
  Download,
  MoreVertical,
  ArrowUpDown,
  Eye,
  Edit,
  Trash2,
} from 'lucide-react';

/**
 * Responsive Data Table with mobile card view
 */

export const DataTable = ({
  columns = [],
  data = [],
  searchable = true,
  filterable = false,
  exportable = false,
  pagination = true,
  pageSize = 10,
  mobileCardView = true,
  onRowClick,
  actions,
  className,
}) => {
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });
  const [selectedRows, setSelectedRows] = useState([]);

  // Filter and search data
  const filteredData = useMemo(() => {
    if (!searchTerm) return data;
    return data.filter(row =>
      columns.some(col =>
        String(row[col.accessor]).toLowerCase().includes(searchTerm.toLowerCase())
      )
    );
  }, [data, searchTerm, columns]);

  // Sort data
  const sortedData = useMemo(() => {
    if (!sortConfig.key) return filteredData;

    return [...filteredData].sort((a, b) => {
      const aVal = a[sortConfig.key];
      const bVal = b[sortConfig.key];

      if (aVal < bVal) return sortConfig.direction === 'asc' ? -1 : 1;
      if (aVal > bVal) return sortConfig.direction === 'asc' ? 1 : -1;
      return 0;
    });
  }, [filteredData, sortConfig]);

  // Paginate data
  const totalPages = Math.ceil(sortedData.length / pageSize);
  const paginatedData = pagination
    ? sortedData.slice((currentPage - 1) * pageSize, currentPage * pageSize)
    : sortedData;

  const handleSort = key => {
    setSortConfig(prev => ({
      key,
      direction: prev.key === key && prev.direction === 'asc' ? 'desc' : 'asc',
    }));
  };

  const handleExport = () => {
    const csv = [
      columns.map(col => col.label).join(','),
      ...sortedData.map(row => columns.map(col => row[col.accessor]).join(',')),
    ].join('\n');

    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'data-export.csv';
    a.click();
  };

  return (
    <div className={cn('w-full', className)}>
      {/* Toolbar */}
      <div className="flex flex-col sm:flex-row gap-4 mb-4">
        {/* Search */}
        {searchable && (
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
            <input
              type="text"
              placeholder="Search..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
              className={cn(
                'w-full pl-10 pr-4 py-2',
                'border border-gray-300 dark:border-gray-600',
                'rounded-lg',
                'bg-white dark:bg-gray-800',
                'text-gray-900 dark:text-white',
                'focus:ring-2 focus:ring-blue-500 focus:border-transparent',
                'placeholder:text-gray-400'
              )}
            />
          </div>
        )}

        {/* Actions */}
        <div className="flex gap-2">
          {filterable && (
            <button className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700 flex items-center gap-2">
              <Filter size={18} />
              <span className="hidden sm:inline">Filter</span>
            </button>
          )}
          {exportable && (
            <button
              onClick={handleExport}
              className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700 flex items-center gap-2"
            >
              <Download size={18} />
              <span className="hidden sm:inline">Export</span>
            </button>
          )}
        </div>
      </div>

      {/* Desktop Table View */}
      <div className="hidden md:block overflow-x-auto border border-gray-200 dark:border-gray-700 rounded-lg">
        <table className="w-full">
          <thead className="bg-gray-50 dark:bg-gray-800">
            <tr>
              {columns.map((column, index) => (
                <th
                  key={index}
                  onClick={() => column.sortable && handleSort(column.accessor)}
                  className={cn(
                    'px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider',
                    column.sortable && 'cursor-pointer hover:bg-gray-100 dark:hover:bg-gray-700'
                  )}
                >
                  <div className="flex items-center gap-2">
                    {column.label}
                    {column.sortable && (
                      <ArrowUpDown
                        size={14}
                        className={cn(sortConfig.key === column.accessor && 'text-blue-500')}
                      />
                    )}
                  </div>
                </th>
              ))}
              {actions && <th className="px-6 py-3"></th>}
            </tr>
          </thead>
          <tbody className="bg-white dark:bg-gray-900 divide-y divide-gray-200 dark:divide-gray-700">
            {paginatedData.map((row, rowIndex) => (
              <tr
                key={rowIndex}
                onClick={() => onRowClick?.(row)}
                className={cn(
                  'hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors',
                  onRowClick && 'cursor-pointer'
                )}
              >
                {columns.map((column, colIndex) => (
                  <td
                    key={colIndex}
                    className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-gray-100"
                  >
                    {column.render
                      ? column.render(row[column.accessor], row)
                      : row[column.accessor]}
                  </td>
                ))}
                {actions && (
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm">
                    <div className="flex items-center justify-end gap-2">{actions(row)}</div>
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Mobile Card View */}
      {mobileCardView && (
        <div className="md:hidden space-y-4">
          {paginatedData.map((row, index) => (
            <div
              key={index}
              onClick={() => onRowClick?.(row)}
              className={cn(
                'bg-white dark:bg-gray-800',
                'border border-gray-200 dark:border-gray-700',
                'rounded-lg p-4',
                'space-y-3',
                onRowClick && 'cursor-pointer active:scale-98'
              )}
            >
              {columns.map((column, colIndex) => (
                <div key={colIndex} className="flex justify-between items-start">
                  <span className="text-xs font-medium text-gray-500 dark:text-gray-400">
                    {column.label}
                  </span>
                  <span className="text-sm text-gray-900 dark:text-white text-right">
                    {column.render
                      ? column.render(row[column.accessor], row)
                      : row[column.accessor]}
                  </span>
                </div>
              ))}
              {actions && (
                <div className="flex gap-2 pt-2 border-t border-gray-200 dark:border-gray-700">
                  {actions(row)}
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      {/* Pagination */}
      {pagination && totalPages > 1 && (
        <div className="mt-4 flex flex-col sm:flex-row items-center justify-between gap-4">
          <div className="text-sm text-gray-600 dark:text-gray-400">
            Showing {(currentPage - 1) * pageSize + 1} to{' '}
            {Math.min(currentPage * pageSize, sortedData.length)} of {sortedData.length} results
          </div>

          <div className="flex items-center gap-2">
            <button
              onClick={() => setCurrentPage(p => Math.max(1, p - 1))}
              disabled={currentPage === 1}
              className={cn(
                'p-2 rounded-lg border',
                currentPage === 1
                  ? 'border-gray-200 dark:border-gray-700 text-gray-400 cursor-not-allowed'
                  : 'border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-700'
              )}
            >
              <ChevronLeft size={18} />
            </button>

            {/* Page Numbers */}
            <div className="hidden sm:flex gap-1">
              {Array.from({ length: totalPages }, (_, i) => i + 1)
                .filter(page => {
                  if (totalPages <= 7) return true;
                  if (page === 1 || page === totalPages) return true;
                  if (page >= currentPage - 1 && page <= currentPage + 1) return true;
                  return false;
                })
                .map((page, index, array) => (
                  <React.Fragment key={page}>
                    {index > 0 && array[index - 1] !== page - 1 && (
                      <span className="px-2 py-1">...</span>
                    )}
                    <button
                      onClick={() => setCurrentPage(page)}
                      className={cn(
                        'px-3 py-1 rounded-lg',
                        page === currentPage
                          ? 'bg-blue-600 text-white'
                          : 'border border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-700'
                      )}
                    >
                      {page}
                    </button>
                  </React.Fragment>
                ))}
            </div>

            {/* Mobile page indicator */}
            <div className="sm:hidden text-sm text-gray-600 dark:text-gray-400">
              {currentPage} / {totalPages}
            </div>

            <button
              onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))}
              disabled={currentPage === totalPages}
              className={cn(
                'p-2 rounded-lg border',
                currentPage === totalPages
                  ? 'border-gray-200 dark:border-gray-700 text-gray-400 cursor-not-allowed'
                  : 'border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-700'
              )}
            >
              <ChevronRight size={18} />
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

// Quick Action Buttons for Table Rows
export const TableActions = ({ onView, onEdit, onDelete }) => {
  return (
    <div className="flex items-center gap-1">
      {onView && (
        <button
          onClick={e => {
            e.stopPropagation();
            onView();
          }}
