// Video Player
export const VideoEmbed = ({
  src,
  poster,
  type = 'video/mp4',
  title,
  controls = true,
  autoplay = false,
  className,
}) => {
  const [isPlaying, setIsPlaying] = useState(autoplay);
  const [isMuted, setIsMuted] = useState(autoplay); // Autoplay videos should be muted
  const videoRef = useRef(null);

  const togglePlay = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const toggleMute = () => {
    if (videoRef.current) {
      videoRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  return (
    <div className={cn('relative group', className)}>
      <div className="relative aspect-video bg-black rounded-xl overflow-hidden">
        <video
          ref={videoRef}
          src={src}
          poster={poster}
          type={type}
          autoPlay={autoplay}
          muted={isMuted}
          playsInline
          className="w-full h-full"
          onPlay={() => setIsPlaying(true)}
          onPause={() => setIsPlaying(false)}
        >
          <track kind="captions" srcLang="en" label="English" />
          Your browser does not support the video tag.
        </video>

        {/* Custom Controls Overlay */}
        {!controls && (
          <div className="absolute inset-0 flex items-center justify-center bg-black/30 opacity-0 group-hover:opacity-100 transition-opacity">
            <button
              onClick={togglePlay}
              className="p-4 bg-white/20 hover:bg-white/30 backdrop-blur-sm rounded-full transition-colors"
            >
              {isPlaying ? (
                <Pause size={32} className="text-white" />
              ) : (
                <Play size={32} className="text-white ml-1" />
              )}
            </button>
          </div>
        )}

        {/* Volume Control */}
        <button
          onClick={toggleMute}
          className="absolute bottom-4 right-4 p-2 bg-black/50 hover:bg-black/70 rounded-lg transition-colors"
        >
          {isMuted ? (
            <VolumeX size={20} className="text-white" />
          ) : (
            <Volume2 size={20} className="text-white" />
          )}
        </button>

        {/* Title Overlay */}
        {title && (
          <div className="absolute top-0 left-0 right-0 bg-gradient-to-b from-black/60 to-transparent p-4">
            <h3 className="text-white text-lg font-semibold">{title}</h3>
          </div>
        )}
      </div>

      {/* Native Controls */}
      {controls && (
        <video controls src={src} poster={poster} className="w-full aspect-video rounded-xl">
          <track kind="captions" srcLang="en" label="English" />
          Your browser does not support the video tag.
        </video>
      )}
    </div>
  );
};

