// Mega Menu - Responsive dropdown with categories
export const MegaMenu = ({ trigger, categories = [], className }) => {
  const [isOpen, setIsOpen] = useState(false);
  const menuRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = event => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    if (isOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [isOpen]);

  return (
    <div className="relative" ref={menuRef}>
      <button
        onClick={() => setIsOpen(!isOpen)}
        className={cn(
          'flex items-center gap-2 px-4 py-2 rounded-lg',
          'text-sm font-medium',
          'hover:bg-gray-100 dark:hover:bg-gray-700',
          'transition-colors'
        )}
      >
        {trigger}
        <ChevronDown size={16} className={cn('transition-transform', isOpen && 'rotate-180')} />
      </button>

      {isOpen && (
        <div
          className={cn(
            'absolute top-full left-0 mt-2',
            'w-screen max-w-4xl',
            'bg-white dark:bg-gray-800',
            'border border-gray-200 dark:border-gray-700',
            'rounded-xl shadow-2xl',
            'p-6',
            'grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6',
            className
          )}
        >
          {categories.map((category, index) => (
            <div key={index}>
              <h3 className="text-sm font-semibold text-gray-900 dark:text-white mb-3">
                {category.title}
              </h3>
              <div className="space-y-2">
                {category.items.map((item, itemIndex) => {
                  const Icon = item.icon;
                  return (
                    <Link
                      key={itemIndex}
                      to={item.path || '#'}
                      onClick={() => setIsOpen(false)}
                      className="flex items-center gap-3 p-2 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700 transition-colors"
                    >
                      {Icon && <Icon size={18} className="text-gray-400" />}
                      <div>
                        <div className="text-sm font-medium text-gray-900 dark:text-white">
                          {item.label}
                        </div>
                        {item.description && (
                          <div className="text-xs text-gray-600 dark:text-gray-400">
                            {item.description}
                          </div>
                        )}
                      </div>
                    </Link>
                  );
                })}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// Collapsible Menu - Accordion style with nested items
export const CollapsibleMenu = ({ items = [], className }) => {
  const [openItems, setOpenItems] = useState([]);
  const location = useLocation();

  const toggleItem = index => {
    setOpenItems(prev => (prev.includes(index) ? prev.filter(i => i !== index) : [...prev, index]));
  };

  return (
    <div className={cn('space-y-1', className)}>
      {items.map((item, index) => {
        const Icon = item.icon;
        const isOpen = openItems.includes(index);
        const hasChildren = item.children && item.children.length > 0;

        return (
          <div key={index}>
            <button
              onClick={() => hasChildren && toggleItem(index)}
              className={cn(
                'w-full flex items-center justify-between gap-3',
                'px-3 py-2.5 rounded-lg',
                'text-sm font-medium transition-all',
                'hover:bg-gray-100 dark:hover:bg-gray-700',
                location.pathname === item.path &&
                  'bg-blue-50 dark:bg-blue-900/30 text-blue-600 dark:text-blue-400',
                !hasChildren &&
                  location.pathname !== item.path &&
                  'text-gray-700 dark:text-gray-300'
              )}
            >
              <div className="flex items-center gap-3">
                {Icon && <Icon size={18} />}
                <span>{item.label}</span>
              </div>
              {hasChildren && (
                <ChevronDown
                  size={16}
                  className={cn('transition-transform', isOpen && 'rotate-180')}
                />
              )}
            </button>

            {hasChildren && isOpen && (
              <div className="ml-6 mt-1 space-y-1 border-l-2 border-gray-200 dark:border-gray-700 pl-3">
                {item.children.map((child, childIndex) => {
                  const ChildIcon = child.icon;
                  return (
                    <Link
                      key={childIndex}
                      to={child.path || '#'}
                      className={cn(
                        'flex items-center gap-2 px-3 py-2 rounded-lg',
                        'text-sm transition-all',
                        'hover:bg-gray-100 dark:hover:bg-gray-700',
                        location.pathname === child.path
                          ? 'text-blue-600 dark:text-blue-400 font-medium'
                          : 'text-gray-600 dark:text-gray-400'
                      )}
                    >
                      {ChildIcon && <ChildIcon size={16} />}
                      <span>{child.label}</span>
                    </Link>
                  );
                })}
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
};

