const AIWorkflowAssistant = ({ isOpen, onClose, onGenerateWorkflow }) => {
  const [prompt, setPrompt] = useState('');
  const [isGenerating, setIsGenerating] = useState(false);
  const [messages, setMessages] = useState([
    {
      role: 'assistant',
      content:
        'Hi! I\'m Ava, your AI workflow assistant. Describe the campaign you want to create, and I\'ll build it for you. For example: "Create a cold outreach campaign with LinkedIn and email follow-ups."',
    },
  ]);
  const [isExpanded, setIsExpanded] = useState(true);
  const [copied, setCopied] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleGenerate = async () => {
    if (!prompt.trim()) return;

    // Add user message
    const userMessage = { role: 'user', content: prompt };
    setMessages(prev => [...prev, userMessage]);
    setPrompt('');
    setIsGenerating(true);

    // Simulate AI thinking
    await new Promise(resolve => setTimeout(resolve, 1500));

    // Parse intent
    const { template, confidence } = parseIntent(prompt);
    const workflow = workflowTemplates[template];

    // Add assistant response
    const assistantMessage = {
      role: 'assistant',
      content: `I've created a "${workflow.name}" workflow for you! This campaign includes ${workflow.nodes.length} steps with ${workflow.edges.length} connections. The workflow is optimized for your use case with proper timing and sequencing.\n\nClick "Apply Workflow" to add it to your canvas.`,
      workflow: workflow,
      confidence: confidence,
    };

    setMessages(prev => [...prev, assistantMessage]);
    setIsGenerating(false);
  };

  const handleApplyWorkflow = workflow => {
    onGenerateWorkflow(workflow);
    setMessages(prev => [
      ...prev,
      {
        role: 'assistant',
        content:
          "Great! I've added the workflow to your canvas. You can now customize each node by clicking on it, or ask me to modify the workflow.",
      },
    ]);
  };

  const handleSuggestedPrompt = suggestedPrompt => {
    setPrompt(suggestedPrompt);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed bottom-4 right-4 z-50 flex flex-col">
      {/* Chat Window */}
      <div
        className={`bg-white dark:bg-gray-800 rounded-2xl shadow-2xl border border-gray-200 dark:border-gray-700 overflow-hidden transition-all duration-300 ${isExpanded ? 'w-96 h-[500px]' : 'w-96 h-16'}`}
      >
        {/* Header */}
        <div
          className="flex items-center justify-between px-4 py-3 bg-gradient-to-r from-purple-600 to-indigo-600 text-white cursor-pointer"
          onClick={() => setIsExpanded(!isExpanded)}
        >
          <div className="flex items-center gap-2">
            <div className="w-8 h-8 rounded-full bg-white/20 flex items-center justify-center">
              <Sparkles className="w-5 h-5" />
            </div>
            <div>
              <h3 className="font-semibold text-sm">Ava - AI Assistant</h3>
              <p className="text-xs text-white/70">Build workflows with AI</p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            {isExpanded ? <ChevronDown className="w-5 h-5" /> : <ChevronUp className="w-5 h-5" />}
            <button
              onClick={e => {
                e.stopPropagation();
                onClose();
              }}
              className="p-1 hover:bg-white/20 rounded-lg"
            >
              <X className="w-4 h-4" />
            </button>
          </div>
        </div>

        {isExpanded && (
          <>
            {/* Messages */}
            <div className="flex-1 overflow-y-auto p-4 space-y-4 h-[340px]">
              {messages.map((message, index) => (
                <div
                  key={index}
                  className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
                >
                  <div className={`max-w-[85%] ${message.role === 'user' ? 'order-2' : 'order-1'}`}>
                    {/* Avatar */}
                    <div
                      className={`flex items-start gap-2 ${message.role === 'user' ? 'flex-row-reverse' : ''}`}
                    >
                      <div
                        className={`w-7 h-7 rounded-full flex items-center justify-center flex-shrink-0 ${
                          message.role === 'user'
                            ? 'bg-indigo-100 dark:bg-indigo-900'
                            : 'bg-purple-100 dark:bg-purple-900'
                        }`}
                      >
                        {message.role === 'user' ? (
                          <User className="w-4 h-4 text-indigo-600 dark:text-indigo-400" />
                        ) : (
                          <Bot className="w-4 h-4 text-purple-600 dark:text-purple-400" />
                        )}
                      </div>

                      <div
                        className={`rounded-2xl px-4 py-2 ${
                          message.role === 'user'
                            ? 'bg-indigo-600 text-white'
                            : 'bg-gray-100 dark:bg-gray-700 text-gray-900 dark:text-white'
                        }`}
                      >
                        <p className="text-sm whitespace-pre-line">{message.content}</p>

                        {/* Workflow Preview */}
                        {message.workflow && (
                          <div className="mt-3 p-3 bg-white dark:bg-gray-800 rounded-xl border border-gray-200 dark:border-gray-600">
                            <div className="flex items-center justify-between mb-2">
                              <span className="font-medium text-sm text-gray-900 dark:text-white">
                                {message.workflow.name}
                              </span>
                              <span className="text-xs px-2 py-0.5 bg-green-100 text-green-700 rounded-full">
                                {Math.round(message.confidence * 100)}% match
                              </span>
                            </div>
                            <div className="flex items-center gap-4 text-xs text-gray-500 dark:text-gray-400 mb-3">
                              <span>{message.workflow.nodes.length} nodes</span>
                              <span>{message.workflow.edges.length} connections</span>
                            </div>
                            <button
                              onClick={() => handleApplyWorkflow(message.workflow)}
                              className="w-full flex items-center justify-center gap-2 px-4 py-2 bg-gradient-to-r from-purple-600 to-indigo-600 text-white rounded-lg text-sm font-medium hover:from-purple-700 hover:to-indigo-700 transition-all"
                            >
                              <Wand2 className="w-4 h-4" />
                              Apply Workflow
                            </button>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              ))}

              {isGenerating && (
                <div className="flex justify-start">
                  <div className="flex items-start gap-2">
                    <div className="w-7 h-7 rounded-full bg-purple-100 dark:bg-purple-900 flex items-center justify-center">
                      <Bot className="w-4 h-4 text-purple-600 dark:text-purple-400" />
                    </div>
                    <div className="bg-gray-100 dark:bg-gray-700 rounded-2xl px-4 py-3">
                      <div className="flex items-center gap-2">
                        <Loader2 className="w-4 h-4 animate-spin text-purple-600" />
                        <span className="text-sm text-gray-600 dark:text-gray-300">
                          Creating your workflow...
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              )}

              <div ref={messagesEndRef} />
            </div>

            {/* Suggested Prompts */}
            {messages.length === 1 && (
              <div className="px-4 pb-2">
                <p className="text-xs text-gray-500 dark:text-gray-400 mb-2">Try these:</p>
                <div className="flex flex-wrap gap-1">
                  {suggestedPrompts.slice(0, 3).map((sp, i) => (
                    <button
                      key={i}
                      onClick={() => handleSuggestedPrompt(sp)}
                      className="text-xs px-2 py-1 bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded-full hover:bg-gray-200 dark:hover:bg-gray-600 transition-colors truncate max-w-[150px]"
                    >
                      {sp.length > 25 ? sp.slice(0, 25) + '...' : sp}
                    </button>
                  ))}
                </div>
              </div>
            )}

            {/* Input */}
            <div className="p-4 border-t border-gray-200 dark:border-gray-700">
              <div className="flex items-center gap-2">
                <input
                  type="text"
                  value={prompt}
                  onChange={e => setPrompt(e.target.value)}
                  onKeyDown={e => e.key === 'Enter' && !e.shiftKey && handleGenerate()}
                  placeholder="Describe your campaign..."
                  className="flex-1 px-4 py-2 bg-gray-100 dark:bg-gray-700 border-0 rounded-full text-sm focus:ring-2 focus:ring-purple-500 text-gray-900 dark:text-white placeholder-gray-500"
                  disabled={isGenerating}
                />
                <button
                  onClick={handleGenerate}
                  disabled={!prompt.trim() || isGenerating}
                  className="p-2 bg-gradient-to-r from-purple-600 to-indigo-600 text-white rounded-full disabled:opacity-50 hover:from-purple-700 hover:to-indigo-700 transition-all"
                >
                  <Send className="w-4 h-4" />
                </button>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

AIWorkflowAssistant.propTypes = {
  isOpen: PropTypes.bool.isRequired,
  onClose: PropTypes.func.isRequired,
  onGenerateWorkflow: PropTypes.func.isRequired,
};

export default AIWorkflowAssistant;

import React from 'react';
import PropTypes from 'prop-types';
import {
  CheckCircle,
  Loader2,
  AlertCircle,
  Clock,
  Users,
  TrendingUp,
  Activity,
} from 'lucide-react';

const ExecutionOverlay = ({ status, nodes }) => {
  const completedCount = Object.values(status).filter(s => s === 'completed').length;
  const runningCount = Object.values(status).filter(s => s === 'running').length;
  const errorCount = Object.values(status).filter(s => s === 'error').length;
  const totalNodes = nodes.length;

  const progressPercent = Math.round((completedCount / totalNodes) * 100);

  return (
    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 p-4 min-w-[250px]">
      {/* Header */}
      <div className="flex items-center gap-2 mb-4">
        <Activity className="w-5 h-5 text-indigo-500 animate-pulse" />
        <span className="font-semibold text-gray-900 dark:text-white">Execution Progress</span>
      </div>

      {/* Progress Bar */}
      <div className="mb-4">
        <div className="flex justify-between text-xs text-gray-500 dark:text-gray-400 mb-1">
          <span>Progress</span>
          <span>{progressPercent}%</span>
        </div>
        <div className="h-2 bg-gray-200 dark:bg-gray-700 rounded-full overflow-hidden">
          <div
            className="h-full bg-gradient-to-r from-indigo-500 to-purple-500 transition-all duration-500"
            style={{ width: `${progressPercent}%` }}
          />
        </div>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-3 gap-3">
        {/* Completed */}
        <div className="text-center">
          <div className="flex items-center justify-center w-8 h-8 mx-auto mb-1 bg-green-100 dark:bg-green-900/30 rounded-full">
            <CheckCircle className="w-4 h-4 text-green-600" />
          </div>
          <div className="text-lg font-bold text-gray-900 dark:text-white">{completedCount}</div>
          <div className="text-xs text-gray-500 dark:text-gray-400">Completed</div>
        </div>

        {/* Running */}
        <div className="text-center">
          <div className="flex items-center justify-center w-8 h-8 mx-auto mb-1 bg-blue-100 dark:bg-blue-900/30 rounded-full">
            <Loader2 className="w-4 h-4 text-blue-600 animate-spin" />
          </div>
          <div className="text-lg font-bold text-gray-900 dark:text-white">{runningCount}</div>
          <div className="text-xs text-gray-500 dark:text-gray-400">Running</div>
        </div>

        {/* Pending */}
        <div className="text-center">
          <div className="flex items-center justify-center w-8 h-8 mx-auto mb-1 bg-gray-100 dark:bg-gray-700 rounded-full">
            <Clock className="w-4 h-4 text-gray-600" />
          </div>
          <div className="text-lg font-bold text-gray-900 dark:text-white">
            {totalNodes - completedCount - runningCount - errorCount}
          </div>
          <div className="text-xs text-gray-500 dark:text-gray-400">Pending</div>
        </div>
      </div>

      {/* Error indicator if any */}
      {errorCount > 0 && (
        <div className="mt-4 flex items-center gap-2 p-2 bg-red-50 dark:bg-red-900/20 rounded-lg">
          <AlertCircle className="w-4 h-4 text-red-600" />
          <span className="text-sm text-red-700 dark:text-red-400">
            {errorCount} node(s) failed
          </span>
        </div>
      )}

      {/* Current node indicator */}
      {runningCount > 0 && (
        <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-700">
          <div className="text-xs text-gray-500 dark:text-gray-400 mb-1">Currently executing:</div>
          {nodes
            .filter(node => status[node.id] === 'running')
            .map(node => (
              <div
                key={node.id}
                className="flex items-center gap-2 text-sm text-gray-700 dark:text-gray-300"
              >
                <Loader2 className="w-3 h-3 animate-spin text-blue-500" />
                {node.data?.label || node.type}
              </div>
            ))}
        </div>
      )}

      {/* Estimated time remaining */}
      {runningCount > 0 && (
        <div className="mt-3 text-xs text-gray-500 dark:text-gray-400 text-center">
          <Clock className="w-3 h-3 inline mr-1" />~{Math.max(1, (totalNodes - completedCount) * 2)}{' '}
          seconds remaining
        </div>
      )}
    </div>
  );
};

export default ExecutionOverlay;

ExecutionOverlay.propTypes = {
  status: PropTypes.object.isRequired,
  nodes: PropTypes.arrayOf(PropTypes.object).isRequired,
};

import React, { useState } from 'react';
import {
  X,
  Search,
  Mail,
  Linkedin,
  Phone,
  MessageSquare,
  Clock,
  GitBranch,
  Play,
  Shuffle,
  Sparkles,
  Zap,
} from 'lucide-react';
import { nodeDefinitions, getNodesByCategory } from './nodes';
import PropTypes from 'prop-types';

const NodeCreatorPanel = ({ isOpen, onClose, onAddNode }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');

  const categories = getNodesByCategory();

  // Filter nodes based on search
  const allNodes = Object.values(nodeDefinitions);
  const filteredNodes = allNodes.filter(
    node =>
      node.label.toLowerCase().includes(searchTerm.toLowerCase()) ||
      node.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const iconMap = {
    Play,
    Mail,
    Linkedin,
    Phone,
    MessageSquare,
    Clock,
    GitBranch,
    Shuffle,
  };

  const colorClasses = {
    blue: 'bg-blue-100 text-blue-600 dark:bg-blue-900/30 dark:text-blue-400',
    green: 'bg-green-100 text-green-600 dark:bg-green-900/30 dark:text-green-400',
    purple: 'bg-purple-100 text-purple-600 dark:bg-purple-900/30 dark:text-purple-400',
    orange: 'bg-orange-100 text-orange-600 dark:bg-orange-900/30 dark:text-orange-400',
    pink: 'bg-pink-100 text-pink-600 dark:bg-pink-900/30 dark:text-pink-400',
    indigo: 'bg-indigo-100 text-indigo-600 dark:bg-indigo-900/30 dark:text-indigo-400',
    emerald: 'bg-emerald-100 text-emerald-600 dark:bg-emerald-900/30 dark:text-emerald-400',
  };

  const handleClose = event => {
    if (event.type === 'click' || event.key === 'Enter' || event.key === ' ') {
      onClose();
    }
  };

  if (!isOpen) return null;

  return (
    <>
      {/* Backdrop */}
      <div
        className="fixed inset-0 bg-black/20 z-40"
        role="button"
        tabIndex={0}
        aria-label="Close node creator panel"
        onClick={handleClose}
        onKeyDown={handleClose}
      />

      {/* Panel */}
      <div className="fixed right-0 top-0 h-full w-80 bg-white dark:bg-gray-800 shadow-2xl z-50 flex flex-col">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200 dark:border-gray-700">
          <div className="flex items-center gap-2">
            <Zap className="w-5 h-5 text-indigo-500" />
            <h2 className="text-lg font-semibold text-gray-900 dark:text-white">Add Node</h2>
          </div>
          <button
            onClick={onClose}
            className="p-1 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg"
          >
            <X className="w-5 h-5 text-gray-500" />
          </button>
        </div>

        {/* Search */}
        <div className="p-4 border-b border-gray-200 dark:border-gray-700">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-400" />
            <input
              type="text"
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
              placeholder="Search nodes..."
              className="w-full pl-10 pr-4 py-2 border border-gray-200 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-white text-sm focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
            />
          </div>
        </div>

        {/* Category Tabs */}
        <div className="flex gap-1 p-2 border-b border-gray-200 dark:border-gray-700 overflow-x-auto">
          <button
            onClick={() => setSelectedCategory('all')}
            className={`px-3 py-1.5 text-xs font-medium rounded-lg whitespace-nowrap transition-colors ${
              selectedCategory === 'all'
                ? 'bg-indigo-100 text-indigo-700 dark:bg-indigo-900/30 dark:text-indigo-300'
                : 'text-gray-600 dark:text-gray-400 hover:bg-gray-100 dark:hover:bg-gray-700'
            }`}
          >
            All
          </button>
          {Object.entries(categories).map(([key, category]) => (
            <button
              key={key}
              onClick={() => setSelectedCategory(key)}
              className={`px-3 py-1.5 text-xs font-medium rounded-lg whitespace-nowrap transition-colors ${
                selectedCategory === key
                  ? 'bg-indigo-100 text-indigo-700 dark:bg-indigo-900/30 dark:text-indigo-300'
                  : 'text-gray-600 dark:text-gray-400 hover:bg-gray-100 dark:hover:bg-gray-700'
              }`}
            >
              {category.label}
            </button>
          ))}
        </div>

        {/* Node List */}
        <div className="flex-1 overflow-y-auto p-4 space-y-2">
          {searchTerm ? (
            // Search results
            filteredNodes.length > 0 ? (
              filteredNodes.map(node => {
                const Icon = node.icon;
                return (
                  <button
                    key={node.type}
                    onClick={() => onAddNode(node.type)}
                    className="w-full flex items-center gap-3 p-3 rounded-lg border border-gray-200 dark:border-gray-700 hover:border-indigo-300 dark:hover:border-indigo-600 hover:bg-gray-50 dark:hover:bg-gray-700/50 transition-all group"
                  >
                    <div className={`p-2 rounded-lg ${colorClasses[node.color]}`}>
                      <Icon className="w-4 h-4" />
                    </div>
                    <div className="text-left">
                      <div className="font-medium text-gray-900 dark:text-white text-sm">
                        {node.label}
                      </div>
                      <div className="text-xs text-gray-500 dark:text-gray-400">
                        {node.description}
                      </div>
                    </div>
                  </button>
                );
              })
            ) : (
              <div className="text-center py-8 text-gray-500 dark:text-gray-400">
                No nodes found for "{searchTerm}"
              </div>
            )
          ) : (
            // Category view
            Object.entries(categories)
              .filter(([key]) => selectedCategory === 'all' || selectedCategory === key)
              .map(([key, category]) => (
                <div key={key} className="mb-6">
                  <h3 className="text-xs font-semibold text-gray-500 dark:text-gray-400 uppercase tracking-wider mb-2">
                    {category.label}
                  </h3>
                  <div className="space-y-2">
                    {category.nodes.map(node => {
                      const Icon = node.icon;
                      return (
                        <button
                          key={node.type}
                          onClick={() => onAddNode(node.type)}
                          className="w-full flex items-center gap-3 p-3 rounded-lg border border-gray-200 dark:border-gray-700 hover:border-indigo-300 dark:hover:border-indigo-600 hover:bg-gray-50 dark:hover:bg-gray-700/50 transition-all group"
                        >
                          <div className={`p-2 rounded-lg ${colorClasses[node.color]}`}>
                            <Icon className="w-4 h-4" />
                          </div>
                          <div className="text-left flex-1">
                            <div className="font-medium text-gray-900 dark:text-white text-sm">
                              {node.label}
                            </div>
                            <div className="text-xs text-gray-500 dark:text-gray-400">
                              {node.description}
                            </div>
                          </div>
                        </button>
                      );
                    })}
                  </div>
                </div>
              ))
          )}
        </div>

        {/* Footer - AI Suggestion */}
        <div className="p-4 border-t border-gray-200 dark:border-gray-700 bg-gradient-to-r from-purple-50 to-indigo-50 dark:from-purple-900/20 dark:to-indigo-900/20">
          <button className="w-full flex items-center justify-center gap-2 py-2 px-4 bg-gradient-to-r from-purple-500 to-indigo-500 text-white rounded-lg hover:from-purple-600 hover:to-indigo-600 transition-all">
            <Sparkles className="w-4 h-4" />
            <span className="text-sm font-medium">AI Suggest Next Step</span>
          </button>
        </div>
      </div>
    </>
  );
};

export default NodeCreatorPanel;

NodeCreatorPanel.propTypes = {
  isOpen: PropTypes.bool.isRequired,
  onClose: PropTypes.func.isRequired,
  onAddNode: PropTypes.func.isRequired,
};

