  const nodeIcons = {
    trigger: Target,
    email: Mail,
    linkedin: Linkedin,
    call: Phone,
    delay: Clock,
    condition: Zap,
    abtest: TrendingUp,
  };

  const handleClose = event => {
    if (event.type === 'click' || event.key === 'Enter' || event.key === ' ') {
