/**
 * Format a KPI value for display
 */
export function formatKpiValue(data) {
  if (!data) return '';

  const { value, format, unit } = data;

  if (format === 'currency') {
    if (value >= 1000000) {
      return `$${(value / 1000000).toFixed(1)}M`;
    }
    if (value >= 1000) {
      return `$${(value / 1000).toFixed(0)}K`;
    }
    return `$${value}`;
  }

  if (unit === '%') {
    return `${value}%`;
  }

  if (unit === 'hrs') {
    return `${value} hrs`;
  }

  if (value >= 1000) {
    return `${(value / 1000).toFixed(1)}K`;
  }

  return String(value);
}

