export const extractIntegrationData = async name => {
  await new Promise(r => setTimeout(r, 500));
  const state = loadState();
  // Simulate different payloads per integration
  let imported = [];
  if (name === 'Salesloft') {
    imported = [
      {
        id: Date.now(),
        name: 'Alex Carter',
        title: 'SDR Manager',
        company: 'RevOpsCo',
        industry: 'SaaS',
        location: 'Remote',
        score: 72,
