        avatar: '',
        source: 'Salesloft',
        verified: true,
        enrichment: { techStack: ['Salesloft', 'Salesforce'] },
        activity: [
          { type: 'email_opened', message: 'Opened outreach from Salesloft', time: 'Today' },
        ],
      },
      {
        id: Date.now() + 1,
        name: 'Nina Patel',
        title: 'VP Growth',
        company: 'ScaleWorks',
        industry: 'AI',
        location: 'NYC',
        score: 88,
        status: 'hot',
        email: 'nina@scaleworks.ai',
