        enrichment: { techStack: ['Salesloft'] },
        activity: [
          { type: 'email_replied', message: 'Responded via Salesloft thread', time: 'Today' },
        ],
      },
    ];
  } else if (name === 'Gong') {
    imported = [
      {
        id: Date.now() + 2,
        name: 'Chris Wong',
        title: 'Director Sales',
        company: 'DataQuanta',
