/**
 * Feature Flags Service
 * Enable/disable features dynamically for A/B testing and gradual rollouts
 */

export interface FeatureFlags {
  // Core Features
  enableRealTimeUpdates: boolean;
  enableAIAssistant: boolean;
  enableMultiChannelCampaigns: boolean;

  // Beta Features
  enableAdvancedAnalytics: boolean;
  enableLeadScoring: boolean;
  enableABTesting: boolean;
  enableWorkflowAutomation: boolean;

  // Experimental Features
  enableVoiceCommands: boolean;
  enablePredictiveLeadGen: boolean;
  enableAutoSequencing: boolean;

  // UI/UX
  enableNewDashboard: boolean;
  enableDarkMode: boolean;
  enableCompactMode: boolean;
  enableKeyboardShortcuts: boolean;
}

// Default feature flags
const defaultFlags: FeatureFlags = {
  // Core - enabled by default
  enableRealTimeUpdates: true,
  enableAIAssistant: true,
  enableMultiChannelCampaigns: true,

  // Beta - enabled for early adopters
  enableAdvancedAnalytics: true,
  enableLeadScoring: true,
  enableABTesting: false,
  enableWorkflowAutomation: false,

  // Experimental - disabled by default
  enableVoiceCommands: false,
  enablePredictiveLeadGen: false,
  enableAutoSequencing: false,

  // UI/UX - enabled by default
  enableNewDashboard: true,
  enableDarkMode: true,
  enableCompactMode: false,
  enableKeyboardShortcuts: true,
};

// Storage key for persisted flags
const STORAGE_KEY = 'artisan_feature_flags';

// In-memory cache
let cachedFlags: FeatureFlags | null = null;

/**
 * Load feature flags from localStorage or remote config
 */
export function loadFeatureFlags(): FeatureFlags {
  if (cachedFlags !== null) return cachedFlags;

  try {
    // Try to load from localStorage first (for overrides)
    const stored = localStorage.getItem(STORAGE_KEY);
    if (stored) {
      cachedFlags = { ...defaultFlags, ...JSON.parse(stored) };
      return cachedFlags as FeatureFlags;
    }
  } catch (error) {
    console.warn('Failed to load feature flags from storage:', error);
  }

  cachedFlags = { ...defaultFlags };
  return cachedFlags as FeatureFlags;
}

/**
 * Check if a specific feature is enabled
 */
export function isFeatureEnabled(flag: keyof FeatureFlags): boolean {
  const flags = loadFeatureFlags();
  return flags[flag] ?? false;
}

/**
 * Update feature flags (for admin use or remote config)
 */
export function updateFeatureFlags(updates: Partial<FeatureFlags>): void {
  const current = loadFeatureFlags();
  cachedFlags = { ...current, ...updates };

  try {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(cachedFlags));
  } catch (error) {
    console.warn('Failed to persist feature flags:', error);
  }
}

/**
 * Reset feature flags to defaults
 */
export function resetFeatureFlags(): void {
  cachedFlags = { ...defaultFlags };
  try {
    localStorage.removeItem(STORAGE_KEY);
  } catch (error) {
    console.warn('Failed to clear feature flags:', error);
  }
}

/**
 * Get all feature flags (for admin panel)
 */
export function getAllFeatureFlags(): FeatureFlags {
  return loadFeatureFlags();
}

/**
 * React hook for feature flags
 */
import { useState, useEffect } from 'react';

export function useFeatureFlag(flag: keyof FeatureFlags): boolean {
  const [enabled, setEnabled] = useState(() => isFeatureEnabled(flag));

  useEffect(() => {
    // Re-check on mount in case flags changed
    setEnabled(isFeatureEnabled(flag));
  }, [flag]);

  return enabled;
}

export function useFeatureFlags(): FeatureFlags {
  const [flags, setFlags] = useState<FeatureFlags>(() => loadFeatureFlags());

  useEffect(() => {
    setFlags(loadFeatureFlags());
  }, []);

  return flags;
}

/**
 * Feature Flag Gate Component
 */
import React from 'react';

interface FeatureGateProps {
  flag: keyof FeatureFlags;
  children: React.ReactNode;
  fallback?: React.ReactNode;
}

export function FeatureGate({
  flag,
  children,
  fallback = null,
}: FeatureGateProps): React.ReactNode {
  const isEnabled = useFeatureFlag(flag);
  return isEnabled ? children : fallback;
}

export default {
  isFeatureEnabled,
  updateFeatureFlags,
  resetFeatureFlags,
  getAllFeatureFlags,
  useFeatureFlag,
  useFeatureFlags,
  FeatureGate,
};

/**
 * Offline Storage Service
 * IndexedDB wrapper for offline support and data persistence
 */

const DB_NAME = 'artisan_offline_db';
const DB_VERSION = 1;

// Store names
export const STORES = {
  LEADS: 'leads',
  CAMPAIGNS: 'campaigns',
  TEMPLATES: 'templates',
  DRAFTS: 'drafts',
  SYNC_QUEUE: 'sync_queue',
  CACHE: 'cache',
} as const;

type StoreName = (typeof STORES)[keyof typeof STORES];

// Sync queue item
export interface SyncQueueItem {
  id: string;
  store: StoreName;
  action: 'create' | 'update' | 'delete';
  data: unknown;
  timestamp: number;
  retries: number;
  lastError?: string;
}

let db: IDBDatabase | null = null;

/**
 * Initialize IndexedDB
 */
export function initializeOfflineStorage(): Promise<IDBDatabase> {
  return new Promise((resolve, reject) => {
    if (db) {
      resolve(db);
      return;
    }

    const request = indexedDB.open(DB_NAME, DB_VERSION);

    request.onerror = () => {
      console.error('[OfflineStorage] Failed to open database:', request.error);
      reject(new Error(request.error?.message || 'Failed to open database'));
    };

    request.onsuccess = () => {
      db = request.result;
      console.log('[OfflineStorage] Database opened successfully');
      resolve(db);
    };

    request.onupgradeneeded = event => {
      const database = (event.target as IDBOpenDBRequest).result;
      console.log('[OfflineStorage] Upgrading database schema');

      // Create object stores
      if (!database.objectStoreNames.contains(STORES.LEADS)) {
        const leadsStore = database.createObjectStore(STORES.LEADS, { keyPath: 'id' });
        leadsStore.createIndex('email', 'email', { unique: true });
        leadsStore.createIndex('status', 'status', { unique: false });
        leadsStore.createIndex('updatedAt', 'updatedAt', { unique: false });
      }

      if (!database.objectStoreNames.contains(STORES.CAMPAIGNS)) {
        const campaignsStore = database.createObjectStore(STORES.CAMPAIGNS, { keyPath: 'id' });
        campaignsStore.createIndex('status', 'status', { unique: false });
        campaignsStore.createIndex('updatedAt', 'updatedAt', { unique: false });
      }

      if (!database.objectStoreNames.contains(STORES.TEMPLATES)) {
        const templatesStore = database.createObjectStore(STORES.TEMPLATES, { keyPath: 'id' });
        templatesStore.createIndex('type', 'type', { unique: false });
      }

      if (!database.objectStoreNames.contains(STORES.DRAFTS)) {
        const draftsStore = database.createObjectStore(STORES.DRAFTS, { keyPath: 'id' });
        draftsStore.createIndex('type', 'type', { unique: false });
        draftsStore.createIndex('updatedAt', 'updatedAt', { unique: false });
      }

      if (!database.objectStoreNames.contains(STORES.SYNC_QUEUE)) {
        const syncStore = database.createObjectStore(STORES.SYNC_QUEUE, { keyPath: 'id' });
        syncStore.createIndex('timestamp', 'timestamp', { unique: false });
        syncStore.createIndex('store', 'store', { unique: false });
      }

      if (!database.objectStoreNames.contains(STORES.CACHE)) {
        const cacheStore = database.createObjectStore(STORES.CACHE, { keyPath: 'key' });
        cacheStore.createIndex('expiresAt', 'expiresAt', { unique: false });
      }
    };
  });
}

/**
 * Get database instance
 */
async function getDB(): Promise<IDBDatabase> {
  if (!db) {
    return initializeOfflineStorage();
  }
  return db;
}

/**
 * Generic get operation
 */
export async function get<T>(store: StoreName, key: string): Promise<T | undefined> {
  const database = await getDB();

  return new Promise((resolve, reject) => {
    const transaction = database.transaction(store, 'readonly');
    const objectStore = transaction.objectStore(store);
    const request = objectStore.get(key);

    request.onsuccess = () => resolve(request.result as T);
    request.onerror = () => reject(new Error(request.error?.message || 'Failed to get data'));
  });
}

/**
 * Generic getAll operation
 */
export async function getAll<T>(store: StoreName): Promise<T[]> {
  const database = await getDB();

  return new Promise((resolve, reject) => {
    const transaction = database.transaction(store, 'readonly');
    const objectStore = transaction.objectStore(store);
    const request = objectStore.getAll();

    request.onsuccess = () => resolve(request.result as T[]);
    request.onerror = () => reject(new Error(request.error?.message || 'Failed to get all data'));
  });
}

/**
 * Generic put operation
 */
export async function put<T>(store: StoreName, data: T): Promise<void> {
  const database = await getDB();

  return new Promise((resolve, reject) => {
    const transaction = database.transaction(store, 'readwrite');
    const objectStore = transaction.objectStore(store);
    const request = objectStore.put(data);

    request.onsuccess = () => resolve();
    request.onerror = () => reject(new Error(request.error?.message || 'Failed to put data'));
  });
}

/**
 * Generic delete operation
 */
export async function remove(store: StoreName, key: string): Promise<void> {
  const database = await getDB();

  return new Promise((resolve, reject) => {
    const transaction = database.transaction(store, 'readwrite');
    const objectStore = transaction.objectStore(store);
    const request = objectStore.delete(key);

    request.onsuccess = () => resolve();
    request.onerror = () => reject(new Error(request.error?.message || 'Failed to delete data'));
  });
}

/**
 * Clear all data in a store
 */
export async function clear(store: StoreName): Promise<void> {
  const database = await getDB();

  return new Promise((resolve, reject) => {
    const transaction = database.transaction(store, 'readwrite');
    const objectStore = transaction.objectStore(store);
    const request = objectStore.clear();

    request.onsuccess = () => resolve();
    request.onerror = () => reject(new Error(request.error?.message || 'Failed to clear data'));
  });
}

// ============================================
// Sync Queue Operations
// ============================================

/**
 * Add item to sync queue
 */
export async function addToSyncQueue(
  store: StoreName,
  action: SyncQueueItem['action'],
  data: unknown
): Promise<void> {
  const item: SyncQueueItem = {
    id: `${Date.now()}-${Math.random().toString(36).substring(2, 9)}`,
    store,
    action,
    data,
    timestamp: Date.now(),
    retries: 0,
  };

  await put(STORES.SYNC_QUEUE, item);
}

/**
 * Get pending sync items
 */
export async function getPendingSyncItems(): Promise<SyncQueueItem[]> {
  return getAll<SyncQueueItem>(STORES.SYNC_QUEUE);
}

/**
 * Remove item from sync queue
 */
export async function removeSyncItem(id: string): Promise<void> {
  return remove(STORES.SYNC_QUEUE, id);
}

/**
 * Update sync item (e.g., increment retries)
 */
export async function updateSyncItem(item: SyncQueueItem): Promise<void> {
  return put(STORES.SYNC_QUEUE, item);
}

// ============================================
// Cache Operations
// ============================================

interface CacheItem<T> {
  key: string;
  data: T;
  expiresAt: number;
}

/**
 * Set cache item with TTL
 */
export async function setCache<T>(
  key: string,
  data: T,
  ttlMs: number = 5 * 60 * 1000 // 5 minutes default
): Promise<void> {
  const item: CacheItem<T> = {
    key,
    data,
    expiresAt: Date.now() + ttlMs,
  };

  await put(STORES.CACHE, item);
}

/**
 * Get cache item (returns undefined if expired)
 */
export async function getCache<T>(key: string): Promise<T | undefined> {
  const item = await get<CacheItem<T>>(STORES.CACHE, key);

  if (!item) return undefined;

  if (Date.now() > item.expiresAt) {
    // Expired, remove it
    await remove(STORES.CACHE, key);
    return undefined;
  }

  return item.data;
}

/**
 * Clear expired cache items
 */
export async function clearExpiredCache(): Promise<void> {
  const database = await getDB();
  const now = Date.now();

  return new Promise((resolve, reject) => {
    const transaction = database.transaction(STORES.CACHE, 'readwrite');
    const objectStore = transaction.objectStore(STORES.CACHE);
    const index = objectStore.index('expiresAt');
    const range = IDBKeyRange.upperBound(now);
    const request = index.openCursor(range);

    request.onsuccess = event => {
      const cursor = (event.target as IDBRequest<IDBCursorWithValue>).result;
      if (cursor) {
        cursor.delete();
        cursor.continue();
      }
    };

    transaction.oncomplete = () => resolve();
    transaction.onerror = () =>
      reject(new Error(transaction.error?.message || 'Transaction failed'));
  });
}

// ============================================
// React Hooks
// ============================================

import { useState, useEffect } from 'react';

/**
 * Hook to check if app is offline
 */
export function useOfflineStatus(): boolean {
  const [isOffline, setIsOffline] = useState(!navigator.onLine);

  useEffect(() => {
    const handleOnline = () => setIsOffline(false);
    const handleOffline = () => setIsOffline(true);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  return isOffline;
}

/**
 * Hook to get pending sync count
 */
export function usePendingSyncCount(): number {
  const [count, setCount] = useState(0);

  useEffect(() => {
    const checkPending = async () => {
      try {
        const items = await getPendingSyncItems();
        setCount(items.length);
      } catch (error) {
        console.error('[OfflineStorage] Failed to get pending sync count:', error);
      }
    };

    checkPending();

    // Check periodically
    const interval = setInterval(checkPending, 30000);
    return () => clearInterval(interval);
  }, []);

  return count;
}

export default {
  initializeOfflineStorage,
  get,
  getAll,
  put,
  remove,
  clear,
  addToSyncQueue,
  getPendingSyncItems,
  removeSyncItem,
  updateSyncItem,
  setCache,
  getCache,
  clearExpiredCache,
  useOfflineStatus,
  usePendingSyncCount,
  STORES,
};

/**
 * Performance monitoring utilities for Artisan
 * Tracks key performance metrics and user experience
 */

/**
 * Measure and log performance metrics for a function
 * @param {string} label - Name of the operation being measured
 * @param {Function} fn - Function to measure
 * @returns {*} Result of the function
 */
export const measurePerformance = async (label, fn) => {
  const startTime = performance.now();

  try {
    const result = await fn();
    const endTime = performance.now();
    const duration = endTime - startTime;

    // Log slow operations (>1000ms)
    if (duration > 1000) {
      console.warn(`[Performance] Slow operation: ${label} took ${duration.toFixed(2)}ms`);
    } else if (process.env.NODE_ENV === 'development') {
      console.log(`[Performance] ${label} took ${duration.toFixed(2)}ms`);
    }

    // Store performance metrics
    trackPerformanceMetric(label, duration);

    return result;
  } catch (error) {
    const endTime = performance.now();
    const duration = endTime - startTime;
    console.error(`[Performance] ${label} failed after ${duration.toFixed(2)}ms`, error);
    throw error;
  }
};

/**
 * Track performance metric in session storage
 */
const trackPerformanceMetric = (label, duration) => {
  try {
    const metrics = JSON.parse(sessionStorage.getItem('artisan_perf_metrics') || '{}');

    if (!metrics[label]) {
      metrics[label] = {
        count: 0,
        total: 0,
        min: Infinity,
        max: 0,
        avg: 0,
      };
    }

    const metric = metrics[label];
    metric.count++;
    metric.total += duration;
    metric.min = Math.min(metric.min, duration);
    metric.max = Math.max(metric.max, duration);
    metric.avg = metric.total / metric.count;

    // Keep only last 100 unique metrics
    const keys = Object.keys(metrics);
    if (keys.length > 100) {
      delete metrics[keys[0]];
    }

    sessionStorage.setItem('artisan_perf_metrics', JSON.stringify(metrics));
  } catch (e) {
    // Fail silently - don't break app for perf tracking
  }
};

/**
 * Get performance metrics summary
 * @returns {Object} Performance metrics
 */
export const getPerformanceMetrics = () => {
  try {
    return JSON.parse(sessionStorage.getItem('artisan_perf_metrics') || '{}');
  } catch {
    return {};
  }
};

/**
 * Clear performance metrics
 */
export const clearPerformanceMetrics = () => {
  try {
    sessionStorage.removeItem('artisan_perf_metrics');
  } catch {
    // Fail silently
  }
};

/**
 * Log Web Vitals (Core Web Vitals)
 */
export const reportWebVitals = metric => {
  const { name, value, id } = metric;

  // Log in development
  if (process.env.NODE_ENV === 'development') {
    console.log(`[Web Vital] ${name}:`, value, 'id:', id);
  }

  // Store for analytics
  try {
    const vitals = JSON.parse(sessionStorage.getItem('artisan_web_vitals') || '[]');
    vitals.push({
      name,
      value,
      id,
      timestamp: Date.now(),
    });

    // Keep only last 50 vitals
    sessionStorage.setItem('artisan_web_vitals', JSON.stringify(vitals.slice(-50)));
  } catch {
    // Fail silently
  }

  // Send to analytics endpoint (future)
  // fetch('/api/analytics/vitals', {
  //   method: 'POST',
  //   headers: { 'Content-Type': 'application/json' },
  //   body: JSON.stringify(metric),
  // }).catch(() => {});
};

/**
 * Custom hook for measuring component render time
 */
export const usePerformance = componentName => {
  if (process.env.NODE_ENV === 'development') {
    const renderStart = performance.now();

    return () => {
      const renderEnd = performance.now();
      const duration = renderEnd - renderStart;

      if (duration > 100) {
        console.warn(`[Performance] ${componentName} render took ${duration.toFixed(2)}ms`);
      }
    };
  }

  return () => {}; // No-op in production
};

/**
 * Debounce function for performance optimization
 */
export const debounce = (func, wait = 300) => {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
};

/**
 * Throttle function for performance optimization
 */
export const throttle = (func, limit = 300) => {
  let inThrottle;
  return function executedFunction(...args) {
    if (!inThrottle) {
      func(...args);
      inThrottle = true;
      setTimeout(() => (inThrottle = false), limit);
    }
  };
};

/**
 * Check if the app is running slow
 * Returns true if performance metrics indicate slowness
 */
export const isRunningSlowly = () => {
  const metrics = getPerformanceMetrics();
  const slowOperations = Object.values(metrics).filter(m => m.avg > 1000);
  return slowOperations.length > 3;
};

/**
 * Get performance summary for debugging
 */
export const getPerformanceSummary = () => {
  const metrics = getPerformanceMetrics();

  return {
    totalOperations: Object.values(metrics).reduce((sum, m) => sum + m.count, 0),
    slowOperations: Object.entries(metrics)
      .filter(([, m]) => m.avg > 1000)
      .map(([label, m]) => ({ label, avg: m.avg.toFixed(2) })),
    topOperations: Object.entries(metrics)
      .sort(([, a], [, b]) => b.avg - a.avg)
      .slice(0, 5)
      .map(([label, m]) => ({
        label,
        avg: m.avg.toFixed(2),
        count: m.count,
      })),
  };
};

export default {
  measurePerformance,
  getPerformanceMetrics,
  clearPerformanceMetrics,
  reportWebVitals,
  usePerformance,
  debounce,
  throttle,
  isRunningSlowly,
  getPerformanceSummary,
};

