// TODO: Add proper TypeScript types for this experimental module
import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  FlaskConical,
  Play,
  Pause,
  Trophy,
  BarChart3,
  Users,
  Mail,
  MousePointer,
  Calendar,
  Sparkles,
  Plus,
  ChevronRight,
  CheckCircle2,
} from 'lucide-react';

/**
 * Experiment Engine Component
 * A/B subject lines, CTAs, send times, persona angles
 */

type ExperimentType = 'subject_line' | 'cta' | 'send_time';
type ExperimentStatus = 'running' | 'completed' | 'paused';

interface VariantMetrics {
  sent: number;
  opened: number;
  clicked: number;
  replied: number;
  conversionRate: number;
  statisticalSignificance: number; // 0 means N/A
}

type VariantContent = string | { start: string; end: string };

interface Variant {
  id: string;
  name: string;
  content: VariantContent;
  traffic: number; // percentage
  isControl: boolean;
  isWinner: boolean;
  metrics: VariantMetrics;
}

interface Experiment {
  id: string;
  name: string;
  type: ExperimentType;
  hypothesis: string;
  status: ExperimentStatus;
  variants: Variant[];
  minSampleSize: number;
  currentSampleSize: number;
  confidenceLevel: number;
  startDate: Date;
  endDate?: Date;
  autoApply: boolean;
  learnings: string[];
  winner?: string; // variant id
}

interface SuggestedExperiment {
  id: string;
  type: ExperimentType | 'persona_angle';
  name: string;
  hypothesis: string;
  potentialImpact: number;
  confidence: number;
}

interface StatsSummary {
  active: number;
  completed: number;
  winRate: number; // percent
  totalLearnings: number;
}

const ExperimentEngine: React.FC = () => {
  const [experiments, setExperiments] = useState<Experiment[]>([]);
  const [selectedExperiment, setSelectedExperiment] = useState<Experiment | null>(null);
  const [suggestedExperiments, setSuggestedExperiments] = useState<SuggestedExperiment[]>([]);
  const [stats] = useState<StatsSummary>({
    active: 3,
    completed: 24,
    winRate: 68,
    totalLearnings: 156,
  });

  useEffect(() => {
    const mockExperiments: Experiment[] = [
      {
        id: 'exp-1',
        name: 'Subject Line: Question vs Statement',
        type: 'subject_line',
        hypothesis: 'Question-based subject lines will increase open rates by 15%',
        status: 'running',
        variants: [
          {
            id: 'v1',
            name: 'Control (Statement)',
            content: 'Increase your sales pipeline by 3x',
            traffic: 50,
            isControl: true,
            isWinner: false,
            metrics: {
              sent: 1250,
              opened: 312,
              clicked: 89,
              replied: 23,
              conversionRate: 1.84,
              statisticalSignificance: 0,
            },
          },
          {
            id: 'v2',
            name: 'Variant (Question)',
            content: 'What if you could 3x your pipeline?',
            traffic: 50,
