              sent: 1248,
              opened: 387,
              clicked: 112,
              replied: 34,
              conversionRate: 2.72,
              statisticalSignificance: 94,
            },
          },
        ],
        minSampleSize: 3000,
        currentSampleSize: 2498,
        confidenceLevel: 95,
        startDate: new Date('2024-01-15'),
        autoApply: true,
        learnings: [],
      },
      {
        id: 'exp-2',
        name: 'CTA: Book a Demo vs Start Free Trial',
        type: 'cta',
        hypothesis: 'Start Free Trial will convert 20% more than Book a Demo',
        status: 'completed',
        winner: 'v2',
        variants: [
          {
            id: 'v1',
            name: 'Book a Demo',
            content: 'Book a Demo ',
