            isControl: true,
            isWinner: false,
            metrics: {
              sent: 892,
              opened: 214,
              clicked: 56,
              replied: 12,
              conversionRate: 1.34,
              statisticalSignificance: 0,
            },
          },
        ],
        minSampleSize: 2000,
        currentSampleSize: 1782,
        confidenceLevel: 95,
        startDate: new Date('2024-01-18'),
