        <ChevronRight
          className={`w-4 h-4 transition-colors ${isSelected ? 'text-cyan-400' : 'text-slate-500'}`}
        />
      </td>
    </motion.tr>
  );
};

const ProgramTableSkeleton = () => (
  <div className="space-y-3">
    {[1, 2, 3, 4].map(i => (
