// Cluster boundary visualization
const ClusterBoundary = ({ cluster, isSelected, onClick }) => {
  return (
    <motion.g
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ delay: 0.3 }}
      style={{ cursor: 'pointer' }}
      onClick={() => onClick(cluster)}
