import React from 'react';
import { motion } from 'framer-motion';
import {
  AlertTriangle,
  CheckCircle,
  Info,
  Target,
  MessageSquare,
  Radio,
  Users,
} from 'lucide-react';
import { GlassCard, GlassCardContent } from '../../../components/futuristic';

const RISK_ICONS = {
  'market-fit': Target,
  'message-fit': MessageSquare,
  'channel-saturation': Radio,
  capacity: Users,
};

const RISK_COLORS = {
  high: { bar: 'bg-rose-500', text: 'text-rose-400', bg: 'bg-rose-500/10' },
  medium: { bar: 'bg-amber-500', text: 'text-amber-400', bg: 'bg-amber-500/10' },
  low: { bar: 'bg-emerald-500', text: 'text-emerald-400', bg: 'bg-emerald-500/10' },
};

const RiskBar = ({ risk }) => {
  const colors = RISK_COLORS[risk.level];
  const Icon = RISK_ICONS[risk.id] || AlertTriangle;

  return (
    <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} className="group">
      <div className="flex items-center justify-between mb-2">
        <div className="flex items-center gap-2">
          <Icon className={`w-4 h-4 ${colors.text}`} />
          <span className="text-sm text-slate-300">{risk.label}</span>
        </div>
        <span className={`text-sm font-medium ${colors.text}`}>{risk.score}%</span>
      </div>

      <div className="relative h-2 bg-slate-800 rounded-full overflow-hidden">
        <motion.div
          initial={{ width: 0 }}
          animate={{ width: `${risk.score}%` }}
          transition={{ duration: 0.8, ease: 'easeOut' }}
          className={`absolute inset-y-0 left-0 rounded-full ${colors.bar}`}
        />
      </div>

      {/* Tooltip on hover */}
      <div className="mt-1 opacity-0 group-hover:opacity-100 transition-opacity">
        <p className="text-xs text-slate-500">{risk.description}</p>
      </div>
    </motion.div>
  );
};

export function RiskBreakdown({ risks, recommendation, activeStrategyData }) {
  if (!risks || risks.length === 0) {
    return (
      <GlassCard variant="subtle">
        <GlassCardContent className="p-6">
          <div className="text-center py-8 text-slate-400">
            <AlertTriangle className="w-12 h-12 mx-auto mb-3 opacity-30" />
            <p>Select a strategy to view risk analysis</p>
          </div>
        </GlassCardContent>
      </GlassCard>
    );
  }

  const overallRisk = Math.round(risks.reduce((sum, r) => sum + r.score, 0) / risks.length);
  const overallLevel = overallRisk >= 60 ? 'high' : overallRisk >= 40 ? 'medium' : 'low';
  const overallColors = RISK_COLORS[overallLevel];

  return (
    <GlassCard variant="subtle">
      <GlassCardContent className="p-6">
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-lg font-semibold text-slate-100">Risk Breakdown</h3>
          <div
            className={`px-3 py-1 rounded-full text-sm font-medium ${overallColors.bg} ${overallColors.text}`}
          >
            {overallLevel.charAt(0).toUpperCase() + overallLevel.slice(1)} Risk ({overallRisk}%)
          </div>
        </div>

        {/* Risk bars */}
        <div className="space-y-4 mb-6">
          {risks.map((risk, index) => (
            <RiskBar key={risk.id} risk={risk} />
          ))}
        </div>

        {/* Recommendation */}
        {recommendation && (
          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            className="p-4 bg-gradient-to-r from-cyan-500/10 to-purple-500/10 rounded-xl border border-cyan-500/20"
          >
            <div className="flex items-start gap-3">
              <div className="p-2 rounded-lg bg-cyan-500/20">
                <Info className="w-4 h-4 text-cyan-400" />
              </div>
              <div>
                <h4 className="font-medium text-slate-100 mb-1">System Recommendation</h4>
                <p className="text-sm text-slate-400">{recommendation.reason}</p>
              </div>
            </div>
          </motion.div>
        )}

        {/* Summary stats */}
        {activeStrategyData?.summary && (
          <div className="grid grid-cols-2 gap-4 mt-6 pt-6 border-t border-white/5">
            <div className="text-center p-3 bg-slate-800/50 rounded-lg">
              <p className="text-2xl font-bold text-emerald-400">
                ${(activeStrategyData.summary.projectedPipeline / 1000).toFixed(0)}K
              </p>
              <p className="text-xs text-slate-500">Projected Pipeline</p>
            </div>
            <div className="text-center p-3 bg-slate-800/50 rounded-lg">
              <p className="text-2xl font-bold text-cyan-400">
                {activeStrategyData.summary.confidence}%
              </p>
              <p className="text-xs text-slate-500">Confidence</p>
            </div>
          </div>
        )}
      </GlassCardContent>
    </GlassCard>
  );
}

export default RiskBreakdown;

import { useState, useEffect, useMemo } from 'react';

// Strategy templates
const STRATEGIES = [
  {
    id: 'strategy-a',
    name: 'Strategy A',
    label: 'Aggressive Growth',
    description: 'High-volume outreach with aggressive messaging',
    model: 'aggressive',
    config: {
      volumeMultiplier: 2.5,
      replyRateBase: 3.5,
      conversionRates: { demo: 0.25, proposal: 0.45, close: 0.35 },
      channelMix: { email: 60, linkedin: 25, phone: 12, sms: 3 },
    },
  },
  {
    id: 'strategy-b',
    name: 'Strategy B',
    label: 'Balanced Approach',
    description: 'Moderate volume with personalized messaging',
    model: 'balanced',
    config: {
      volumeMultiplier: 1.5,
      replyRateBase: 5.5,
      conversionRates: { demo: 0.32, proposal: 0.52, close: 0.38 },
      channelMix: { email: 45, linkedin: 35, phone: 15, sms: 5 },
    },
  },
  {
    id: 'strategy-c',
    name: 'Strategy C',
    label: 'Conservative Quality',
    description: 'Low volume, high-touch account-based approach',
    model: 'conservative',
    config: {
      volumeMultiplier: 0.8,
      replyRateBase: 8.0,
      conversionRates: { demo: 0.42, proposal: 0.62, close: 0.45 },
      channelMix: { email: 30, linkedin: 40, phone: 25, sms: 5 },
    },
  },
];

// Risk categories
const RISK_CATEGORIES = [
  {
    id: 'market-fit',
    label: 'Market Fit',
    description: 'Risk of messaging not resonating with target market',
  },
  {
    id: 'message-fit',
    label: 'Message Fit',
    description: 'Risk of value proposition misalignment',
  },
  {
    id: 'channel-saturation',
    label: 'Channel Saturation',
    description: 'Risk of diminishing returns from channel overuse',
  },
  { id: 'capacity', label: 'Capacity', description: 'Risk of team capacity constraints' },
];

// Generate projection data for a strategy
const generateProjectionData = (strategy, params) => {
  const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'];
  const { acv, monthlyVolume, seats } = params;
  const { volumeMultiplier, replyRateBase, conversionRates } = strategy.config;

  let cumulative = 0;
  const data = months.map((month, index) => {
    // Ramp up effect
    const rampFactor = Math.min(1, (index + 1) / 2);

    // Calculate funnel
    const contacts = monthlyVolume * volumeMultiplier * rampFactor;
    const replies = contacts * (replyRateBase / 100);
    const demos = replies * conversionRates.demo;
    const proposals = demos * conversionRates.proposal;
    const closed = proposals * conversionRates.close;

    // Pipeline value
    const monthlyPipeline = closed * acv;
    cumulative += monthlyPipeline;

    // Confidence bands (narrower as model is more conservative)
    const confidenceWidth =
      strategy.model === 'aggressive' ? 0.4 : strategy.model === 'balanced' ? 0.25 : 0.15;

    return {
      month,
      pipeline: Math.round(cumulative),
      low: Math.round(cumulative * (1 - confidenceWidth)),
      high: Math.round(cumulative * (1 + confidenceWidth)),
      meetings: Math.round(demos * (index + 1)),
    };
  });

  return data;
};

// Calculate risk scores for a strategy
const calculateRisks = (strategy, params) => {
  const { model, config } = strategy;

  const risks = {
    'market-fit': model === 'aggressive' ? 65 : model === 'balanced' ? 35 : 20,
    'message-fit': model === 'aggressive' ? 55 : model === 'balanced' ? 30 : 15,
    'channel-saturation':
      config.channelMix.email > 50 ? 70 : config.channelMix.email > 40 ? 45 : 25,
    capacity:
      params.seats < 3 && config.volumeMultiplier > 1.5
        ? 75
        : params.seats < 5 && config.volumeMultiplier > 2
          ? 60
          : 30,
  };

  return RISK_CATEGORIES.map(cat => ({
    ...cat,
    score: risks[cat.id],
    level: risks[cat.id] >= 60 ? 'high' : risks[cat.id] >= 40 ? 'medium' : 'low',
  }));
};

export function useSimulation() {
  const [loading, setLoading] = useState(true);
  const [selectedStrategies, setSelectedStrategies] = useState([
    'strategy-a',
    'strategy-b',
    'strategy-c',
  ]);
  const [activeStrategy, setActiveStrategy] = useState('strategy-b');

  // Simulation parameters
  const [params, setParams] = useState({
    acv: 50000,
    monthlyVolume: 2000,
    seats: 5,
    replyRateAssumption: 5,
    demoConversion: 30,
    proposalConversion: 50,
    closeRate: 35,
  });

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 600);
    return () => clearTimeout(timer);
  }, []);

  // Generate projections for each strategy
  const projections = useMemo(() => {
    return STRATEGIES.reduce((acc, strategy) => {
      acc[strategy.id] = generateProjectionData(strategy, params);
      return acc;
    }, {});
  }, [params]);

  // Calculate risks for active strategy
  const activeStrategyData = useMemo(() => {
    const strategy = STRATEGIES.find(s => s.id === activeStrategy);
    if (!strategy) return null;

    const projection = projections[activeStrategy];
    const risks = calculateRisks(strategy, params);
    const finalPipeline = projection[projection.length - 1]?.pipeline || 0;
    const totalMeetings = projection[projection.length - 1]?.meetings || 0;

    // Calculate overall confidence
    const avgRisk = risks.reduce((sum, r) => sum + r.score, 0) / risks.length;
    const confidence = Math.round(100 - avgRisk);

    return {
      ...strategy,
      projection,
      risks,
      summary: {
        projectedPipeline: finalPipeline,
        projectedMeetings: totalMeetings,
        confidence,
        roi: Math.round((finalPipeline / (params.seats * 3588 * 6)) * 100), // 6 month ROI
      },
    };
  }, [activeStrategy, projections, params]);

  // Get recommendation
  const recommendation = useMemo(() => {
    // Simple recommendation logic - prefer balanced unless specific conditions
    if (params.seats >= 5 && params.acv >= 75000) {
      return {
        strategyId: 'strategy-c',
        reason:
          'With your team size and high ACV, a conservative ABM approach will maximize deal quality.',
      };
    }
    if (params.seats >= 3 && params.acv >= 30000) {
      return {
        strategyId: 'strategy-b',
        reason:
          'A balanced approach optimizes for both volume and conversion with your current resources.',
      };
    }
    return {
      strategyId: 'strategy-a',
      reason: 'Aggressive outreach is recommended to quickly build pipeline with your lean team.',
    };
  }, [params]);

  return {
    strategies: STRATEGIES,
    selectedStrategies,
    setSelectedStrategies,
    activeStrategy,
    setActiveStrategy,
    params,
    setParams,
    updateParam: (key, value) => setParams(prev => ({ ...prev, [key]: value })),
    projections,
    activeStrategyData,
    recommendation,
    loading,
    riskCategories: RISK_CATEGORIES,
  };
}

