              <div className="flex flex-wrap gap-2">
                {[
                  { key: 'all', label: 'All Leads' },
                  { key: 'verified', label: 'Verified', variant: 'success' },
                  { key: 'hot', label: 'Hot (92+)', variant: 'danger' },
                  { key: 'warm', label: 'Warm (75-91)', variant: 'warning' },
