          </div>
        </motion.div>

        {/* Warnings */}
        {warnings.length > 0 && (
          <motion.div
            initial={prefersReducedMotion ? {} : { opacity: 0, y: 20 }}
            animate={prefersReducedMotion ? {} : { opacity: 1, y: 0 }}
            transition={{ delay: 0.05 }}
            className="space-y-2"
          >
            {warnings.map((warning, i) => {
              const metricLabel =
                usageMetrics.find(m => m.key === warning.key)?.label || warning.key;
              return (
                <div
                  key={i}
