                      </div>

                      <UsageBar
                        label=""
                        used={data.used}
                        limit={data.limit}
                        showTrend={true}
                        trend={data.trend}
                      />

                      <div className="flex justify-between mt-2 text-xs">
                        <span className="text-slate-400">
                          {data.used.toLocaleString()} / {data.limit.toLocaleString()}
                        </span>
                        <span
                          className={`font-medium ${
                            percent >= 90
                              ? 'text-red-400'
                              : percent >= 75
                                ? 'text-amber-400'
                                : 'text-slate-400'
                          }`}
                        >
                          {percent}% used
                        </span>
                      </div>
                    </motion.div>
                  );
                })}
              </div>
            </GlassCardContent>
          </GlassCard>
        </motion.div>

        {/* Usage Trends Chart */}
        <motion.div
          initial={prefersReducedMotion ? {} : { opacity: 0, y: 20 }}
