                    />
                    <p className="text-xs text-gray-500 dark:text-gray-400 mt-2">
                      Use variables: {{ firstName }}, {{ lastName }}, {{ company }}, {{ title }},{' '}
                      {{ industry }}
                    </p>
                  </div>

                  {/* Actions */}
                  <div className="flex gap-3 pt-4 border-t border-gray-200 dark:border-gray-700">
                    <Button
                      variant="primary"
                      className="flex-1 bg-gradient-to-r from-purple-500 to-pink-500"
                      onClick={() => {
                        setShowTemplateEditor(false);
                        setAiResponse(`Template "${editingTemplate.name}" updated successfully!`);
                        setIsAiAssistOpen(true);
                      }}
                    >
                      <CheckCircle2 size={16} className="mr-2" />
                      Save Changes
                    </Button>
                    <Button
                      variant="outline"
                      onClick={() => {
                        handleAiAction('optimize');
                        setShowTemplateEditor(false);
                      }}
                    >
                      <Wand2 size={16} className="mr-2" />
                      AI Optimize
                    </Button>
                    <Button variant="outline" onClick={() => setShowTemplateEditor(false)}>
                      Cancel
                    </Button>
                  </div>
                </div>
              </div>
            </Card>
          </div>
        )}

        {/* AI Assistant Panel */}
        {isAiAssistOpen && (
          <div
            className="fixed inset-0 bg-black/60 backdrop-blur-md z-50 flex items-center justify-center p-4"
            onClick={() => !isProcessing && setIsAiAssistOpen(false)}
          >
            <Card
              className="w-full max-w-3xl bg-white/95 dark:bg-gray-900/95 backdrop-blur-xl border-2 border-purple-500/50 shadow-2xl"
              onClick={e => e.stopPropagation()}
            >
              <div className="p-8">
                {/* Header */}
                <div className="flex items-center justify-between mb-8">
                  <div className="flex items-center gap-4">
                    <div className="relative">
                      <div className="w-14 h-14 rounded-2xl bg-gradient-to-br from-purple-500 via-pink-500 to-purple-600 flex items-center justify-center shadow-lg">
                        <Sparkles className="text-white" size={28} />
                      </div>
                      <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-500 rounded-full border-2 border-white animate-pulse"></div>
                    </div>
                    <div>
                      <h2 className="text-3xl font-bold text-gray-900 dark:text-white flex items-center gap-2">
                        Ava Template Assistant
                        <Badge
                          variant="outline"
                          className="text-xs bg-gradient-to-r from-purple-500/10 to-pink-500/10"
                        >
                          AI
                        </Badge>
                      </h2>
                      <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">
                        Select an action to automate your workflow
                      </p>
                    </div>
                  </div>
                  <button
                    onClick={() => {
                      setIsAiAssistOpen(false);
                      setAiResponse('');
                      setAiAction('');
                    }}
                    className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-200 transition-colors"
                    disabled={isProcessing}
                  >
                    <XCircle size={28} />
                  </button>
                </div>

                {/* Quick Actions Grid */}
                <div className="grid grid-cols-2 gap-4 mb-6">
                  {aiActions.map(action => {
                    const Icon = action.icon;
                    const isActive = aiAction === action.id && isProcessing;
                    return (
                      <button
                        key={action.id}
                        onClick={() => handleAiAction(action.id)}
                        disabled={isProcessing}
                        className={`relative group p-5 rounded-xl border-2 transition-all text-left overflow-hidden ${
                          isActive
                            ? 'border-purple-500 bg-gradient-to-br from-purple-50 to-pink-50 dark:from-purple-900/30 dark:to-pink-900/30 shadow-lg scale-105'
                            : 'border-gray-200 dark:border-gray-700 hover:border-purple-400 bg-white/70 dark:bg-gray-800/70 hover:shadow-md hover:scale-102'
                        } ${isProcessing && !isActive ? 'opacity-40 cursor-not-allowed' : 'cursor-pointer'}`}
                      >
                        {/* Gradient overlay on hover */}
                        <div className="absolute inset-0 bg-gradient-to-br from-purple-500/5 to-pink-500/5 opacity-0 group-hover:opacity-100 transition-opacity"></div>

                        <div className="relative flex items-start gap-4">
                          <div
                            className={`w-12 h-12 rounded-xl flex items-center justify-center transition-all ${
                              isActive
                                ? 'bg-gradient-to-br from-purple-500 to-pink-500 shadow-lg shadow-purple-500/50'
                                : 'bg-gradient-to-br from-purple-400 to-pink-400 group-hover:scale-110 group-hover:shadow-md'
                            }`}
                          >
                            <Icon className="text-white" size={22} />
                          </div>

                          <div className="flex-1 min-w-0">
                            <h3 className="font-semibold text-gray-900 dark:text-white mb-1 text-sm leading-tight">
                              {action.label}
                            </h3>
                            <p className="text-xs text-gray-600 dark:text-gray-400 line-clamp-2">
                              {action.id === 'generate' && 'Create custom templates using AI'}
                              {action.id === 'optimize' && 'Improve open rates and responses'}
                              {action.id === 'personalize' && 'Add dynamic prospect data'}
                              {action.id === 'ab-test' && 'Test multiple variations'}
                              {action.id === 'translate' && 'Reach global markets'}
                              {action.id === 'schedule' && 'Automate your outreach'}
                            </p>
                          </div>

                          {isActive && (
                            <div className="absolute top-3 right-3">
                              <div className="animate-spin rounded-full h-6 w-6 border-2 border-purple-500 border-t-transparent"></div>
                            </div>
                          )}
                        </div>
                      </button>
                    );
                  })}
                </div>

                {/* Response Area */}
                {aiResponse && (
                  <div className="space-y-4">
                    <div className="h-px bg-gradient-to-r from-transparent via-gray-300 dark:via-gray-700 to-transparent"></div>
                    <div className="p-5 rounded-xl bg-gradient-to-br from-green-50 via-emerald-50 to-green-50 dark:from-green-900/20 dark:via-emerald-900/20 dark:to-green-900/20 border-2 border-green-500/30 shadow-inner">
                      <div className="flex items-start gap-4">
                        <div className="w-10 h-10 rounded-xl bg-gradient-to-br from-green-500 to-emerald-500 flex items-center justify-center flex-shrink-0 shadow-md">
                          <CheckCircle2 className="text-white" size={20} />
                        </div>
                        <div className="flex-1">
                          <p className="font-bold text-gray-900 dark:text-white mb-2 text-lg">
                            Action Complete!
                          </p>
                          <p className="text-sm text-gray-700 dark:text-gray-300 leading-relaxed mb-4">
                            {aiResponse}
                          </p>
                          <div className="flex gap-2">
                            <Button variant="outline" size="sm" className="text-xs">
                              <Download size={14} className="mr-1" />
                              Export Result
                            </Button>
                            <Button variant="outline" size="sm" className="text-xs">
                              <Play size={14} className="mr-1" />
                              Preview
                            </Button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                )}

                {/* Footer hint */}
                {!aiResponse && !isProcessing && (
                  <div className="mt-6 p-4 rounded-lg bg-purple-50/50 dark:bg-purple-900/10 border border-purple-200/50 dark:border-purple-800/50">
                    <p className="text-xs text-gray-600 dark:text-gray-400 text-center flex items-center justify-center gap-2">
                      <Sparkles size={14} className="text-purple-500" />
                      Ava will process your request in seconds using advanced AI models
                    </p>
                  </div>
                )}
              </div>
            </Card>
          </div>
        )}
      </div>
    </DashboardLayout>
  );
};

export default Templates;

import React, { useState, useCallback, useEffect } from 'react';
import DashboardLayout from '../components/layout/DashboardLayout';
import WorkflowCanvas from '../components/workflow/WorkflowCanvasEnhanced';
import PlaybookLibrary from '../components/workflow/PlaybookLibrary';
import AIWorkflowAssistant from '../components/workflow/AIWorkflowAssistant';
import { useToast } from '../components/Toast';
import { saveCampaignDraft, getCampaignDraft } from '../lib/storage';
import {
  BookOpen,
  Save,
  Play,
  Settings,
  ChevronDown,
  Users,
  Calendar,
  Target,
  BarChart3,
  Sparkles,
  ArrowLeft,
  Eye,
  EyeOff,
  Download,
  Upload,
  Zap,
} from 'lucide-react';

const VisualCampaignBuilder = () => {
  const { showToast } = useToast();
  const [campaignName, setCampaignName] = useState('Untitled Campaign');
  const [isEditing, setIsEditing] = useState(false);
  const [showPlaybooks, setShowPlaybooks] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [showAIAssistant, setShowAIAssistant] = useState(false);
  const [workflow, setWorkflow] = useState(null);
  const [isSaving, setIsSaving] = useState(false);
  const [lastSaved, setLastSaved] = useState(null);
  const [showMetrics, setShowMetrics] = useState(true);

  // Campaign settings
  const [settings, setSettings] = useState({
    targetAudience: 'All Leads',
    startDate: '',
    endDate: '',
    timezone: 'America/New_York',
    dailyLimit: 100,
    priority: 'normal',
    aiOptimization: true,
  });

  // Load saved workflow on mount
  useEffect(() => {
    const draft = getCampaignDraft();
    if (draft?.workflow) {
      setWorkflow(draft.workflow);
      setCampaignName(draft.campaignName || 'Untitled Campaign');
      setSettings(draft.settings || settings);
      showToast(' Draft loaded successfully', 'info');
    }
  }, []);

  // Handle workflow save
  const handleSave = useCallback(
    async workflowData => {
      setIsSaving(true);
      try {
        const draft = {
          campaignName,
          workflow: workflowData,
          settings,
          savedAt: new Date().toISOString(),
        };
        saveCampaignDraft(draft);
        setWorkflow(workflowData);
        setLastSaved(new Date());
        showToast(' Campaign saved successfully', 'success');
      } catch (error) {
        showToast('Failed to save campaign', 'error');
      } finally {
        setIsSaving(false);
      }
    },
    [campaignName, settings, showToast]
  );

  // Handle workflow execution
  const handleExecute = useCallback(
    result => {
      console.log('Execution result:', result);
      showToast(` Campaign executed: ${result.nodes} steps processed`, 'success');
    },
    [showToast]
  );

  // Handle playbook selection
  const handleSelectPlaybook = useCallback(
    playbook => {
      setWorkflow(playbook.workflow);
      setCampaignName(`${playbook.name} Campaign`);
      setShowPlaybooks(false);
      showToast(` Loaded "${playbook.name}" template`, 'success');
    },
    [showToast]
  );

  // Handle AI-generated workflow
  const handleAIWorkflow = useCallback(
    aiWorkflow => {
      setWorkflow(aiWorkflow);
      setCampaignName(aiWorkflow.name || 'AI Generated Campaign');
      showToast(' AI workflow applied to canvas!', 'success');
    },
    [showToast]
  );

  // Export workflow
  const handleExport = useCallback(() => {
    if (!workflow) {
      showToast('No workflow to export', 'warning');
      return;
    }
    const data = JSON.stringify({ campaignName, workflow, settings }, null, 2);
    const blob = new Blob([data], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${campaignName.replace(/\s+/g, '-').toLowerCase()}-workflow.json`;
    a.click();
    showToast('Workflow exported', 'success');
  }, [workflow, campaignName, settings, showToast]);

  // Import workflow
  const handleImport = useCallback(() => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = '.json';
    input.onchange = e => {
      const file = e.target.files[0];
      if (!file) return;
      const reader = new FileReader();
      reader.onload = event => {
        try {
          const data = JSON.parse(event.target.result);
          if (data.workflow) {
            setWorkflow(data.workflow);
            setCampaignName(data.campaignName || 'Imported Campaign');
            setSettings(data.settings || settings);
            showToast('Workflow imported successfully', 'success');
          }
        } catch {
          showToast('Invalid workflow file', 'error');
        }
      };
      reader.readAsText(file);
    };
    input.click();
  }, [settings, showToast]);

  return (
    <DashboardLayout>
      <div className="h-[calc(100vh-64px)] flex flex-col">
        {/* Top Bar */}
        <div className="flex items-center justify-between px-4 py-3 bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700">
          <div className="flex items-center gap-4">
            {/* Back Button */}
            <button
              onClick={() => window.history.back()}
              className="p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg"
            >
              <ArrowLeft className="w-5 h-5 text-gray-600 dark:text-gray-400" />
            </button>

            {/* Campaign Name */}
            <div className="flex items-center gap-2">
              {isEditing ? (
                <input
                  type="text"
                  value={campaignName}
                  onChange={e => setCampaignName(e.target.value)}
                  onBlur={() => setIsEditing(false)}
                  onKeyDown={e => e.key === 'Enter' && setIsEditing(false)}
                  autoFocus
                  className="text-lg font-semibold bg-transparent border-b-2 border-indigo-500 focus:outline-none text-gray-900 dark:text-white"
                />
              ) : (
                <h1
                  onClick={() => setIsEditing(true)}
                  className="text-lg font-semibold text-gray-900 dark:text-white cursor-pointer hover:text-indigo-600 dark:hover:text-indigo-400"
                >
                  {campaignName}
                </h1>
              )}
              <span className="px-2 py-0.5 bg-yellow-100 text-yellow-700 text-xs rounded-full">
                Draft
              </span>
            </div>
          </div>

          <div className="flex items-center gap-3">
            {/* Last Saved */}
            {lastSaved && (
              <span className="text-sm text-gray-500 dark:text-gray-400 mr-2">
                Saved {lastSaved.toLocaleTimeString()}
              </span>
            )}

            {/* Toggle Metrics */}
            <button
              onClick={() => setShowMetrics(!showMetrics)}
              className={`flex items-center gap-2 px-3 py-2 rounded-lg transition-colors ${
                showMetrics
                  ? 'bg-indigo-100 text-indigo-700 dark:bg-indigo-900/30 dark:text-indigo-300'
                  : 'text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'
              }`}
              title={showMetrics ? 'Hide Metrics' : 'Show Metrics'}
            >
              {showMetrics ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
            </button>

            {/* Import/Export */}
            <div className="flex items-center border-l border-gray-200 dark:border-gray-700 pl-2 ml-1">
              <button
                onClick={handleImport}
                className="p-2 text-gray-600 dark:text-gray-400 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
                title="Import Workflow"
              >
                <Upload className="w-4 h-4" />
              </button>
              <button
                onClick={handleExport}
                className="p-2 text-gray-600 dark:text-gray-400 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
                title="Export Workflow"
              >
                <Download className="w-4 h-4" />
              </button>
            </div>

            {/* Playbook Library */}
            <button
              onClick={() => setShowPlaybooks(true)}
              className="flex items-center gap-2 px-3 py-2 text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
            >
              <BookOpen className="w-4 h-4" />
              <span className="text-sm font-medium hidden sm:inline">Playbooks</span>
            </button>

            {/* Settings */}
            <button
              onClick={() => setShowSettings(!showSettings)}
              className={`flex items-center gap-2 px-3 py-2 rounded-lg transition-colors ${
                showSettings
                  ? 'bg-gray-100 dark:bg-gray-700'
                  : 'text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'
              }`}
            >
              <Settings className="w-4 h-4" />
              <span className="text-sm font-medium hidden sm:inline">Settings</span>
              <ChevronDown
                className={`w-4 h-4 transition-transform ${showSettings ? 'rotate-180' : ''}`}
              />
            </button>

            {/* AI Assistant */}
            <button
              onClick={() => setShowAIAssistant(true)}
              className="flex items-center gap-2 px-4 py-2 bg-gradient-to-r from-purple-600 to-indigo-600 text-white rounded-lg hover:from-purple-700 hover:to-indigo-700 transition-all shadow-lg shadow-purple-500/25"
            >
              <Sparkles className="w-4 h-4" />
              <span className="text-sm font-medium">Ask Ava</span>
            </button>
          </div>
        </div>

        {/* Settings Panel (Collapsible) */}
        {showSettings && (
          <div className="bg-gray-50 dark:bg-gray-900 border-b border-gray-200 dark:border-gray-700 p-4">
            <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-7 gap-4">
              {/* Target Audience */}
              <div>
                <label className="block text-xs font-medium text-gray-600 dark:text-gray-400 mb-1">
                  <Users className="w-3 h-3 inline mr-1" />
                  Target Audience
                </label>
                <select
                  value={settings.targetAudience}
                  onChange={e => setSettings({ ...settings, targetAudience: e.target.value })}
                  className="w-full px-3 py-2 text-sm border border-gray-200 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
                >
                  <option>All Leads</option>
                  <option>Enterprise Leads</option>
                  <option>SMB Leads</option>
                  <option>Warm Leads</option>
                  <option>Cold Leads</option>
                </select>
              </div>

              {/* Start Date */}
              <div>
                <label className="block text-xs font-medium text-gray-600 dark:text-gray-400 mb-1">
                  <Calendar className="w-3 h-3 inline mr-1" />
                  Start Date
                </label>
                <input
                  type="date"
                  value={settings.startDate}
                  onChange={e => setSettings({ ...settings, startDate: e.target.value })}
                  className="w-full px-3 py-2 text-sm border border-gray-200 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
                />
              </div>

              {/* End Date */}
              <div>
                <label className="block text-xs font-medium text-gray-600 dark:text-gray-400 mb-1">
                  <Calendar className="w-3 h-3 inline mr-1" />
                  End Date
                </label>
                <input
                  type="date"
                  value={settings.endDate}
                  onChange={e => setSettings({ ...settings, endDate: e.target.value })}
                  className="w-full px-3 py-2 text-sm border border-gray-200 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
                />
              </div>

              {/* Daily Limit */}
              <div>
                <label className="block text-xs font-medium text-gray-600 dark:text-gray-400 mb-1">
                  <Target className="w-3 h-3 inline mr-1" />
                  Daily Limit
                </label>
                <input
                  type="number"
                  value={settings.dailyLimit}
                  onChange={e => setSettings({ ...settings, dailyLimit: parseInt(e.target.value) })}
                  className="w-full px-3 py-2 text-sm border border-gray-200 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
                />
              </div>

              {/* Priority */}
              <div>
                <label className="block text-xs font-medium text-gray-600 dark:text-gray-400 mb-1">
                  <BarChart3 className="w-3 h-3 inline mr-1" />
                  Priority
                </label>
                <select
                  value={settings.priority}
                  onChange={e => setSettings({ ...settings, priority: e.target.value })}
                  className="w-full px-3 py-2 text-sm border border-gray-200 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
                >
                  <option value="low">Low</option>
                  <option value="normal">Normal</option>
                  <option value="high">High</option>
                </select>
              </div>

              {/* AI Optimization */}
              <div>
                <label className="block text-xs font-medium text-gray-600 dark:text-gray-400 mb-1">
                  <Sparkles className="w-3 h-3 inline mr-1" />
                  AI Optimization
                </label>
                <button
                  onClick={() =>
                    setSettings({ ...settings, aiOptimization: !settings.aiOptimization })
                  }
                  className={`w-full px-3 py-2 text-sm rounded-lg flex items-center justify-center gap-2 transition-colors ${
                    settings.aiOptimization
                      ? 'bg-indigo-100 text-indigo-700 dark:bg-indigo-900/30 dark:text-indigo-300'
                      : 'bg-gray-100 text-gray-600 dark:bg-gray-700 dark:text-gray-400'
                  }`}
                >
                  <Zap className="w-4 h-4" />
                  {settings.aiOptimization ? 'Enabled' : 'Disabled'}
                </button>
              </div>

              {/* Timezone */}
              <div>
                <label className="block text-xs font-medium text-gray-600 dark:text-gray-400 mb-1">
                  Timezone
                </label>
                <select
                  value={settings.timezone}
                  onChange={e => setSettings({ ...settings, timezone: e.target.value })}
                  className="w-full px-3 py-2 text-sm border border-gray-200 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
                >
                  <option value="America/New_York">Eastern Time</option>
                  <option value="America/Chicago">Central Time</option>
                  <option value="America/Denver">Mountain Time</option>
                  <option value="America/Los_Angeles">Pacific Time</option>
                  <option value="Europe/London">London</option>
                  <option value="Europe/Paris">Paris</option>
                </select>
              </div>
            </div>
          </div>
        )}

        {/* Workflow Canvas */}
        <div className="flex-1 overflow-hidden">
          <WorkflowCanvas
            campaignName={campaignName}
            initialWorkflow={workflow}
            onSave={handleSave}
            onExecute={handleExecute}
            onAIAssistantOpen={() => setShowAIAssistant(true)}
            showMetrics={showMetrics}
          />
        </div>

        {/* Playbook Library Modal */}
        <PlaybookLibrary
          isOpen={showPlaybooks}
          onClose={() => setShowPlaybooks(false)}
          onSelectPlaybook={handleSelectPlaybook}
        />

        {/* AI Workflow Assistant */}
        <AIWorkflowAssistant
          isOpen={showAIAssistant}
          onClose={() => setShowAIAssistant(false)}
          onGenerateWorkflow={handleAIWorkflow}
        />
      </div>
    </DashboardLayout>
  );
};

export default VisualCampaignBuilder;

