import { useState, useCallback, useMemo, useEffect } from 'react';

// Local type definitions
interface WorkflowBlock {
  id: string;
  type: string;
  name: string;
  position: { x: number; y: number };
  config: Record<string, unknown>;
  inputs: string[];
  outputs: string[];
}

interface WorkflowConnection {
  id: string;
  from: { blockId: string; portId: string };
  to: { blockId: string; portId: string };
}

interface ExecutionEvent {
  id: string;
  blockId: string;
  status: 'pending' | 'running' | 'completed' | 'failed';
  startedAt?: Date;
  completedAt?: Date;
}

interface WorkflowTemplate {
  id: string;
  name: string;
  description: string;
  blocks: number;
}

/**
 * Hook for managing workflow state
 */
export function useWorkflow(workflowId?: string) {
  const [blocks, setBlocks] = useState<WorkflowBlock[]>([]);
  const [connections, setConnections] = useState<WorkflowConnection[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);

  useEffect(() => {
    const loadWorkflow = async () => {
      if (!workflowId) {
        setBlocks([]);
        setConnections([]);
        setIsLoading(false);
        return;
      }

      setIsLoading(true);
      await new Promise(resolve => setTimeout(resolve, 300));
      // Mock data would be loaded here
      setIsLoading(false);
    };

    loadWorkflow();
  }, [workflowId]);

  const addBlock = useCallback((type: string, position: { x: number; y: number }) => {
    const newBlock: WorkflowBlock = {
      id: `block-${Date.now()}`,
      type: type as any,
      name: `New ${type}`,
      position,
      config: {},
      inputs: [],
      outputs: [],
    };
    setBlocks(prev => [...prev, newBlock]);
    setHasUnsavedChanges(true);
    return newBlock;
  }, []);

  const updateBlock = useCallback((blockId: string, updates: Partial<WorkflowBlock>) => {
    setBlocks(prev => prev.map(b => (b.id === blockId ? { ...b, ...updates } : b)));
    setHasUnsavedChanges(true);
  }, []);

  const deleteBlock = useCallback((blockId: string) => {
    setBlocks(prev => prev.filter(b => b.id !== blockId));
    setConnections(prev =>
      prev.filter(c => c.from.blockId !== blockId && c.to.blockId !== blockId)
    );
    setHasUnsavedChanges(true);
  }, []);

  const addConnection = useCallback(
    (from: { blockId: string; portId: string }, to: { blockId: string; portId: string }) => {
      const newConnection: WorkflowConnection = {
        id: `conn-${Date.now()}`,
        from,
        to,
      };
      setConnections(prev => [...prev, newConnection]);
      setHasUnsavedChanges(true);
      return newConnection;
    },
    []
  );

  const deleteConnection = useCallback((connectionId: string) => {
    setConnections(prev => prev.filter(c => c.id !== connectionId));
    setHasUnsavedChanges(true);
  }, []);

  const saveWorkflow = useCallback(async () => {
    setIsSaving(true);
    await new Promise(resolve => setTimeout(resolve, 500));
    setIsSaving(false);
    setHasUnsavedChanges(false);
  }, []);

  const validateWorkflow = useCallback(() => {
    const errors: string[] = [];

    // Check for orphaned blocks
    const connectedBlocks = new Set([
      ...connections.map(c => c.from.blockId),
      ...connections.map(c => c.to.blockId),
    ]);

    blocks.forEach(block => {
      if (!connectedBlocks.has(block.id) && blocks.length > 1) {
        errors.push(`Block "${block.name}" is not connected`);
      }
    });

    return { isValid: errors.length === 0, errors };
  }, [blocks, connections]);

  return {
    blocks,
    connections,
    isLoading,
    isSaving,
    hasUnsavedChanges,
    addBlock,
    updateBlock,
    deleteBlock,
    addConnection,
    deleteConnection,
    saveWorkflow,
    validateWorkflow,
  };
}

/**
 * Hook for workflow execution
 */
export function useWorkflowExecution(_workflowId: string) {
  const [isRunning, setIsRunning] = useState(false);
  const [events, setEvents] = useState<ExecutionEvent[]>([]);
  const [currentBlockId, setCurrentBlockId] = useState<string | null>(null);
  const [stats] = useState({
    processed: 0,
    succeeded: 0,
    failed: 0,
    avgDuration: 0,
  });

  const startExecution = useCallback(async () => {
    setIsRunning(true);
    setEvents([]);
    // Start execution
  }, []);

  const pauseExecution = useCallback(() => {
    setIsRunning(false);
  }, []);

  const resumeExecution = useCallback(() => {
    setIsRunning(true);
  }, []);

  const stopExecution = useCallback(() => {
    setIsRunning(false);
    setCurrentBlockId(null);
  }, []);

  const retryEvent = useCallback(async (_eventId: string) => {
    // Retry failed event
  }, []);

  return {
    isRunning,
    events,
    currentBlockId,
    stats,
    startExecution,
    pauseExecution,
    resumeExecution,
    stopExecution,
    retryEvent,
  };
}

